INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1953d021-e179-4dcc-a1c4-e5027c879a2b', 'Dragon''s Breath', 'You touch one willing creature and imbue it with the power to spew magical energy from its mouth, provided it has one. Choose acid, cold, fire, lightning, or poison. Until the spell ends, the creature can use an action to exhale energy of the chosen type in a 15-foot cone. Each creature in that area must make a Dexterity saving throw, taking 3d6 damage of the chosen type on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell us ing a spell slot of 3rd level or higher, the damage increases by 1d6 for each slot level above 2nd.', 'a hot pepper', 'Touch', 'VSM', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('34da5813-dfb3-4627-a477-871ab6f017c3', 'Arcane Weapon', 'You channel arcane energy into one simple or martial weapon youre holding, and choose one damage type: acid, cold, fire, lightning, poison, or thunder. Until the spell ends, you deal an extra 1d6 damage of the chosen type to any target you hit with the weapon. If the weapon isnt magical, it becomes a magic weapon for the spells duration.
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('696749a5-4eca-405c-93c5-f84cc7653d58', 'Acid Splash', 'You hurl a bubble of acid. Choose one creature within range, or choose two creatures within range that are within 5 feet of each other. A target must succeed on a Dexterity saving throw or take 1d6 acid damage.<br> This spells damage increases by 1d6 when you reach 5th Level (2d6), 11th level (3d6) and 17th level (4d6).', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a2ba07d6-c779-4899-a507-650004e4a636', 'Minor Illusion', 'You create a sound or an image of an object within range that lasts for the duration. The illusion also ends if you dismiss it as an action or cast this spell again.<br> If you create a sound, its volume can range from a whisper to a scream. It can be your voice, someone else''s voice, a lion''s roar, a beating of drums, or any other sound you choose. The sound continues unabated throughout the duration, or you can make discrete sounds at different times before the spell ends.<br> If you create an image of an object - such as a chair, muddy footprints, or a small chest - it must be no larger than a 5-foot cube. The image can''t create sound, light, smell, or any other sensory effect. Physical interaction with the image reveals it to be an illusion, because things can pass through it.<br> If a creature uses its action to examine the sound or image, the creature can determine that it is an illusion with a successful Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the illusion becomes faint to the creature.', '', '30 feet', 'SM', NULL, '1 action', false, '1 minute', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6649e6c2-a33d-4cd1-851f-6f4a6f10982e', 'Prestidigitation', 'This spell is a minor magical trick that novice spellcasters use for practice. You create one of the following magical effects within range.<br>  You create an instantaneous, harmless sensory effect, such as a shower of sparks, a puff of wind, faint musical notes, or an odd odor.<br>  You instantaneously light or snuff out a candle, a torch, or a small campfire.<br>  You instantaneously clean or soil an object no larger than 1 cubic foot.<br>  You chill, warm, or flavor up to 1 cubic foot of nonliving material for 1 hour.<br>  You make a color, a small mark, or a symbol appear on an object or a surface for 1 hour.<br>  You create a nonmagical trinket or an illusory image that can fit in your hand and that lasts until the end of your next turn.<br> If you cast this spell multiple times, you can have up to 3 of its non-instantaneous effects active at a time, and you can dismiss such an effect as an action.', '', '10 feet', 'VS', NULL, '1 action', false, 'Up to 1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('66136164-1f41-4fdc-8877-2583f8c19602', 'Vicious Mockery', 'You unleash a string of insults laced with subtle enchantments at a creature you can see within range. If the target can hear you (though it need not understand you), it must succeed on a Wisdom saving throw or take 1d4 psychic damage and have disadvantage on the next attack roll it makes before the end of its next turn.<br> This spell''s damage increases by 1d4 when you reach 5th level (2d4), 11th level (3d4) and 17th level (4d4).', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('25975c36-1d98-4b23-ba5b-0e612ec5a048', 'Animal Friendship', 'This spell lets you convince a beast that you mean it no harm. Choose a beast that you can see within range. It must see and hear you. If the beast''s Intelligence is 4 or higher, the spell fails. Otherwise, the beast must succeed on a Wisdom saving throw or be charmed by you for the spell''s duration. If you or one of your companions harms the target, the spell ends.<br><br><b>At Higher Levels</b>: When you cast this spell using a 2nd level spell slot or higher, you can affect 1 additional beast for each slot level above 1st.', 'A morsel of food', '30 feet', 'VSM', NULL, '1 action', false, '24 hours', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a855b5e9-e402-4fd7-9a1b-ce81b68a36f9', 'Bane', 'Up to 3 creatures of your choice that you can see within range must make Charisma saving throws. Whenever a target that fails this saving throw makes an attack roll or a saving throw before the spell ends, the target must roll a d4 and subtract the number rolled from the attack roll or saving throw.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target 1 additional creature for each slot level above 1st.', 'A drop of blood', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1073e372-5500-45a8-8993-2e79b5f83e41', 'Charm Person', 'You attempt to charm a humanoid you can see within range. It must make a Wisdom saving throw, and does so with advantage if you or your companions are fighting it. If it fails the saving throw, it is charmed by you until the spell ends or until you or your companions do anything harmful to it. The charmed creature regards you as a friendly acquaintance. When the spell ends, the creature knows it was charmed by you.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target 1 additional creature for each slot level above 1st. The creatures must be within 30 feet of each other when you target them.', '', '30 feet', 'VS', NULL, '1 action', false, '1 hour', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('98523b2d-b52b-4fea-aa27-b4ebf00a8ab6', 'Comprehend Languages', 'For the duration, you understand the literal meaning of any spoken language that you hear. You also understand any spoken language that you hear. You also understand any written language that you see, but you must be touching the surface of which the words are written. It takes about 1 minute to read 1 page of text.<br> This spell doesn''t decode secret messages in a text or glyph, such as an arcane sigil, that isn''t part of a written language.', 'A pinch of soot and salt', 'Self', 'VSM', NULL, '1 action', true, '1 hour', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('60954beb-5e29-481a-9841-605053034ac5', 'Detect Magic', 'For the duration, you sense the presence of magic within 30 feet of you. If you sense magic in this way, you can use your action to see a faint aura around any visible creature or object in the area that bears magic, and you learn its school of magic, if any.<br> The spell can penetrate most barriers, but is blocked by 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt.', '', 'Self', 'VS', NULL, '1 action', true, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ed94cae9-7da6-4441-bc58-f8bf66900f75', 'Calm Emotions', 'You attempt to suppress strong emotions in a group of people. Each humanoid in a 20-foot-radius sphere centered on a point you choose within range must make a Charisma saving throw a creature can choose to fail this saving throw if it wishes. If a creature fails its saving throw, choose 1 of the following 2 effects.<br> You can suppress any effect causing a target to be charmed or frightened. When this spell ends, any suppressed effect resumes, provided that its duration has not expired in the meantime.<br> Alternatively, you can make a target indifferent about creatures of your choice that it is hostile toward. This indifference ends if the target is attacked or harmed by a spell or if it witnesses any of its friends being harmed. When the spell ends, the creature becomes hostile again, unless the DM rules otherwise.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('eb26668c-c6b3-45ca-9e36-7736659fac16', 'Healing Word', 'A creature of your choice that you can see within range regains hit points equal to 1d4 + your spellcasting ability modifier. This spell has no effect on undead or constructs.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the healing increases by 1d4 for each slot level above 1st.', '', '60 feet', 'V', NULL, '1 bonus action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('50243553-d31d-454e-9cfd-b691ac54ca89', 'Sword Burst', 'You create a momentary circle of spectral blades that sweep around you. All other creatures within 5 feet of you must succeed on a Dexterity saving throw or take 1d6 force damage.<br><b>At Higher Levels</b>: This spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', '', 'Self (5-foot radius)', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ed397d64-e85a-4d65-b914-16b34ad570c8', 'Detect Thoughts', 'For the duration, you can read the thoughts of certain creatures. When you cast the spell and as your action on each turn until the spell ends, you can focus your mind on any one creature that you can see within 30 feet of you. If the creature you choose has an Intelligence of 3 or lower or doesn''t speak any language, the creature is unaffected.<br> You initially learn the surface thoughts of the creature - what is most on its mind in that moment. As an action, you can either shift your attention to another creature''s thoughts or attempt to probe deeper into the same creature''s mind. If you probe deeper, the target must make a Wisdom saving throw. If it fails, you gain insight into its reasoning (if any), its emotional state, and something that looms large in its mind (such as something it worries over, loves, or hates). If it succeeds, the spell ends. Either way, the target knows that you are probing into its mind, and unless you shift your attention to another creature''s thoughts, the creature can use its action on its turn to make an Intelligence check contested by your Intelligence check if it succeeds, the spell ends.<br> Questions verbally directed at the target creature naturally shape the course of its thoughts, so this spell is particularly effective as part of an interrogation.<br> You can also use this spell to detect the presence of thinking creatures you can''t see. When you cast the spell or as your action during the duration, you can search for thoughts within 30 feet of you. The spell can penetrate barriers, but 2 feet of rock, 2 inches of any metal other than lead, or a thin sheet of lead blocks you. You can''t detect a creature with an Intelligence of 3 or lower or one that doesn''t speak any language.<br> Once you detect the presence of a creature in this way, you can read its thoughts for the rest of the duration as described above, even if you can''t see it, but it must still be within range.', 'a copper piece', 'Self', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('50cb452c-ba51-4dae-a6f1-d2ff928d0722', 'Enhance Ability', 'You touch a creature and bestow upon it a magical enhancement. Choose 1 of the following effects - the target gains the effect until the spell ends.<br> <b>Bear''s Endurance</b>: The target has advantage on Constitution checks. It also gains 2d6 temporary hit points, which are lost when the spell ends.<br> <b>Bull''s Strength</b>: The target has advantage on Strength checks, and his or her carrying capacity doubles.<br> <b>Cat''s Grace</b>: The target has advantage on Dexterity checks. It also doesn''t take damage from falling 20 feet or less if it isn''t incapacitated.<br> <b>Eagle''s Splendor</b>: The target has advantage on Charisma checks.<br> <b>Fox''s Cunning</b>: The target has advantage on Intelligence checks.<br> <b>Owl''s Wisdom</b>: The target has advantage on Wisdom checks.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, you can target 1 additional creature for each slot level above 2nd.', 'Fur or a feather from a beast', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e8fd04a0-3b11-4ad4-9516-17ce2342dbb1', 'Enthrall', 'You weave a distracting string of words, causing creatures of your choice that you can see within range and that can hear you to make a Wisdom saving throw. Any creature that can''t be charmed succeeds on this saving throw automatically, and if you or your companions are fighting a creature, it has advantage on the save. On a failed save, the target has disadvantage on Wisdom (Perception) checks made to perceive any creature other than you until the spell ends or until the target can no longer hear you. The spell ends if you are incapacitated or can no longer speak.', '', '60 feet', 'VS', NULL, '1 action', false, '1 minute', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2180cf4d-6494-4dda-b8c1-3bd5f52f2322', 'Heat Metal', 'Choose a manufactured metal object, such as a metal weapon or a suit of heavy or medium metal armor, that you can see within range. You cause the object to glow red-hot. Any creature in physical contact with the object takes 2d8 fire damage when you cast the spell. Until the spell ends, you can use a bonus action on each of your subsequent turns to cause this damage again.<br> If a creature is holding or wearing the object and takes the damage from it, the creature must succeed on a Constitution saving throw or drop the object if it can. If it doesn''t drop the object, it has disadvantage on attack rolls and ability checks until the start of your next turn.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d8 for each slot above 2nd.', 'A piece of iron and a flame', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2a681bea-4332-42ba-82b6-40e3597425dc', 'Hold Person', 'Choose a humanoid that you can see within range. The target must succeed on a Wisdom saving throw or be paralyzed for the duration. At the end of each of its turns, the target can make another Wisdom saving throw. On a success, the spell ends on the target.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, you can target on additional humanoid for each slot level above 2nd. The humanoids must be within 30 feet of each other when you target them.', 'a small, straight piece of iron', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('02db6f4f-f41f-4dda-ba71-56043ae60590', 'Knock', 'Choose an object that you can see within range. The object can be a door, a box, a chest, a set of manacles, a padlock, or another object that contains a mundane or magical means that prevents access.<br> A target that is held shut by a mundane lock or that is stuck or barred becomes unlocked, unstuck, or unbarred. If the object has multiple locks, only one of them is unlocked.<br> If you choose a target that is held shut with <i>arcane lock</i>, that spell is suppressed for 10 minutes, during which time the target can be opened and shut normally.<br> When you cast the spell, a loud knock, audible from as far away as 300 feet, emanates from the target object.', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e749c213-d4e9-4523-82e1-c716c14660d8', 'Locate Animals or Plants', 'Describe or name a specific kind of beast or plant. Concentrating on the voice of nature in your surroundings, you learn the direction and distance to the closest creature or plant of that kind within 5 miles, if any are present.', 'A bit of fur from a bloodhound', 'Self', 'VSM', NULL, '1 action', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('dfd4ac29-8357-45f3-bdff-f906afa4596d', 'Disguise Self', 'You make yourself, including your clothing, armor, weapons, and other belongings on your person, look different until the spell ends or until you use your action to dismiss it. You can seem 1 foot shorter or taller and can appear thin, fat, or in between. You can''t change your body type, so you must adopt a form that has the same basic arrangement of limbs. Otherwise, the extent of the illusion is up to you.<br> The changes wrought by this spell fail to hold up to physical inspection. For example, if you use this spell to add a hat to your outfit, objects pass through the hat, and anyone who touches it would feel nothing or would feel your head and hair. If you use this spell to appear thinner than you are, the hand of someone who reaches out to touch you would bump into you while it was seemingly still in midair.<br> To discern that you are disguised, a creature can use its action to inspect your appearance and must succeed on an Intelligence (Investigation) check against your spell save DC.', '', 'Self', 'VS', NULL, '1 action', false, '1 hour', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('35361857-12fd-49ab-ae8c-d52a9e4782d4', 'Locate Object', 'Describe or name an object that is familiar to you. You sense the direction to the object''s location, as long as that object is within 1,000 feet of you. If the object is in motion, you know the direction of its movement.<br> The spell can locate a specific object known to you, as long as you have seen it up close - within 30 feet - at least once. Alternatively, the spell can locate the nearest object of a particular kind, such as a certain kind of apparel, jewelry, furniture, tool, or weapon.<br> This spell can''t locate an object if any thickness of lead, even a thin sheet, blocks a direct path between you and the object.', 'A forked twig', 'Self', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9ac4ba3c-d219-465a-8119-7d8c53e27481', 'Magic Mouth', 'You implant a message within an object in range, a message that is uttered when a trigger condition is met. Choose an object that you can see and that isn''t being worn or carried by another creature. Then speak the message, which must be 25 words or less, though it can be delivered over as long as 10 minutes. Finally, determine the circumstance that will trigger the spell to deliver your message.<br> When that circumstance occurs, a magical mouth appears on the object and recites the message in your voice and at the same volume you spoke. If the object you chose has a mouth or something that looks like a mouth (for example, the mouth of a statue), the magical mouth appears there so that words appear to come from the object''s mouth. When you cast this spell, you can have the spell end after it delivers its message, or it can remain and repeats its message whenever the trigger occurs.<br> The triggering circumstance can be as general or as detailed as you like, though it must be based on visual or audible conditions that occur within 30 feet of the object. For example, you could instruct the mouth to speak when any creature moves within 30 feet of the object or when a silver bell rings within 30 feet of it.', 'A small bit of honeycomb and jade dust worth at least 10 gp, Consumed', '30 feet', 'VSMgp', NULL, '1 minute', true, 'Until dispelled', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d1912b61-18ae-438f-85ec-360782cfe06b', 'Shatter', 'A sudden loud ringing noise, painfully intense, erupts from a point of your choice within range. Each creature in a 10-foot-radius sphere centered on that point must make a Constitution saving throw. A creature takes 3d8 thunder damage on a failed save, or half as much damage on a successful one. A creature made of inorganic material such as stone, crystal, or metal has disadvantage on this saving throw.<br> A nonmagical object that isn''t being worn or carried also takes the damage if it''s in the spell''s area.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of or higher, the damage increases by 1d8 for each slot level above 2nd.', 'A chip of mica', '60 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('96ae4597-5ecc-4b1e-88f9-0a8e0183ffaf', 'Suggestion', 'You suggest a course of activity (limited to a sentence or two) and magically influence a creature you can see within range that can hear and understand you. Creatures that can''t be charmed are immune to this effect. The suggestion must be worded in such a manner as to make the course of action sound reasonable. Asking the creature to stab itself, throw itself onto a spear, immolate itself, or do some other obviously harmful act ends the spell.<br> The target must make a Wisdom saving throw. On a failed save, it pursues the course of action you described to the best of its ability. The suggested course of action can continue for the entire duration. If the suggested activity can be completed in a shorter time, the spell ends when the subject finishes what it was asked to do.<br> You can also specify conditions that will trigger a special activity during the duration. For example, you might suggest that a knight give her warhorse to the first beggar she meets. If the condition isn''t met before the spell expires, the activity isn''t performed.<br> If you or any of your companions damage the target, the spell ends.', 'a snake''s tongue and either a bit of honeycomb or a drop of sweet oil', '30 feet', 'VM', NULL, '1 action', false, 'up to 8 hours', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0a7a6d8b-ad60-4210-9b7b-ec345a80b4f4', 'Zone of Truth', 'You create a magical zone that guards against deception in a 15-foot-radius sphere centered on a point of your choice within range. Until the spell ends, a creature that enters the spell''s area for the first time on a turn or starts its turn there must make a Charisma saving throw. On a failed save, a creature can''t speak a deliberate lie while in the radius. You know whether each creature succeeds or fails on its saving throw.<br> An affected creature is aware of the spell and can thus avoid answering questions to which it would normally respond with a lie. Such creatures can be evasive in its answers as long as it remains within the boundaries of the truth.', '', '60 feet', 'VS', NULL, '1 action', false, '10 minutes', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2ec27a80-0f13-4ac6-ac5c-686f8f806236', 'Mordenkainen''s Private Sanctum', 'You make an area within range magically secure. The area is a cube that can be as small as 5 feet to as large as 100 feet on each side. The spell lasts for the duration or until you use an action to dismiss it.<br> When you cast the spell, you decide what sort of security the spell provides, choosing any or all of the following properties.<br>  Sound can''t pass through the barrier at the edge of the warded area.<br>  The barrier of the warded area appears dark and foggy, preventing vision (including darkvision) through it.<br>  Sensors created by divination spells can''t appear inside the protected area or pass through the barrier at its perimeter.<br>  Creatures in the area can''t be targeted by divination spells.<br>  Nothing can teleport into or out of the warded area.<br>  Planar travel is blocked within the warded area.<br> Casting this spell on the same spot every day for a year makes this effect permanent.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, you can increase the size of the cube by 100 feet for each slot level beyond 4th. Thus you could protect a cube that can be up to 200 feet on one side by using a spell slot of 5th level.', 'a thin sheet of lead, a piece of opaque glass, a wad of cotton or cloth, and powdered chrysolite', '120 feet', 'VSM', NULL, '10 minutes', false, '24 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d7fb7e29-d9e4-45f2-a9e9-d2ae3b24d61e', 'Invisibility', 'A creature you touch becomes invisible until the spell ends. Anything the target is wearing or carrying is invisible as long as it is on the target''s person. The spell ends for a target that attacks or casts a spell.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, you can target 1 additional creature for each slot level above 2nd.', 'An eyelash encased in gum arabic', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('deafd8cc-dce1-42cd-adf5-2846ca0d5710', 'Bestow Curse', 'You touch a creature, and that creature must succeed on a Wisdom saving throw or become cursed for the duration of the spell. When you cast this spell, choose the nature of the curse from the following options.<br>  Choose 1 ability score. While cursed, the target has disadvantage on ability checks and saving throws made with that ability score.<br>  While cursed, the target has disadvantage on attack rolls against you.<br>  While cursed, the target must make a Wisdom saving throw at the start of each of its turns. If it fails, it wastes its action that turn doing nothing.<br>  While the target is cursed, your attacks and spells deal an extra 1d8 necrotic damage to the target.<br> A <i>remove curse</i> spell ends this effect. At the DM''s option, you may choose an alternative curse effect, but it should be no more powerful than those described above. The DM has final say on such a curse''s effect.<br><br><b>At Higher Levels</b>: If you cast this spell using a spell slot of 4th level or higher, the duration is concentration, up to 10 minutes. If you use a spell slot of 5th level or higher, the duration is 8 hours. -<br> -<br> -<br> -<br> -<br> If you use a spell slot of 7th level or higher, the duration If you use a spell slot of 7th level or higher, the duration is 24 hours. If you use a 9th level spell slot, the spell lasts until it is dispelled. Using a spell slot of 5th level or higher grants a duration that doesn''t require concentration.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d5574d37-b68c-4fa0-8928-b67e456ddc5d', 'Clairvoyance', 'You create an invisible sensor within range in a location familiar to you (A place you have visited or seen before) or in an obvious location that is unfamiliar to you (such as behind a door, around a corner, or in a grove of trees). The sensor remains in place for the duration, and it can''t be attacked or otherwise interacted with. When you cast the spell, you choose seeing or hearing. You can use the chosen sense through the sensor as if you were in its space. As your action, you can switch between seeing and hearing. A creature that can see the sensor (such as a creature benefitting from see invisibility or truesight) sees a luminous, intangible orb about the size of your fist.<br> When you cast the spell, you choose seeing or hearing. You can use the chosen sense through the sensor as if you were in its space. As your action, you can switch between seeing and hearing. A creature that can see the sensor (such as a creature benefitting from see invisibility or truesight) sees a luminous, intangible orb about the size of your fist.<br> A creature that can see the sensor (such as a creature benefitting from <i>see invisibility</i> or truesight) sees a luminous, intangible orb about the size of your fist.', 'A focus worth at least 100 gp, either a jeweled horn for hearing or a glass eye for scrying', '1 mile', 'VSMgp', NULL, '10 minutes', false, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f0c4fdd9-8517-4aed-a2ff-7891bc172e0c', 'Dispel Magic', 'Choose any creature, object, or magical effect within range. Any spell of 3rd level or lower on the target ends. For each spell of 4th level or higher on the target, make an ability check using your spellcasting ability. The DC equals 10 + the spell''s level. On a successful check, the spell ends.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you automatically end the effects of a spell on the target if the spell''s level is equal to or less than the level of the spell slot you used.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b583c6ba-9b29-4314-90ec-98c3d3c37651', 'Fear', 'You project a phantasmal image of a creature''s worst fears. Each creature in a 30-foot cone must succeed on a Wisdom saving throw or drop whatever it is holding and become frightened for the duration.<br> While frightened by this spell, a creature must take the Dash action and move away from you by the safest available route on each of its turns, unless there is nowhere to move. If the creature ends its turn in a location where it doesn''t have line of sight to you, the creature can make a Wisdom saving throw. On a successful save, the spell ends for that creature.', 'A white feather or the heart of a hen', 'Self (30-foot cone)', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c241e76d-6d21-460a-88a9-8e82bf2cd8c5', 'Glyph of Warding', 'When you cast this spell, you inscribe a glyph that harms other creatures, either upon a surface (such as a table or a section of floor or wall) or within an object that can be closed (such as a book, a scroll, or a treasure chest) to conceal the glyph. If you choose a surface, the glyph can cover an area of the surface no larger than 10 feet in diameter. If you choose an object, that object must remain in its place, if the object is moved more than 10 feet from where you cast this spell, the glyph is broken and the spell ends without being triggered.<br> The glyph is nearly invisible and requires a successful Intelligence (Investigation) check against your spell save DC to be found.<br> You decide what triggers the glyph when you cast the spell. For glyphs inscribed on a surface, the most typical triggers include touching or standing on the glyph, removing another object covering the glyph, approaching within a certain distance of the glyph, or manipulating the object on which the glyph is inscribed. For glyphs inscribed within an object, the most common triggers include opening that object, approaching within a certain distance of the object, or seeing or reading the glyph. Once a glyph is triggered, this spell ends.<br> You can further refine the trigger so the spell activates only under certain circumstances or according to physical characteristics (such as height or weight), creature kind (for example, the ward could be set to affect aberrations or drow), or alignment. You can also set conditions for creatures that don''t trigger the glyph, such as those who say a certain password.<br> When you inscribe the glyph, choose explosive runes or a spell glyph.<br> <b>Explosive Runes</b>: When triggered, the glyph erupts with magical energy in a 20-foot-radius sphere centered on the glyph. The sphere spreads around corners. Each creature in the aura must make a Dexterity saving throw. A creature takes 5d8 acid, cold, fire, lightning, or thunder damage on a failed saving throw (your choice when you create the glyph), or half as much damage on a successful one.<br> <b>Spell Glyph</b>: You can store a prepared spell of or lower in the glyph by casting it as part of creating the glyph. The spell must target a single creature or an area. The spell being stored has no immediate effect when cast in this way. When the glyph is triggered, the stored spell is cast. If the spell has a target, it targets the creature that triggered the glyph. If the spell affects an area, the area is centered on that creature. If the spell summons hostile creatures or creates harmful objects or traps, they appear as close as possible to the intruder and attack it. If the spell requires concentration, it lasts until the end of its full duration.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage of an explosive runes glyph increases by 1d8 for each slot level above 3rd. If you create a <i>spell glyph</i>, you can store any spell of up to the same level as the slot you use for the <i>glyph of warding</i>.', 'Incense and powdered diamond worth at least 200 gp, Consumed', 'Touch', 'VSMgp', NULL, '1 hour', false, 'Until dispelled or triggered', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('81649f6c-0522-4324-bba7-f11c405e5aa4', 'Melf''s Minute Meteors', 'You create six tiny meteors in your space. They float in the air and orbit you for the spell''s duration. When you cast the spell-and as a bonus action on each of your turns thereafter-you can expend one or two of the meteors, sending them streaking toward a point or points you choose within 120 feet of you. Once a meteor reaches its destination or impacts against a solid surface, the meteor explodes. Each creature within 5 feet of the point where the meteor explodes must make a Dexterity saving throw. A creature takes 2d6 fire damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the number of meteors created increases by two for each slot level above 3rd.', 'niter, sulfur, and pine tar formed into a bead', 'Self', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('357025b3-1982-4004-ad0e-d942431af725', 'Hypnotic Pattern', 'You create a twisting pattern of colors that weaves through the air inside a 30-foot cube within range. The pattern appears for a moment and vanishes. Each creature in the area who sees the pattern must make a Wisdom saving throw. On a failed save, the creature becomes charmed for the duration. While charmed by this spell, the creature is incapacitated and has a speed of 0.<br> The spell ends for an affected creature if it takes any damage or if someone else uses an action to shake the creature out of its stupor.', 'A glowing stick of incense or a crystal vial filled with phosphorescent material', '120 feet', 'SM', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0f93ca7a-ce60-425b-8459-444aca792fe2', 'Major Image', 'You create the image of an object, a creature, or some other visible phenomenon that is no larger than a 20-foot cube. The image appears at a spot that you can see within range and lasts for the duration. It seems completely real, including sounds, smells, and temperature appropriate to the thing depicted. You can''t create sufficient heat or cold to cause damage, a sound loud enough to deal thunder damage or deafen a creature, or a smell that might sicken a creature (like a troglodyte''s stench).<br> As long as you are within range of the illusion, you can use your action to cause the image to move to any other spot within range. As the image changes location, you can alter its appearance so that its movements appear natural for the image. For example, if you create an image of a creature and move it, you can alter the image so that it appears to be walking. Similarly, you can cause the illusion to make different sounds at different times, even making it carry on a conversation, for example.<br> Physical interaction with the image reveals it to be an illusion, because things can pass through it. A creature that uses its action to examine the image can determine that it is an illusion with a successful Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the creature can see through the image, and its other sensory qualities become faint to the creature.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the spell lasts until dispelled, without requiring your concentration.', 'a bit of fleece', '120 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e17fb6d5-81cb-45ce-905d-319e6cd188d0', 'Plant Growth', 'This spell channels vitality into plants within a specific area. There are two possible uses for the spell, granting either immediate or long-term benefits.<br> If you cast this spell using 1 action, choose a point within range. All normal plants in a 100-foot radius centered on that point become thick and overgrown. A creature moving through the area must spend 4 feet of movement for every 1 foot it moves.<br> You can exclude one or more areas of any size within the spell''s area from being affected.<br> If you cast this spell over 8 hours, you enrich the land. All plants in a half-mile radius centered on a point within range become enriched for 1 year. The plants yield twice the normal amount of food when harvested.', '', '150 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4613de72-c95d-4744-9359-b1f0338cafbc', 'Speak with Dead', 'You grant the semblance of life and intelligence to a corpse of your choice within range, allowing it to answer the questions you pose. The corpse must still have a mouth and can''t be undead. The spell fails if the corpse was the target of this spell within the last 10 days.<br> Until the spell ends, you can ask the corpse up to five questions. The corpse knows only what it knew in life, including the languages it knew. Answers are usually brief, cryptic, or repetitive, and the corpse is under no compulsion to offer a truthful answer if you are hostile to it or it recognizes you as an enemy. This spell doesn''t return the creature''s soul to its body, only its animating spirit. Thus, the corpse can''t learn new information, doesn''t comprehend anything that has happened since it died, and can''t speculate about future events.', 'burning incense', '10 feet', 'VSM', NULL, '1 action', false, '10 minutes', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a6644a94-ae4c-4df0-b1ea-39e9e0cc11cb', 'Speak with Plants', 'You imbue plants within 30 feet of you with limited sentience and animation, giving them the ability to communicate with you and follow your simple commands. You can question plants about events in the spell''s area within the past day, gaining information about creatures that have passed, weather, and other circumstances.<br> You can also turn difficult terrain caused by plant growth (such as thickets and undergrowth) into ordinary terrain that lasts for the duration. Or you can turn ordinary terrain where plants are present into difficult terrain that lasts for the duration, causing vines and branches to hinder pursuers, for example.<br> Plants might be able to perform other tasks on your behalf, at the DM''s discretion. The spell doesn''t enable plants to uproot themselves and move about, but they can freely move branches, tendrils, and stalks.<br> If a plant creature is in the area, you can communicate with it as if you shared a common language, but you gain no magical ability to influence it.<br> This spell can cause the plants created by the <i>entangle</i> spell to release a restrained creature.', '', 'Self (30-foot radius)', 'VS', NULL, '1 action', false, '10 minutes', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('21394e1d-b497-4d6b-a84a-50120f94a812', 'Stinking Cloud', 'You create a 20-foot-radius sphere of yellow, nauseating gas centered on a point within range. The cloud spreads around corners, and its area is heavily obscured. The cloud lingers in the air for the duration.<br> Each creature that is completely within the cloud at the start of its turn must make a Constitution saving throw against poison. On a failed save, the creature spends its action that turn retching and reeling. Creatures that don''t need to breathe or are immune to poison automatically succeed on this saving throw.<br> A moderate wind (at least 10 miles per hour) disperses the cloud after 4 rounds. A strong wind (at least 20 miles per hour) disperses it after 1 round.', 'A rotten egg or several skunk cabbage leaves', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8105087b-c5d3-437a-b51b-06744a7d8af7', 'Tongues', 'This spell grants the creature you touch the ability to understand any spoken language it hears. Moreover, when the target speaks, any creature that knows at least one language and can hear the target understands what it says.', 'a small clay model of a ziggurat', 'Touch', 'VM', NULL, '1 action', false, '1 hour', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('78ee8005-63b8-4814-beda-7d4456416a56', 'Compulsion', 'Creatures of your choice that you can see within range and that can hear you must make a Wisdom saving throw. A target automatically succeeds on this saving throw if it can''t be charmed. On a failed save, a target is affected by this spell. Until the spell ends, you can use a bonus action on each of your turns to designate a direction that is horizontal to you. Each affected target must use as much of its movement as possible to move in that direction on its next turn. It can take its action before it moves. After moving in this way, it can make another Wisdom saving throw to try to end the effect.<br> A target isn''t compelled to move into an obviously deadly hazard, such as a fire pit, but it will provoke opportunity attacks to move in the designated direction.', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a2d81f86-f874-478d-b71a-733111c6538a', 'Faerie Fire', 'Each object in a 20-foot cube within range is outlined in (A color) of light (Your choice). Any creature in the area when the spell is cast is also outlined in light if it fails a Dexterity saving throw. For the duration, objects and affected creatures shed dim light in a 10-foot radius.<br> Any attack roll against an affected creature or object has advantage if the attacker can see it, and the affected creature or object can''t benefit from being invisible.', '', '60 feet', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('54e8445a-6a70-40f0-9c3f-f394e89fd7cd', 'Dimension Door', 'You teleport yourself from your current location to any other spot within range. You arrive at exactly the spot desired. It can be a place you can see, one you can visualize, or one you can describe by stating distance and direction, such as 200 feet straight downward or upward to the northwest at a 45-degree angle, 300 feet.<br> You can bring along objects as long as their weight doesn''t exceed what you can carry. You can also bring one willing creature of your size or smaller who is carrying gear up to its carrying capacity. The creature must be within 5 feet of you when you cast this spell.<br> If you would arrive in a place already occupied by an object or a creature, you and any creature traveling with you each take 4d6 force damage, and the spell fails to teleport you.', '', '500 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('932f3177-2833-4f54-bb42-a4a8a578b9cb', 'Freedom of Movement', 'You touch a willing creature. For the duration, the target''s movement is unaffected by difficult terrain, and spells and other magical effects can neither reduce the target''s speed nor cause the target to be paralyzed or restrained.<br> The target can also spend 5 feet of movement to automatically escape from nonmagical restraints, such as manacles or a creature that has it grappled. Finally, being underwater imposes no penalties on the target''s movement or attacks.', 'a leather strap, bound around the arm or a similar appendage', 'Touch', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9452f646-7ec4-4735-900e-f3cb7f4bae5f', 'Greater Invisibility', 'You or a creature you touch becomes invisible until the spell ends. Anything the target is wearing or carrying is invisible as long as it is on the target''s person.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fb694fec-5667-4b47-b81d-f2905088f663', 'Hallucinatory Terrain', 'You make natural terrain in a 150-foot cube in range look, sound, and smell like some other sort of natural terrain. Thus, open fields or a road can be made to resemble a swamp, hill, crevasse, or some other difficult or impassable terrain. A pond can be made to seem like a grassy meadow, a precipice like a gentle slope, or a rock-strewn gully like a wide and smooth road. Manufactured structures, equipment, and creatures within the area aren''t changed in appearance.<br> The tactile characteristics of the terrain are unchanged, so creatures entering the area are likely to see through the illusion. If the difference isn''t obvious by touch, a creature carefully examining the illusion can attempt an Intelligence (Investigation) check against your spell save DC to disbelieve it. A creature who discerns the illusion for what it is, sees it as a vague image superimposed on the terrain.', 'A stone, a twig, and a bit of green plant', '300 feet', 'VSM', NULL, '10 minutes', false, '24 hours', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('acdad945-95ba-4f22-965f-985dd20dd949', 'Locate Creature', 'Describe or name a creature that is familiar to you. You sense the direction to the creature''s location, as long as that creature is within 1,000 feet of you. If the creature is moving, you know the direction of its movement.<br> The spell can locate a specific creature known to you, or the nearest creature of a specific kind (such as a human or a unicorn), so long as you have seen such a creature up close - within 30 feet - at least once. If the creature you described or named is in a different form, such as being under the effects of a <i>polymorph</i> spell, this spell doesn''t locate the creature.<br> This spell can''t locate a creature if running water at least 10 feet wide blocks a direct path between you and the creature.', 'A bit of fur from a bloodhound', 'Self', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e01b06ea-024c-45e1-814e-8bb1e7ecb40d', 'Polymorph', 'This spell transforms a creature with at least 1 hit point that you can see within range into a new form. An unwilling creature must make a Wisdom saving throw to avoid the effect. A shapechanger automatically succeeds on this saving throw.<br> The transformation lasts for the duration, or until the target drops to 0 hit points or dies. The new form can be any beast whose challenge rating is equal to or less than the target''s (or the target''s level, if it doesn''t have a challenge rating). The target''s game statistics, including mental ability scores, are replaced by the statistics of the chosen beast. It retains its alignment and personality.<br> The target assumes the hit points of its new form. When it reverts to its normal form, the creature returns to the number of hit points it had before it transformed. If it reverts as a result of dropping to 0 hit points, any excess damage carries over to its normal form. As long as the excess damage doesn''t reduce the creature''s normal form to 0 hit points, it isn''t knocked unconscious.<br> The creature is limited in the actions it can perform by the nature of its new form, and it can''t speak, cast spells, or take any other action that requires hands or speech.<br> The target''s gear melds into the new form. The creature can''t activate, use, wield, or otherwise benefit from any of its equipment.', 'A caterpillar cocoon', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('90adfcf2-f7c4-4f75-98da-8b70e5a0d6b1', 'Maddening Darkness', 'Magical darkness spreads from a point you choose with in range to fill a 60-foot-radius sphere until the spell ends. The darkness spreads around corners. A creature with darkvision can''t see through this darkness. Nonmagical light, as well as light c reated by spells of 8th level or lower, can''t illuminate the area.<br> Shrieks, gibbering, and mad laughter can be heard within the sphere. Whenever a creature starts its turn in the sphere, it must make a Wisdom saving throw, taking 8d8 psychic damage on a failed save, or half as much damage on a successful one.', 'a drop of pitch mixed with a drop of mercury', '150 feet', 'VM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('971322a8-06d2-4900-9752-27cd253b1fb1', 'Leomund''s Secret Chest', 'You hide a chest, and all its contents, on the Ethereal Plane. You must touch the chest and the miniature replica that serves as a material component for the spell. The chest can contain up to 12 cubic feet of nonliving material (3 feet by 2 feet by 2 feet).<br> While the chest remains on the Ethereal Plane, you can use an action and touch the replica to recall the chest. It appears in an unoccupied space on the ground within 5 feet of you. You can send the chest back to the Ethereal Plane by using an action and touching both the chest and the replica.<br> After 60 days, there is a cumulative 5 percent chance per day that the spell''s effect ends. This effect ends if you cast this spell again, if the smaller replica chest is destroyed, or if you choose to end the spell as an action. If the spell ends and the larger chest is on the Ethereal Plane, it is irretrievably lost.', 'an exquisite chest, 3 feet by 2 feet by 2 feet, constructed from rare materials worth at least 5,000 gp, and a Tiny replica made from the same materials worth at least 50 gp', 'Touch', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4c65e715-0a7d-4667-9265-d69a3fcf33a8', 'Feather Fall', 'Reaction - When you or a creature within 60 feet of you falls Choose up to 5 falling creatures within range. A falling creature''s rate of descent slows to 60 feet per round until the spell ends. If the creature lands before the spell ends, it takes no falling damage and can land on its feet, and the spell ends for that creature.', 'A white feather or the heart of a hen', '60 feet', 'VM', NULL, '1 reaction', false, '1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('063329c9-7b96-4b9b-b7d0-ffc3fd6642c6', 'Heroism', 'A willing creature you touch is imbued with bravery. Until the spell ends, the creature is immune to being frightened and gains temporary hit points equal to your spellcasting ability modifier at the start of each of its turns. When the spell ends, the target loses any remaining temporary hit points from this spell.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target one additional creature for each slot level above 1st.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9dc99698-06ce-4938-adfb-01fce4b3ce55', 'Animate Objects', 'Objects come to life at your command. Choose up to ten nonmagical objects within range that are not being worn or carried. Medium targets count as two objects, Large targets count as four objects, Huge targets count as eight objects. You can''t animate any object larger than Huge. Each target animates and becomes a creature under your control until the spell ends or until reduced to 0 hit points.<br> As a bonus action, you can mentally command any creature you made with this spell if the creature is within 500 feet of you (if you control multiple creatures, you can command any or all of them at the same time, issuing the same command to each one). You decide what action the creature will take and where it will move during its next turn, or you can issue a general command, such as to guard a particular chamber or corridor. If you issue no commands, the creature only defends itself against hostile creatures. Once given an order, the creature continues to follow it until its task is complete.<br> An animated object is a construct with AC, hit points, attacks, Strength, and Dexterity determine by its size. Its Constitution is 10 and its Intelligence and Wisdom are 3, and its Charisma is 1. Its speed is 30 feet, if the objects lack legs or other appendages it can use for locomotion, it instead has a flying speed of 30 feet and can hover. If the object is securely attached to a surface or larger object, such as a chain bolted to a wall, its speed is 0. It has blindsight with a radius of 30 feet and is blind beyond that distance. When the animated object drops to 0 hit points, it reverts to its original object form, and any remaining damage carries over to its original object form.<br> If you command an object to attack, it can make a single melee attack against a creature within 5 feet of it. It makes a slam attack with an attack bonus and bludgeoning damage determine by its size. The DM might rule that a specific object inflicts slashing or piercing damage based on its form.<br> Animated Object Statistics<br>Size. . , HP, AC, Hit, Damage,Str,Dex<br>Tiny. . , 20, 18, +8 , 1d4 + 4. , 4 , 18<br>Small , 25, 16, +6 , 1d8 + 2. , 6 , 14<br>Med. ., 40, 13, +5 , 2d6 + 1 , 10, 12<br>Large , 50, 10, +6 , 2d10 +2,14, 10<br>Huge. , 80, 10, +8 , 2d12 +4,18 , 6<br> <b>At Higher Levels</b>: If you cast this spell using a spell slot of 6th level or higher, you can animate two additional objects for each slot level above 5th.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f4e38d8f-a658-4218-ac47-1b62f3349dd3', 'Dominate Person', 'You attempt to beguile a humanoid that you can see within range. It must succeed on a Wisdom saving throw or be charmed by you for the duration. If you or creatures that are friendly to you are fighting it, it has advantage on the saving throw.<br> While the target is charmed, you have a telepathic link with it as long as the two of you are on the same plane of existence. You can use this telepathic link to issue commands to the creature while you are conscious (no action required), which it does its best to obey. You can specify a simple and general course of action, such as Attack that creature, Run over there, or Fetch that object. If the creature completes the order and doesn''t receive further direction from you, it defends and preserves itself to the best of its ability.<br> You can use your action to take total and precise control of the target. Until the end of your next turn, the creature takes only the actions you choose, and doesn''t do anything that you don''t allow it to do. During this time you can also cause the creature to use a reaction, but this requires you to use your own reaction as well.<br> Each time the target takes damage, it makes a new Wisdom saving throw against the spell. If the saving throw succeeds, the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a 6th-level spell slot, the duration is concentration, up to 10 minutes. When you use a 7th-level spell slot, the duration is concentration, up to 1 hour. When you use a spell slot of 8th level or higher, the duration is concentration, up to 8 hours.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bff9955b-6808-4672-9105-0d7e15537bcd', 'Dream', 'This spell shapes a creature''s dreams. Choose a creature known to you as the target of this spell. The target must be on the same plane of existence as you. Creatures that don''t sleep, such as elves, can''t be contacted by this spell. You, or a willing creature you touch, enters a trance state, acting as a messenger. While in the trance, the messenger is aware of his or her surroundings, but can''t take actions or move.<br> If the target is asleep, the messenger appears in the target''s dreams and can converse with the target as long as it remains asleep, through the duration of the spell. The messenger can also shape the environment of the dream, creating landscapes, objects, and other images. The messenger can emerge from the trance at any time, ending the effect of the spell early. The target recalls the dream perfectly upon waking. If the target is awake when you cast the spell, the messenger knows it, and can either end the trance (and the spell) or wait for the target to fall asleep, at which point the messenger appears in the target''s dreams.<br> You can make the messenger appear monstrous and terrifying to the target. If you do, the messenger can deliver a message of no more than ten words and then the target must make a Wisdom saving throw. On a failed save, echoes of the phantasmal monstrosity spawn a nightmare that lasts the duration of the target''s sleep and prevents the target from gaining any benefit from that rest. In addition, when the target wakes up, it takes 3d6 psychic damage.<br> If you have a body part, lock of hair, clipping from a nail, or similar portion of the target''s body, the target makes its saving throw with disadvantage.', 'a handful of sand, a dab of ink, and a writing quill plucked from a sleeping bird', 'Special', 'VSM', NULL, '1 minute', false, '8 hours', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c67214ac-3ea1-4310-b69c-9a629e2e55c3', 'Tasha''s Hideous Laughter', 'A creature of your choice that you can see within range perceives everything as hilariously funny and falls into fits of laugher if this spell affects it. The target must succeed on a Wisdom saving throw of fall prone, becoming incapacitated and unable to stand up for the duration. A creature with an Intelligence score of 4 or less isn''t affected.<br> At the end of each of its turns, and each time it takes damage, the target can make another Wisdom saving throw. The target has advantage on the saving throw if it''s triggered by damage. On a success, the spell ends.', 'tiny tarts and a feather that waved in the air', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e7c70575-805c-40e3-bfa3-82f78d3e345b', 'Identify', 'You choose 1 object that you must touch throughout the casting of the spell. If it is a magic item or some other magic-imbued object, you learn its properties and how to use them, whether it requires attunement to use, and how many charges it has, if any. You learn whether any spells are affecting the item and what they are. If the item was created by a spell, you learn which spell created it.<br> If you instead touch a creature throughout the casting, you learn what spells, if any, are currently affecting it.', 'A pearl worth at least 100 gp and an owl feather', 'Touch', 'VSMgp', NULL, '1 minute', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('24764e8c-057b-42af-8daa-b30fe47e0a0a', 'Geas', 'You place a magical command on a creature that you can see within range, forcing it to carry out some service or refrain from some action or course of activity as you decide. If the creature can understand you, it must succeed on a Wisdom saving throw or become charmed by you for the duration. While the creature is charmed by you, it takes 5d10 psychic damage each time it acts in a manner directly counter to your instructions, but no more than once each day. A creature that can''t understand you is unaffected by the spell.<br> You can issue any command you choose, short of an activity that would result in certain death. Should you issue a suicidal command, the spell ends.<br> You can end the spell early by using an action to dismiss it. A <i>remove curse</i>, <i>greater restoration</i>, or <i>wish</i> spell also ends it.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th or 8th level, the duration is 1 year. When you cast this spell using a spell slot of the spell lasts until it is ended by one of the spells mentioned above.', '', '60 feet', 'V', NULL, '1 minute', false, '30 days', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0d90b104-da4d-4dbc-b348-6364313528dd', 'Hold Monster', 'Choose a creature that you can see within range. The target must succeed on a Wisdom saving throw or be paralyzed for the duration. This spell has no effect on undead. At the end of each of its turns, the target can make another Wisdom saving throw. On a success, the spell ends on the target.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, you can target on additional creature for each slot level above 5th. The creatures must be within 30 feet of each other when you target them.', 'a small, straight piece of iron', '90 feet', 'VSM', NULL, '1 action', false, 'Until dispelled', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7ffd1975-a31f-4f24-b756-3ec1114cab0b', 'Legend Lore', 'Name or describe a person, place, or object. The spell brings to your mind a brief summary of the significant lore about the thing you named. The lore might consist of current tales, forgotten stories, or even secret lore that has never been widely known. If the thing you named isn''t of legendary importance, you gain no information.<br> The more information you already have about the thing, the more precise and detailed the information you receive is.<br> The information you learn is accurate but might be couched in figurative language. For example, if you have a mysterious magic axe on hand, the spell might yield this information - Woe to the evildoer whose hand touches the axe, for even the haft slices the hand of the evil ones. Only a true Child of Stone, lover and beloved of Moradin, may awaken the true powers of the axe, and only with the sacred word <i>Rudnogg</i> on the lips.', 'incense worth at least 250 gp, which the spell consumes, and four ivory strips worth at least 50 gp each', 'Self', 'VSMgp', NULL, '10 minutes', false, 'up to 10 minutes', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('08885028-98a0-42d2-aeb0-444c6c064ad5', 'Mass Cure Wounds', 'A wave of healing energy washes out from a point of your choice within range. Choose up to six creatures in a 30-foot-radius sphere centered on that point. Each target regains hit points equal to 3d8 + your spellcasting ability modifier. This spell has no effect on undead or constructs.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the healing increases by 1d8 for each slot level above 5th.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5917df17-e4a6-43f5-a870-a10120877967', 'Mislead', 'You become invisible at the same time that an illusory double of you appears where you are standing. The double lasts for the duration, but the invisibility ends if you attack or cast a spell.<br> You can use your action to move your illusory double up to twice your speed and make it gesture, speak, and behave in whatever way you choose.<br> You can see through its eyes and hear through its ears as if you were located where it is. On each of your turns as a bonus action, you can switch from using its senses to using your own, or back again. While you are using its senses, you are blinded and deafened in regard to your own surroundings.', '', 'Self', 'S', NULL, '1 action', false, 'Instantaneous', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('67683630-3e51-4d3a-ade0-0934d7bc20ed', 'Scatter', 'The air quivers around up to five creatures of your choice that you can see within range. An unwilling creature must succeed on a Wisdom saving throw to resist this spell. You teleport each affected target to an unoccupied space that you can see within 120 feet of you. That space must be on the ground or on a floor.', '', '30 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7ccb47b7-39bb-4408-af41-c3dee1c4a261', 'Modify Memory', 'You attempt to reshape another creature''s memories. One creature that you can see must make a Wisdom saving throw. If you are fighting the creature, it has advantage on the saving throw. On a failed save, the target becomes charmed by you for the duration. The charmed target is incapacitated and unaware of its surroundings, though it can still hear you. If it takes any damage or is targeted by another spell, this spell ends, and none of the target''s memories are modified.<br> While this charm lasts, you can affect the target''s memory of an event that it experienced within the last 24 hours and that lasted no more than 10 minutes. You can permanently eliminate all memory of the event, allow the target to recall the event with perfect clarity and exacting detail, change its memory of the details of the event, or create a memory of some other event.<br> You must speak to the target to describe how its memories are affected, and it must be able to understand your language for the modified memories to take root. Its mind fills in any gaps in the details of your description. If the spell ends before you have finished describing the modified memories, the creature''s memory isn''t altered. Otherwise, the modified memories take hold when the spell ends.<br> A modified memory doesn''t necessarily affect how a creature behaves, particularly if the memory contradicts the creature''s natural inclinations, alignment, or beliefs. An illogical modified memory, such as implanting a memory of how much the creature enjoyed dousing itself in acid, is dismissed, perhaps as a bad dream. The DM might deem a modified memory too nonsensical to affect a creature in a significant manner.<br> A <i>remove curse</i> or <i>greater restoration</i> spell cast on the target restores the creature''s true memory.<br> <b>At Higher Levels</b>: If you cast this spell using a spell slot of 6th level or higher, you can alter the target''s memories of an event that took place up to 7 days ago (6th level), 30 days ago (7th level), 1 year ago (8th level), or any time in the creature''s past (9th level).', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d089a43b-fd1e-441d-9726-92b71887fb35', 'Tenser''s Floating Disk', 'This spell creates a circular, horizontal plane of force, 3 feet in diameter and 1 inch thick, that floats 3 feet above the ground in an unoccupied space of your choice that you can see within range. The disk remains for the duration, and can hold up to 500 pounds. If more weight is placed on it, the spell ends, and everything on the disk falls to the ground.<br> The disk is immobile while you are within 20 feet of it. If you move more than 20 feet away from it, the disk follows you so that it remains within 20 feet of you. It can more across uneven terrain, up or down stairs, slopes and the like, but it can''t cross an elevation change of 10 feet or more. For example, the disk can''t move across a 10-foot-deep pit, nor could it leave such a pit if it was created at the bottom.<br> If you move more than 100 feet from the disk (typically because it can''t move around an obstacle to follow you), the spell ends.', 'a drop of mercury', '30 feet', 'VSM', NULL, '1 action', true, '1 hour', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('46b00466-6757-4ace-b9ce-9c2ca123f526', 'Shield of Faith', 'A shimmering field appears and surrounds a creature of your choice within range, granting it a +2 bonus to AC for the duration.', 'a small parchment with a bit of holy text written on it', '60 feet', 'VSM', NULL, '1 bonus action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6c308da3-b34d-4133-9b97-5ac4cfc3da3d', 'Planar Binding', 'With this spell, you attempt to bind a celestial, an elemental, a fey, or a fiend to your service. The creature must be within range for the entire casting of the spell. (Typically, the creature is first summoned into the center of an inverted <i>magic circle</i> in order to keep it trapped while this spell is cast.) At the completion of the casting, the target must make a Charisma saving throw. On a failed save, it is bound to serve you for the duration. If the creature was summoned or created by another spell, that spell''s duration is extended to match the duration of this spell.<br> A bound creature must follow your instructions to the best of its ability. You might command the creature to accompany you on an adventure, to guard a location, or to deliver a message. The creature obeys the letter of your instructions, but if the creature is hostile to you, it strives to twist your words to achieve its own objectives. If the creature carries out your instructions completely before the spell ends, it travels to you to report this fact if you are on the same plane of existence. If you are on a different plane of existence, it returns to the place where you bound it and remains there until the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of a higher level, the duration increases to 10 days with a 6th-level slot, to 30 days with a 7th-level slot, to 180 days with an 8th-level slot, and to a year and a day with a 9th-level spell slot.', 'a jewel worth at least 1,000 gp, which the spell consumes', '60 feet', 'VSMgp', NULL, '1 hour', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5adfb1a8-a32c-4084-adc7-3296440ddeb8', 'Raise Dead', 'You return a dead creature you touch to life, provided that it has been dead no longer than 10 days. If the creature''s soul is both willing and at liberty to rejoin the body, the creature returns to life with 1 hit point.<br> This spell also neutralizes any poison and cures nonmagical diseases that affected the creature at the time it died. This spell doesn''t, however, remove magical diseases, curses, or similar effects, if these aren''t first removed prior to casting the spell, they take effect when the creature returns to life. The spell can''t return an undead creature to life.<br> This spell closes all mortal wounds, but it doesn''t restore missing body parts. If the creature is lacking body parts or organs integral for its survival - its head, for instance - the spell automatically fails.<br> Coming back from the dead is an ordeal. The target takes a -4 penalty to all attack rolls, saving throws, and ability checks. Every time the target finishes a long rest, the penalty is reduced by 1 until it disappears.', 'a diamond worth at least 500 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 hour', false, '24 hours', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('133a3578-2c73-4dfe-b7e2-1e527b38d5aa', 'Scrying', 'You can see and hear a particular creature you choose that is on the same plane of existence as you. The target must make a Wisdom saving throw, which is modified by how well you know the target and the sort of physical connection you have to it. If a target knows you''re casting this spell, it can fail the saving throw voluntarily if it wants to be observed.<br> <b>Knowledge</b> - Secondhand (you have heard of the target) +5. Firsthand (you have met the target) +0. Familiar (you know the target well) -5.<br> <b>Connection</b> Likeness or picture -2. Possession or garment -4. Body part, lock of hair, bit of nail, or the like -10.<br> On a successful save, the target isn''t affected, and you can''t use this spell against it again for 24 hours.<br> On a failed save, the spell creates an invisible sensor within 10 feet of the target. You can see and hear through the sensor as if you were there. The sensor moves with the target, remaining within 10 feet of it for the duration. A creature that can see invisible objects sees the sensor as a luminous orb about the size of your fist.<br> Instead of targeting a creature, you can choose a location you have seen before as the target of this spell. When you do, the sensor appears at that location and doesn''t move.', 'A focus worth at least 1,000 gp, such as a crystal ball, a silver mirror, or a font filled with holy water', 'Self', 'VSMgp', NULL, '10 minutes', false, 'Instantaneous', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6b85ade4-19fc-4321-a20f-f93f1c75cc58', 'Tasha''s Caustic Brew', 'A stream of acid emanates from you in a line 30 feet long and 5 feet wide in a direction you choose. Each creature in the line must succeed on a Dexterity saving throw or be covered in acid for the spell''s duration or until a creature uses its action to scrape or wash the acid off itself or another creature. A creature covered in the acid takes 2d4 acid damage at start of each of its turns.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot 2nd level or higher, the damage increases by 2d4 for each slot level above 1st.', 'a bit of rotten food', 'Self (30-foot line)', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2afc8262-3839-449c-9e1e-33821b805c8d', 'Greater Restoration', 'You imbue a creature you touch with positive energy to undo a debilitating effect. You can reduce the target''s exhaustion level by 1, or end one of the following effects on the target.<br>  1 effect that charmed or petrified the target<br>  1 curse, including the target''s attunement to a cursed magic item<br>  Any reduction to 1 of the target''s ability scores<br>  1 effect reducing the target''s hit point maximum', 'Diamond dust worth 100 gp, Consumed', 'Touch', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5b8fc61c-1416-442e-b3b3-7702b9f3d959', 'Seeming', 'This spell allows you to change the appearance of any number of creatures that you can see within range. You give each target you choose a new, illusory appearance. An unwilling target can make a Charisma saving throw, and if it succeeds, it is unaffected by this spell.<br> The spell disguises physical appearances as well as clothing, armor, weapons, and equipment. You can make each creature seem 1 foot shorter or taller and appear thin, fat, or in-between. You can''t change a target''s body type, so you must choose a form that has the same basic arrangement of limbs. Otherwise, the extent of the illusion is up to you. The spell lasts for the duration, unless you use your action to dismiss it sooner.<br> The changes wrought by this spell fail to hold up to physical inspections. For example, if you use this spell to add a hat to a creature''s outfit objects pass through the hat, and anyone who touches it would feel nothing or would feel the creature''s head and hair. If you use this spell to appear thinner then you are, the hand of someone who reaches out to touch you would bump into you while it was seemingly still in midair.<br> A creature can use its action to inspect a target and make an Intelligence (Investigation) check against your spell save DC. If it succeeds, it becomes aware that the target is disguised.', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('42d82183-1cfe-4792-8eda-4ab65cd9ee47', 'Teleportation Circle', 'As you cast the spell, you draw a 10-foot-diameter circle on the ground inscribed with sigils that link your location to a permanent teleportation circle of your choice whose sigil sequence you know and that is on the same plane of existence as you. A shimmering portal opens within the circle you drew and remains open until the end of your next turn. Any creature that enters the portal instantly appears within 5 feet of the destination circle or in the nearest unoccupied space if that space is occupied.<br> Many major temples, guilds, and other important places have permanent teleportation circles inscribed somewhere within their confines. Each such circle includes a unique sigil sequence - a string of magical runes arranged in a particular pattern. When you first gain the ability to cast this spell, you learn the sigil sequences for two destinations on the Material Plane, determined by the DM. You can learn additional sigil sequences during your adventures. You can commit a new sigil sequence to memory after studying it for 1 minute.<br> You can create a permanent teleportation circle by casting this spell in the same location every day for one year. You need not use the circle to teleport when you cast the spell in this way.', 'rare chalks and inks infused with precious gems with 50 gp, which the spell consumes', '10 feet', 'VMgp', NULL, '1 minute', false, 'up to 10 minutes', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('422933fd-17b1-4794-afad-b0a7c69e5819', 'Eyebite', 'For the spell''s duration, your eyes become an inky void imbued with dread power. One creature of your choice within 60 feet of you that you can see must succeed on a Wisdom saving throw or be affected by one of the following effects of your choice for the duration. On each of your turns until the spell ends, you can use your action to target another creature but can''t target a creature again if it has succeeded on a saving throw against this casting of <i>eyebite</i>.<br> <b>Asleep</b>: The target falls unconscious. It wakes up if it takes any damage or if another creature uses its action to shake the sleeper awake.<br> <b>Panicked</b>: The target is frightened of you. On each of its turns, the frightened creature must take the Dash action and move away from you by the safest and shortest available route, unless there is nowhere to move. If the target moves to a place at least 60 feet away from you where it can no longer see you, this effect ends.<br> <b>Sickened</b>: The target has disadvantage on attack rolls and ability checks. At the end of each of its turns, it can make another Wisdom saving throw. If it succeeds, the effect ends.', '', 'Self', 'VS', NULL, '1 action', false, '24 hours', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ffd4f71d-ff75-4d79-83b4-b482224a4242', 'Find the Path', 'This spell allows you to find the shortest, most direct physical route to a specific fixed location that you are familiar with on the same plane of existence. If you name a destination on another plan of existence, a destination that moves (such as a mobile fortress), or a destination that isn''t specific (such as a green dragon''s lair), the spell fails.<br> For the duration, as long as you are on the same plane of existence as the destination, you know how far it is and in what direction it lies. While you are traveling there, whenever you are presented with a choice of paths along the way, you automatically determine which path is the shortest and most direct route (but not necessarily the safest route) to the destination.', 'A set of divinatory tools - such as bones, ivory sticks, cards, teeth, or carved runes - worth 100 gp and an object from the location you wish to find', 'Self', 'VSMgp', NULL, '1 minute', false, 'up to 1 minute', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bf4c3a88-ee0b-45f5-9f1f-3b7dcb8079b7', 'Aganazzar''s Scorcher', 'A line of roaring flame 30 feet long and 5 feet wide emanates from you in a direction you choose. Each creature in the line must make a Dexterity saving throw. A creature takes 3d8 fire damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d8 for each slot level above 2nd.', 'a red dragon''s scale', '30 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d65ec512-bfe1-4e87-9d9f-b52d1ad5fa35', 'Maximilian''s Earthen Grasp', 'You choose a 5-foot-square unoccupied space on the ground that you can see within range. A Medium hand made from compacted soil rises there and reaches for one creature you can see within 5 feet of it. The target must make a Strength saving throw. On a failed save, the target takes 2d6 bludgeoning damage and is restrained for the spell''s duration.<br> As an action, you can cause the hand to crush the restrained target, which must make a Strength saving throw. The target takes 2d6 bludgeoning damage on a failed save, or half as much damage on a successful one.<br> To break out, the restrained target can use its action to make a Strength check against your spell save DC. On a success, the target escapes and is no longer restrained by the hand.<br> As an action, you can cause the hand to reach for a different creature or to move to a different unoccupied space within range. The hand releases a restrained target if you do either.', 'a miniature hand sculpted from clay', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7a1b4d73-9647-408d-a0a1-7c9a9e9d10bc', 'Guards and Wards', 'You create a ward that protects up to 2,500 square feet of floor space (an area 50 feet square, or one hundred 5-foot squares or twenty-five 10-foot squares). The warded area can be up to 20 feet tall, and shaped as you desire. You can ward several stories of a stronghold by dividing the area among them, as long as you can walk into each contiguous area while you are casting the spell.<br> When you cast this spell, you can specify individuals that are unaffected by any or all of the effects that you choose. You can also specify a password that, when spoken aloud, makes the speaker immune to these effects.<br> <i>Guards and wards</i> creates the following effects within the warded area.<br> <b>Corridors</b>: Fog fills all the warded corridors, making them heavily obscured. In addition, at each intersection or branching passage offering a choice of direction, there is a 50 percent chance that a creature other than you will believe it is going in the opposite direction from the one it chooses.<br> <b>Doors</b>: All doors in the warded area are magically locked, as if sealed by an <i>arcane</i> lock spell. In addition, you can cover up to ten doors with an illusion (equivalent to the illusory object function of the <i>minor illusion</i> spell) to make them appear as plain sections of wall.<br> <b>Stairs</b>: Webs fill all stairs in the warded area from top to bottom, as the <i>web</i> spell. These strands regrow in 10 minutes if they are burned or torn away while <i>guards and wards</i> lasts.<br> <b>Other Spell Effect</b>: You can place your choice of one of the following magical effects within the warded area of the stronghold.<br>  Place <i>dancing lights</i> in four corridors. You can designate a simple program that the lights repeat as long as <i>guards and wards</i> lasts.<br>  Place <i>magic mouth</i> in two locations.<br>  Place <i>stinking cloud</i> in two locations. The vapors appear in the places you designate they return within 10 minutes if dispersed by wind while <i>guards and wards</i> lasts.<br>  Place a constant <i>gust of wind</i> in one corridor or room.<br>  Place a <i>suggestion</i> in one location. You select an area of up to 5 feet square, and any creature that enters or passes through the area receives the suggestion mentally.<br> The whole warded area radiates magic. A <i>dispel magic</i> cast on a specific effect, if successful, removes only that effect.<br> You can create a permanently guarded and warded structure by casting this spell there every day for one year.', 'burning incense, a small measure of brimstone and oil, a knotted string, a small amount of umber hulk blood, and a small silver rod worth at least 10 gp', 'Touch', 'VSMgp', NULL, '10 minutes', false, 'up to 1 minute', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b7309309-509e-4048-b204-40f145a5214e', 'Mass Suggestion', 'You suggest a course of activity (limited to a sentence or two) and magically influence up to twelve creatures of your choice that you can see within range and that can hear and understand you. Creatures that can''t be charmed are immune to this effect. The suggestion must be worded in such a manner as to make the course of action sound reasonable. Asking the creature to stab itself, throw itself onto a spear, immolate itself, or do some other obviously harmful act automatically negates the effect of the spell.<br> Each target must make a Wisdom saving throw. On a failed save, it pursues the course of action you described to the best of its ability. The suggested course of action can continue for the entire duration. If the suggested activity can be completed in a shorter time, the spell ends when the subject finishes what it was asked to do.<br> You can also specify conditions that will trigger a special activity during the duration. For example, you might suggest that a group of soldiers give all their money to the first beggar they meet. If the condition isn''t met before the spell ends, the activity isn''t performed.<br> If you or any of your companions damage a creature affected by this spell, the spell ends for that creature.<br> <b>At Higher Levels</b>: When you cast this spell using a 7th-level spell slot, the duration is 10 days. When you use an 8th-level spell slot, the duration is 30 days. When you use a 9th-level spell slot, the duration is a year and a day.', 'a snake''s tongue and either a bit of honeycomb or a drop of sweet oil', '60 feet', 'VM', NULL, '1 action', false, 'Until dispelled', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5199bde2-f30b-4278-95a5-3e6a94041589', 'Programmed Illusion', 'You create an illusion of an object, a creature, or some other visible phenomenon within range that activates when a specific condition occurs. The illusion is imperceptible until then. It must be no larger than a 30-foot cube, and you decide when you cast the spell how the illusion behaves and what sounds it makes. This scripted performance can last up to 5 minutes.<br> When the condition you specify occurs, the illusion springs into existence and performs in the manner you described. Once the illusion finishes performing, it disappears and remains dormant for 10 minutes. After this time, the illusion can be activated again.<br> The triggering condition can be as general or as detailed as you like, though it must be based on visual or audible conditions that occur within 30 feet of the area. For example, you could create an illusion of yourself to appear and warn off others who attempt to open a trapped door, or you could set the illusion to trigger only when a creature says the correct word or phrase.<br> Physical interaction with the image reveals it to be an illusion, because things can pass through it. A creature that uses its action to examine the image can determine that it is an illusion with a successful Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the creature can see through the image, and any noise it makes sounds hollow to the creature.', '', '120 feet', 'VSMgp', NULL, '1 action', false, 'Until dispelled', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('943f8b3d-bc5b-4e54-93a8-17e5ea81a479', 'Etherealness', 'You step into the border regions of the Ethereal Plane, in the area where it overlaps with your current plane. You remain in the Border Ethereal for the duration or until you use your action to dismiss the spell. During this time, you can move in any direction. If you move up or down, every foot of movement costs an extra foot. You can see and hear the plane you originated from, but everything there looks gray, and you can''t see anything more than 60 feet away.<br> While on the Ethereal Plane, you can only affect and be affected by other creatures on that plane. Creatures that aren''t on the Ethereal Plane can''t perceive you and can''t interact with you, unless a special ability or magic has given them the ability to do so.<br> You ignore all objects and effects that aren''t on the Ethereal Plane, allowing you to move through objects you perceive on the plaen you originated from.<br> When the spell ends, you immediately return to the plane you originated from in the spot you currently occupy. If you occupy the same spot as a solid object or creature when this happens, you are immediately shunted to the nearest unoccupied space that you can occupy and take force damage equal to twice the number of feet you are moved.<br> This spell has no effect if you cast it while you are on the Ethereal Plane or a plane that doesn''t border it, such as one of the Outer Planes.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 8th level or higher, you can target up to three willing creatures (including you) for each slot level above 7th. The creatures must be within 10 feet of you when you cast the spell.', '', 'Self', 'VS', NULL, '1 action', false, 'Up to 8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e80b7b0f-617f-44bf-9fa0-10ae58ef7e81', 'Otiluke''s Resilient Sphere', 'A sphere of shimmering force encloses a creature or object of Large size or smaller within range. An unwilling creature must make a Dexterity saving throw. On a failed save, the creature is enclosed for the duration.<br> Nothing, not physical objects, energy, or other spell effects, can pass through the barrier, in or out, though a creature in the sphere can breathe there. The sphere is immune to all damage, and a creature or object inside can''t be damaged by attacks or effects originating from outside, nor can a creature inside the sphere damage anything outside it.<br> The sphere is weightless and just large enough to contain the creature or object inside. An enclosed creature can use its action to push against the sphere''s walls and thus roll the sphere at up to half the creature''s speed. Similarly, the globe can be picked up and moved by other creatures. A <i>disintegrate</i> spell targeting the globe destroys it without harming anything inside it.', 'a hemispherical piece of clear crystal and a matching hemispherical piece of gum arabic', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5691c59c-9f2c-454c-bf4b-42df192d29df', 'Forcecage', 'An immobile, invisible, cube-shaped prison composed of magical force springs into existence around an area you choose within range. The prison can be a cage or a solid box as you choose.<br> A prison in the shape of a cage can be up to 20 feet on a side and is made from 1/2-inch diameter bars spaced 1/2 inch apart.<br> A prison in the shape of a box can be up to 10 feet on a side, creating a solid barrier that prevents any matter from passing through it and blocking any spells cast into or out of the area.<br> When you cast the spell, any creature that is completely inside the cage''s area is trapped. Creatures only partially within the area, or those too large to fit inside the area, are pushed away from the center of the area until they are completely outside the area.<br> A creature inside the cage can''t leave it by nonmagical means. If the creature tries to use teleportation or interplanar travel to leave the cage, it must first make a Charisma saving throw. On a success, the creature can use that magic to exit the cage. On a failure, the creature can''t exit the cage and wastes the use of the spell or effect. The cage also extends into the Ethereal Plane, blocking ethereal travel.<br> This spell can''t be dispelled by <i>dispel magic</i>.', 'Ruby dust worth 1,500 gp', '100 feet', 'VSMgp', NULL, '1 action', false, '1 hour', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d41cc063-69ed-4b95-9ae5-5d0190c9ff3b', 'Mirage Arcane', 'You make terrain in an area up to 1 mile square look, sound, smell, and even feel like some other sort of terrain. The terrain''s general shape remains the same, however. Open fields or a road could be made to resemble a swamp, hill, crevasse, or some other difficult or impassable terrain. A pond can be made to seem like a grassy meadow, a precipice like a gentle slope, or a rock-strewn gully like a wide and smooth road.<br> Similarly, you can alter the appearance of structures, or add them where none are present. The spell doesn''t disguise, conceal, or add creatures.<br> The illusion includes audible, visual, tactile, and olfactory elements, so it can turn clear ground into difficult terrain (or vice versa) or otherwise impede movement through the area. Any piece of the illusory terrain (such as a rock or stick) that is removed from the spell''s area disappears immediately.<br> Creatures with truesight can see through the illusion to the terrain''s true form, however, all other elements of the illusion remain, so while the creature is aware of the illusion''s presence, the creature can still physically interact with the illusion.', '', 'Sight', 'VS', NULL, '10 minutes', false, '10 days', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0978eb93-2b99-4f48-9871-6300d3663548', 'Project Image', 'You create an illusory copy of yourself that lasts for the duration. The copy can appear at any location within range that you have seen before, regardless of intervening obstacles. The illusion looks and sounds like you but is intangible. If the illusion takes any damage, it disappears, and the spell ends.<br> You can use your action to move this illusion up to twice your speed, and make it gesture, speak, and behave in whatever way you choose. It mimics your mannerisms perfectly.<br> You can see through its eyes and hear through its ears as if you were in its space. On your turn as a bonus action, you can switch from using its senses to using your own, or back again. While you are using its senses, you are blinded and deafened in regard to your own surroundings.<br> Physical interaction with the image reveals it to be an illusion, because things can pass through it. A creature that uses its action to examine the image can determine that it is an illusion with a successful Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the creature can see through the image, and any noise it makes sounds hollow to the creature.', 'a small replica of you made from materials worth at least 5 gp', '500 miles', 'VSMgp', NULL, '1 action', false, 'up to 1 day', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('79e9c744-697f-4044-b08b-6fdca41b0dd7', 'Resurrection', 'You touch a dead creature that has been dead for no more than a century, that didn''t die of old age, and that isn''t undead. If its soul is free and willing, the target returns to life with all its hit points.<br> This spell neutralizes any poisons and cures normal diseases afflicting the creature when it died. It doesn''t, however, remove magical diseases, curses, and the like, if such affects aren''t removed prior to casting the spell, they afflict the target on its return to life.<br> This spell closes all mortal wounds and restores any missing body parts.<br> Coming back from the dead is an ordeal. The target takes a -4 penalty to all attack rolls, saving throws, and ability checks. Every time the target finishes a long rest, the penalty is reduced by 1 until it disappears.<br> Casting this spell to restore life to a creature that has been dead for one year or longer taxes you greatly. Until you finish a long rest, you can''t cast spells again, and you have disadvantage on all attack rolls, ability checks, and saving throws.', 'a diamond worth at least 1,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 hour', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6ed00f90-d003-4eae-b403-502e8c5bb63c', 'Dominate Monster', 'You attempt to beguile a creature that you can see within range. It must succeed on a Wisdom saving throw or be charmed by you for the duration. If you or creatures that are friendly to you are fighting it, it has advantage on the saving throw.<br> While the creature is charmed, you have a telepathic link with it as long as the two of you are on the same plane of existence. You can use this telepathic link to issue commands to the creature while you are conscious (no action required), which it does its best to obey. You can specify a simple and general course of action, such as Attack that creature, Run over there, or Fetch that object. If the creature completes the order and doesn''t receive further direction from you, it defends and preserves itself to the best of its ability.<br> You can use your action to take total and precise control of the target. Until the end of your next turn, the creature takes only the actions you choose, and doesn''t do anything that you don''t allow it to do. During this time, you can also cause the creature to use a reaction, but this requires you to use your own reaction as well.<br> Each time the target takes damage, it makes a new Wisdom saving throw against the spell. If the saving throw succeeds, the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell with a 9th-level spell slot, the duration is concentration, up to 8 hours.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fc72f5bc-1d5d-4dc8-8539-dfaa0f240188', 'Feeblemind', 'You blast the mind of a creature that you can see within range, attempting to shatter its intellect and personality. The target takes 4d6 psychic damage and must make an Intelligence saving throw.<br> On a failed save, the creature''s Intelligence and Charisma scores become 1. The creature can''t cast spells, activate magic items, understand language, or communicate in any intelligible way. The creature can, however, identify its friends, follow them, and even protect them.<br> At the end of every 30 days, the creature can repeat its saving throw against this spell. If it succeeds on its saving throw, the spell ends.<br> The spell can also be ended by <i>greater restoration</i>, <i>heal</i> or <i>wish</i>.', 'A handful of clay, crystal, glass, or mineral spheres', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e5a8982c-0c7f-4882-8c82-b0861912fc0f', 'Foresight', 'You touch a willing creature and bestow a limited ability to see into the immediate future. For the duration, the target can''t be surprised and has advantage on attack rolls, ability checks, and saving throws. Additionally, other creatures have disadvantage on attack rolls against the target for the duration.<br> This spell immediately ends if you cast it again before its duration ends.', 'A hummingbird feather', 'Touch', 'VSM', NULL, '1 minute', false, '8 hours', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d0ef2508-2b79-4642-86fa-1bb77a8a2e98', 'True Polymorph', 'Choose one creature with at least 1 hit point or nonmagical object that you can see within range. You transform the creature into a different creature, the creature into an object, or the object into a creature (the object must be neither worn nor carried by another creature). The transformation lasts for the duration, or until the target drops to 0 hit points or dies. If you concentrate on this spell for the full duration, the transformation becomes permanent.<br> Shapechangers aren''t affected by this spell. An unwilling creature can make a Wisdom saving throw, and if it succeeds, it isn''t affected by this spell.<br> <b>Creature into Creature</b>: If you turn a creature into another kind of creature, the new form can be any kind you choose whose challenge rating is equal to or less than the target''s (or its level, if the target doesn''t have a challenge rating). The target''s game statistics, including mental ability scores, are replaced by the statistics of the new form. It retains its alignment and personality.<br> The target assumes the hit points of its new form, and when it reverts to its normal form, the creature returns to the number of hit points it had before it transformed. If it reverts as a result of dropping to 0 hit points, any excess damage carries over to its normal form. As long as the excess damage doesn''t reduce the creature''s normal form to 0 hit points, it isn''t knocked unconscious.<br> The creature is limited in the actions it can perform by the nature of its new form, and it can''t speak, cast spells, or take any other action that requires hands or speech unless its new form is capable of such actions.<br> The target''s gear melds into the new form. The creature can''t activate, use, wield, or otherwise benefit from any of its equipment.<br> <b>Object into Creature</b>: You can turn an object into any kind of creature, as long as the creature''s size is no larger than the object''s size and the creature''s challenge rating is 9 or lower. The creature is friendly to you and your companions. It acts on each of your turns. You decide what action it takes and how it moves. The DM has the creature''s statistics and resolves all of its actions and movement.<br> If the spell becomes permanent, you no longer control the creature. It might remain friendly to you, depending on how you have treated it.<br> <b>Creature into Object</b>: If you turn a creature into an object, it transforms along with whatever it is wearing and carrying into that form. The creature''s statistics become those of the object, and the creature has no memory of time spent in this form, after the spell ends and it returns to its normal form.', 'a drop of mercury, a dollop of gum arabic, and a wisp of smoke', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d6b88003-a630-474b-b89a-a4b69dea01fa', 'Sacred Flame', 'Flame-like radiance descends on a creature that you can see within range. The target must succeed on a Dexterity saving throw or take 1d8 radiant damage. The target gains no benefit from cover for this saving throw.<br> The spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('005b1f38-806b-45c9-a4f8-401bfd46bbd3', 'Thaumaturgy', 'You manifest a minor wonder, a sign of supernatural power, within range. You create one of the following magical effects within range.<br>  Your voice booms up to three times as loud as normal for 1 minute.<br>  You cause flames to flicker, brighten, dim, or change color for 1 minute.<br>  You cause harmless tremors in the ground for 1 minute.<br>  You create an instantaneous sound that originates from a point of your choice within range, such as a rumble of thunder, the cry of a raven, or ominous whispers.<br>  You instantaneously cause an unlocked door or window to fly open or slam shut.<br>  You alter the appearance of your eyes for 1 minute.<br> If you cast this spell multiple times, you can have up to three of its 1-minute effects active at a time, and you can dismiss such an effect as an action.', '', '30 feet', 'V', NULL, '1 action', false, 'Up to 1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('eb21e978-ae6f-43b5-b1b1-3200da33df70', 'Bless', 'You bless up to three creatures of your choice within range. Whenever a target makes an attack roll or a saving throw before the spell ends, the target can roll a d4 and add the number rolled to the attack roll or saving throw.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target one additional creature for each slot level above 1st.', 'a sprinkling of holy water', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e5fc937b-9a7d-4577-9c65-ef12d7bf76ec', 'Command', 'You speak a one-word command to a creature you can see within range. The target must succeed on a Wisdom saving throw or follow the command on its next turn. The spell has no effect if the target is undead, if it doesn''t understand your language, or if your command is directly harmful to it.<br> Some typical commands and their effects follow.<br> You might issue a command other than one described here. If you do so, the DM determines how the target behaves. If the target can''t follow your command, the spell ends.<br> <b>Approach</b>: The target moves toward you by the shortest and most direct route, ending its turn if it moves within 5 feet of you.<br> <b>Drop</b>: The target drops whatever it is holding and then ends its turn.<br> <b>Flee</b>: The target spends its turn moving away from you by the fastest available means.<br> <b>Grovel</b>: The target falls prone and then ends its turn.<br> <b>Halt</b>: The target doesn''t move and takes no actions. A flying creature stays aloft, provided that it is able to do so. If it must move to stay aloft, it flies the minimum distance needed to remain in the air.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can affect one additional creature for each slot level above 1st. The creatures must be within 30 feet of each other when you target them.', '', '60 feet', 'V', NULL, '1 action', false, '1 round', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c2c32450-52ee-4447-bfcd-e8f69c745ad9', 'Create or Destroy Water', 'You either create or destroy water.<br> <b>Create Water</b>: You create up to 10 gallons of clean water within range in an open container. Alternatively, the water falls as rain in a 30-foot cube within range, extinguishing exposed flames in the area.<br> <b>Destroy Water</b>: You destroy up to 10 gallons of water in an open container within range. Alternatively, you destroy fog in a 30-foot cube within range.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you create or destroy 10 additional gallons of water, or the size of the cube increases by 5 feet, for each slot level above 1st.', 'a drop of water if creating water or a few grains of sand if destroying it', '30 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7d9cec34-b827-45a4-8366-c20a5d95829e', 'Detect Poison and Disease', 'For the duration, you can sense the presence and location of poisons, poisonous creatures, and diseases within 30 feet of you. You also identify the kind of poison, poisonous creature, or disease in each case.<br> The spell can penetrate most barriers, but is blocked by 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt.', 'a yew leaf', 'Self', 'VSM', NULL, '1 action', true, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e843ed96-03c8-47bc-a5dd-e69fca5725ee', 'Teleport', 'This spell instantly transports you and up to eight willing creatures of your choice that you can see within range, or a single object that you can see within range, to a destination you select. If you target an object, it must be able to fit entirely inside a 10-foot cube, and it can''t be held or carried by an unwilling creature. The destination you choose must be known to you, and it must be on the same plane of existence as you. Your familiarity with the destination determines whether you arrive there successfully. The DM rolls d100 and consults the table.<br> Familiarity-Mishap-Similar Area-Off Target-On Target<br> Perm. Circle - xxxx - xxxxxxxxxx - xxxxxxxx - 01-100<br> Assoc. Object - xxxx - xxxxxxxxx - xxxxxxxx - 01-100<br> Very Familiar - 01-05 - 06-13. - - - 14-24. - - - 25-100<br> Seen Casually- 01-33 - 34-43. - - - 44-53. - - - 54-100<br> Viewed Once - 01-43 - 44-53. - - - 54-73. - - - 74-100<br> Description. - - 01-43 - 44-53. - - - 54-73. - - - 74-100<br> False Dest. - - - 01-50 - 51-100. - - xxxxxxxx - xxxxxx<br> <b>Familiarity</b>: Permanent Circle means a permanent teleportation circle whose sigil sequence you know. Associated Object means that you possess an object taken from the desired destination within the last six months, such as a book from a wizard''s library, bed linen from a royal suite, or a chunk of marble from a Lich''s secret tomb.<br> Very Familiar is a place you have been very often, a place you have carefully studied, or a place you can see when you cast the spell. Seen Casually is some place you have seen more than once but with which you aren''t very familiar. Viewed Once is a place you have seen once, possibly using magic. Description is a place whose location and appearance you know through someone else''s description, perhaps from a map.<br> False Destination is a place that doesn''t exist. Perhaps you tried to scry an enemy''s sanctum but instead viewed an illusion, or you are attempting to teleport to a familiar location that no longer exists.<br> <b>On Target</b>: You and your group (or the target object) appear where you want to go.<br> <b>Off Target</b>: You and your group (or the target object) appear a random distance away from the destination in a random direction. Distance off target is 1d10 x 1d10 percent of the distance that was to be travelled. For example, if you tried to travel 120 miles, landed off target, and rolled a 5 and 3 on the two d10s, then you would be off target by 15 percent, or 18 miles. The DM determines the direction off target randomly by rolling a d8 and designating 1 as north, 2 as north-east, 3 as east, and so on around the points of the compass. If you were teleporting to a coastal city and wound up 18 miles out at sea, you could be in trouble.<br> <b>Similar Area</b>: You and your group (or the target object) wind up in a different area that''s visually or thematically similar to the target area. If you are heading for your home laboratory, for example, you might wind up in another wizard''s laboratory or in an alchemical supply shop that has many of the same tools and implements as your laboratory. Generally, you appear in the closest similar place, but since the spell has no range limit, you could conceivably wind up anywhere on the plane.<br> <b>Mishap</b>: The spell''s unpredictable magic results in a difficult journey. Each teleporting creature (or the target object) takes 3d10 force damage and the DM rerolls on the table to see where you wind up (multiple mishaps can occur, dealing damage each time).', '', '10 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e2abf65e-9cc7-42cd-864f-508d3a6e4063', 'Lesser Restoration', 'You touch a creature and can end either 1 disease or 1 condition afflicting it. The condition can be blinded, deafened, paralyzed, or poisoned.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('df1c0d13-6f31-4883-906b-b2a83c8526cc', 'Guidance', 'You touch one willing creature. Once before the spell ends, the target can roll a d4 and add the number rolled to one ability check of its choice. It can roll the die before or after making the ability check. The spell then ends.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3d1270ec-8d51-4e0a-8684-6222a932b002', 'Guiding Bolt', 'A flash of light streaks toward a creature of your choice within range. Make a ranged spell attack against the target. On a hit, the target takes 4d6 radiant damage, and the next attack roll made against this target before the end of your next turn has advantage, thanks to the mystical dim light glittering on the target until then.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', '120 feet', 'VS', NULL, '1 action', false, '1 round', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('44d7697b-314b-4b75-8269-9623f618e6fd', 'Protection from Evil and Good', 'Until the spell ends, one willing creature you touch is protected against certain types of creatures - aberrations, celestials, elementals, fey, fiends, and undead.<br> The protection grants several benefits. Creatures of those types have disadvantage on attack rolls against the target. The target also can''t be charmed, frightened, or possessed by them. If the target is already charmed, frightened, or possessed by such a creature, the target has advantage on any new saving throw against the relevant effect.', 'holy water or powdered silver and iron, which the spell consumes', 'Touch', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b6530257-864a-4518-a8f4-fe8d26c144a7', 'Purify Food and Drink', 'All nonmagical food and drink within a 5-foot-radius sphere centered on a point of your choice within range is purified and rendered free of poison and disease.', '', '10 feet', 'VS', NULL, '1 action', true, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b76c88f3-eff4-4574-8f35-b83f28a08337', 'Sanctuary', 'You ward a creature within range against attack. Until the spell ends, any creature who targets the warded creature with an attack or a harmful spell must first make a Wisdom saving throw. On a failed save, the creature must choose a new target or lose the attack or spell. This spell doesn''t protect the warded creature from area effects, such as the explosion of a fireball.<br> If the warded creature makes an attack or casts a spell that affects an enemy creature, this spell ends.', 'a small silver mirror', '30 feet', 'VSM', NULL, '1 bonus action', false, '1 minute', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('336cfb97-9c1e-42fe-bb82-de7ad54141b2', 'Augury', 'By casting gem-inlaid sticks, rolling dragon bones, laying out ornate cards, or employing some other divining tool, you receive an omen from an otherworldly entity about the results of a specific course of action that you plan to take within the next 30 minutes. The DM chooses from the following possible omens.<br>  <i>Weal</i>, for good results<br>  <i>Woe</i>, for bad results<br>  <i>Weal and woe</i>, for both good and bad results<br>  <i>Nothing</i>, for results that aren''t especially good or bad<br> The spell doesn''t take into account any possible circumstances that might change the outcome, such as the casting of additional spells or the loss or gain of a companion.<br> If you cast the spell two or more times before completing your next long rest, there is a cumulative 25 percent chance for each casting after the first that you get a random reading. The DM makes this roll in secret.', 'specially marked sticks, bones, or similar tokens worth at least 25 gp', 'Self', 'VSMgp', NULL, '1 minute', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8d10a1ab-701c-4b6c-9be2-8e481b660a05', 'Continual Flame', 'A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn''t use oxygen. A <i>continual flame</i> can be covered or hidden but not smothered or quenched.', 'ruby dust worth 50 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, 'Until dispelled', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6da7e079-c44e-49be-b322-356337f3f731', 'Find Traps', 'You sense the presence of any trap within range that is within line of sight. A trap, for the purpose of this spell, includes anything that would inflict a sudden or unexpected effect you consider harmful or undesirable, which was specifically intended as such by its creator. Thus, the spell would sense an area affected by the <i>alarm</i> spell, a <i>glyph of warding</i>, or a mechanical pit trap, but it would not reveal a natural weakness in the floor, an unstable ceiling, or a hidden sinkhole.<br> This spell merely reveals that a trap is present. You don''t learn the location of each trap, but you do learn the general nature of the danger posed by a trap you sense.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e90aa4d1-8fe6-4ece-b29f-f70ae3bd033b', 'Gentle Repose', 'You touch a corpse or other remains. For the duration, the target is protected from decay and can''t become undead.<br> The spell also effectively extends the time limit on raising the target from the dead, since days spent under the influence of this spell don''t count against the time limit of spells such as <i>raise dead</i>.', 'a pinch of salt and one copper piece placed on each of the corpse''s eyes, which must remain there for the duration', 'Touch', 'VSM', NULL, '1 action', true, '10 days', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2ff9a79c-4ee4-4c2f-9186-ae80ee6d4693', 'Prayer of Healing', 'Up to six creatures of your choice that you can see within range each regain hit points equal to 2d8 + your spellcasting ability modifier. This spell has no effect on undead or constructs.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the healing increases by 1d8 for each slot level above 2nd.', '', '30 feet', 'V', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('eec7526b-8513-400a-a3a6-d0b72eb2600a', 'Protection from Poison', 'You touch a creature. If it is poisoned, you neutralize the poison. If more than one poison afflicts the target, you neutralize on poison that you know is present, or you neutralize one at random.<br> For the duration, the target has advantage on saving throws against being poisoned, and it has resistance to poison damage.', '', 'Touch', 'VS', NULL, '1 action', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bd4bbf39-0bb5-47b0-a6da-3f690f402ac6', 'Detect Evil and Good', 'For the duration, you know if there is an aberration, celestial, elemental, fey, fiend, or undead within 30 feet of you, as well as where the creature is located. Similarly, you know if there is a place of object within 30 feet of you that has been magically consecrated or desecrated.<br> The spell can penetrate most barriers, but it is blocked by 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6dd039ee-dfcf-464b-b4c1-9d1c3dd3a7ee', 'Misty Step', 'Briefly surrounded by silvery mist, you teleport up to 30 feet to an unoccupied space that you can see.', '', 'Self', 'V', NULL, '1 bonus action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('807a15ee-f810-45b8-a217-614fd79459b3', 'Spiritual Weapon', 'You create a floating, spectral weapon within range that lasts for the duration or until you cast this spell again. When you cast the spell, you can make a melee spell attack against a creature within 5 feet of the weapon. On a hit, the target takes force damage equal to 1d8 + your spellcasting ability modifier.<br> As a bonus action on your turn, you can move the weapon up to 20 feet and repeat the attack against a creature within 5 feet of it.<br> The weapon can take whatever form you choose. Clerics of deities who are associated with a particular weapon (as St. Cuthbert is known for his mace and Thor for his hammer) make this spell''s effect resemble that weapon.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot 3rd level of or higher, the damage increases by 1d8 for every two slot levels above the 2nd.', '', '60 feet', 'VS', NULL, '1 bonus action', false, '1 minute', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5c4e70c8-a25e-4893-8eb6-b72a51ae0d97', 'Warding Bond', 'This spell wards a willing creature you touch and creates a mystic connection between you and the target until the spell ends. While the target is within 60 feet of you, it gains a +1 bonus to AC and saving throws, and it has resistance to all damage. Also, each time it takes damage, you take the same amount of damage.<br> The spell ends if you drop to 0 hit points or if you and the target become separated by more than 60 feet. It also ends if the spell is cast again on either of the connected creatures. You can also dismiss the spell as an action.', 'a pair of platinum rings worth at least 50 gp each, which you and target must wear for the duration', 'Touch', 'VSMgp', NULL, '1 action', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9a9e1b5d-0e79-4c19-a937-7ecab1032c7f', 'Nystul''s Magic Aura', 'You place an illusion on a creature or an object you touch so that divination spells reveal false information about it. The target can be a willing creature or an object that isn''t being carried or worn by another creature.<br> When you cast the spell, choose one or both of the following effects. The effect lasts for the duration. If you cast this spell on the same creature or object every day for 30 days, placing the same effect on it each time, the illusion lasts until it is dispelled.<br> <b>False Aura</b>: You change the way the target appears to spells and magical effects, such as <i>detect magic</i>, that detect magical auras. You can make a nonmagical object appear magical, a magical object appear nonmagical, or change the object''s magical aura so that it appears to belong to a specific school of magic that you choose. When you use this effect on an object, you can make the false magic apparent to any creature that handles the item.<br> <b>Mask</b>: You change the way the target appears to spells and magical effects that detect creature types, such as a paladin''s Divine Sense or the trigger of a <i>symbol</i> spell. You choose a creature type and other spells and magical effects treat the target as if it were a creature of that type or of that alignment.', 'a small square of silk', 'Touch', 'VSM', NULL, '1 action', false, '24 hours', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e99de04f-135c-478f-be6d-d6eed75c95e6', 'Melf''s Acid Arrow', 'A shimmering green arrow streaks toward a target within range and bursts in a spray of acid. Make a ranged spell attack against the target. On a hit, the target takes 4d4 acid damage immediately and 2d4 acid damage at the end of its next turn. On a miss, the arrow splashes the target with acid for half as much of the initial damage and no damage at the end of its next turn.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage (both initial and later) increases by 1d4 for each slot level above 2nd.', 'powdered rhubarb leaf and an adder''s stomach', '90 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ae07f728-8ec1-4375-aafd-fef64733511b', 'Animate Dead', 'This spell creates an undead servant. Choose a pile of bones or a corpse of a Medium or Small humanoid within range. Your spell imbues the target with a foul mimicry of life, raising it as an undead creature. The target becomes a skeleton if you chose bones or a zombie if you chose a corpse (the DM has the creature''s game statistics).<br> On each of your turns, you can use a bonus action to mentally command any creature you made with this spell if the creature is within 60 feet of you (if you control multiple creatures, you can command any or all of them at the same time, issuing the same command to each one). You decide what action the creature will take and where it will move during its next turn, or you can issue a general command, such as to guard a particular chamber or corridor. If you issue no commands, the creature only defends itself against hostile creatures. Once given an order, the creature continues to follow it until its task is complete.<br> The creature is under your control for 24 hours, after which it stops obeying any command you''ve given it. To maintain the control of the creature for another 24 hours, you must cast this spell on the creature again before the current 24-hour period ends. This use of the spell reasserts your control over up to four creatures you have animated with this spell, rather than animating a new one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you animate or reassert control over two additional undead creatures for each slot above 3rd. Each of the creatures must come from a different corpse or pile of bones.', 'a drop of blood, a piece of flesh, and a pinch of bone dust', '10 feet', 'VSM', NULL, '1 minute', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('87d21afd-a660-4721-a99b-96302791d00e', 'Create Food and Water', 'You create 45 pounds of food and 30 gallons of water on the ground or in containers within range, enough to sustain up to fifteen humanoids or five steeds for 24 hours. The food is bland but nourishing, and spoils if uneaten after 24 hours. The water is clean and doesn''t go bad.', '', '30 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('22e8c870-da8e-4d31-8077-2baacfb2a704', 'Daylight', 'A 60-foot-radius sphere of light spreads out from a point you choose within range. The sphere is bright light and sheds dim light for an additional 60 feet.<br> If you chose a point on an object you are holding or one that isn''t being worn or carried, the light shines from the object with and moves with it. Completely covering the affected object with an opaque object, such as a bowl or a helm, blocks the light.<br> If any of this spell''s area overlaps with an area of darkness created by a spell of or lower, the spell that created the darkness is dispelled.', '', '60 feet', 'VS', NULL, '1 action', false, '1 hour', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2df879c7-cdd6-4e40-bc40-d4784b9363ce', 'Magic Circle', 'You create a 10-foot-radius, 20-foot-tall cylinder of magical energy centered on a point on the ground that you can see within range. Glowing runes appear wherever the cylinder intersects with the floor or other surface.<br> Choose one or more of the following types of creatures - celestials, elementals, fey, fiends, or undead. The circle affects a creature of the chosen type in the following ways.<br>  The creature can''t willingly enter the cylinder by nonmagical means. If the creature tries to use teleportation or interplanar travel to do so, it must first succeed on a Charisma saving throw.<br>  The creature has disadvantage on attack rolls against targets within the cylinder.<br>  Targets within the cylinder can''t be charmed, frightened, or possessed by the creature. When you cast this spell, you can elect to cause its magic to operate in the reverse direction, preventing a creature of the specified type from leaving the cylinder and protecting targets outside it.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the duration increases by 1 hour for each slot level above 3rd.', 'holy water or powdered silver and iron worth at least 100 gp, which the spell consumes', '10 feet', 'VSMgp', NULL, '1 minute', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('89ef1823-92e0-4074-8000-5693456f357c', 'Meld into Stone', 'You step into a stone object or surface large enough to fully contain your body, melding yourself and all the equipment you carry with the stone for the duration. Using your movement, you step into the stone at a point you can touch. Nothing of your presence remains visible or otherwise detectable by nonmagical senses.<br> While merged with the stone, you can''t see what occurs outside it, and any Wisdom (Perception) checks you make to hear sounds outside it are made with disadvantage. You remain aware of the passage of time and can cast spells on yourself while merged in the stone. You can use your movement to leave the stone where you entered it, which ends the spell. You otherwise can''t move.<br> Minor physical damage to the stone doesn''t harm you, but its partial destruction or a change in its shape (to the extent that you no longer fit within it) expels you and deals 6d6 bludgeoning damage to you. The stone''s complete destruction (or transmutation into a different substance) expels you and deals 50 bludgeoning damage to you. If expelled, you fall prone in an unoccupied space closest to where you first entered.', '', 'Touch', 'VS', NULL, '1 action', true, '8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('248ca646-7f59-458d-8c9f-0cad5dfbfa64', 'Protection from Energy', 'For the duration, the willing creature you touch has resistance to one damage type of your choice: acid, cold, fire, lightning, or thunder.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('30560bf0-6653-4e86-89d0-a652ebfbdeae', 'Remove Curse', 'At your touch, all curses affecting one creature or object end. If the object is a cursed magic item, its curse remains, but the spell breaks its owner''s attunement to the object so it can be removed or discarded.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('dec87f78-1e30-4aaf-a3e8-f63a4486e418', 'Revivify', 'You touch a creature that has died within the last minute. That creature returns to life with 1 hit point. This spell can''t return to life a creature that has died of old age, nor can it restore any missing body parts.', 'diamonds worth 300 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('36c08c96-67fe-4973-ba1a-b6f33d2576cc', 'Spirit Guardians', 'You call forth spirits to protect you. They flit around you to a distance of 15 feet for the duration. If you are good or neutral, their spectral form appears angelic or fey (your choice). If you are evil, they appear fiendish.<br> When you cast this spell, you can designate any number of creatures you can see to be unaffected by it. An affected creature''s speed is halved in the area, and when the creature enters the area for the first time on a turn or starts its turn there, it must make a Wisdom saving throw. On a failed save, the creature takes 3d8 radiant damage (if you are good or neutral) or 3d8 necrotic damage (if you are evil). On a successful save, the creature takes half as much damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d8 for each slot level above 3rd.', 'a holy symbol', 'Self (15-foot-radius)', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('733e9d7e-0471-445c-be5b-1b02c85a57e5', 'Illusory Script', 'You write on parchment, paper, or some other suitable writing material and imbue it with a potent illusion that lasts for the duration.<br> To you and any creatures you designate when you cast the spell, the writing appears normal, written in your hand, and conveys whatever meaning you intended when you wrote the text. To all others, the writing appears as if it were written in an unknown or magical script that is unintelligible. Alternatively, you can cause the writing to appear to be an entirely different message, written in a different hand and language, though the language must be one you know.<br> Should the spell be dispelled, the original script and the illusion both disappear.<br> A creature with truesight can read the hidden message.', 'A lead-based ink worth at least 10 gp, Consumed', 'Touch', 'SMgp', NULL, '1 minute', true, '10 days', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('60e3a039-19c2-4c35-bbef-e20c94192980', 'Skywrite', 'You cause up to 10 words to form in a part of the sky you can see. The words appear to be made of cloud and remain in place for the spell''s duration. The words dissipate when the spell ends. A strong wind can disperse the clouds and end the spell early.', '', 'Sight', 'VS', NULL, '1 action', true, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f0399b24-cd8f-463a-b3e9-b47d60bbb28a', 'Silence', 'For the duration, no sound can be created within or pass through a 20-foot-radius sphere centered on a point you choose within range. Any creature or object entirely inside the sphere is immune to thunder damage, and creatures are deafened while entirely inside it. Casting a spell that includes a verbal component is impossible there.', '', '120 feet', 'VS', NULL, '1 action', true, 'up to 10 minutes', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5a794483-a888-4d3f-ba28-ad3c05e994e1', 'Banishment', 'You attempt to send one creature that you can see within range to another place of existence. The target must succeed on a Charisma saving throw or be banished.<br> If the target is native to the plane of existence you''re on, you banish the target to a harmless demiplane. While there, the target is incapacitated. The target remains there until the spell ends, at which point the target reappears in the space it left or in the nearest unoccupied space if that space is occupied.<br> If the target is native to a different plane of existence that the one you''re on, the target is banished with a faint popping noise, returning to its home plane. If the spell ends before 1 minute has passed, the target reappears in the space it left or in the nearest unoccupied space if that space is occupied. Otherwise, the target doesn''t return.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, you can target one additional creature for each slot level above 4th.', 'an item distasteful to the target', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('78d0d698-0aa0-43e4-8188-9ab1cf81b2e3', 'Control Water', 'Until the spell ends, you control any freestanding water inside an area you choose that is a cube up to 100 feet on a side. You can choose from any of the following effects when you cast this spell. As an action on your turn, you can repeat the same effect or choose a different one.<br> <b>Flood</b>: You cause the water level of all standing water in the area to rise by as much as 20 feet. If the area includes a shore, the flooding water spills over onto dry land.<br> If you choose an area in a large body of water, you instead create a 20-foot tall wave that travels from one side of the area to the other and then crashes down. Any Huge or smaller vehicles in the wave''s path are carried with it to the other side. Any Huge or smaller vehicles struck by the wave have a 25 percent chance of capsizing.<br> The water level remains elevated until the spell ends or you choose a different effect. If this effect produced a wave, the wave repeats on the start of your next turn while the flood effect lasts.<br> <b>Part Water</b>: You cause water in the area to move apart and create a trench. The trench extends across the spell''s area, and the separated water forms a wall to either side. The trench remains until the spell ends or you choose a different effect. The water then slowly fills in the trench over the course of the next round until the normal water level is restored.<br> <b>Redirect Flow</b>: You cause flowing water in the area to move in a direction you choose, even if the water has to flow over obstacles, up walls, or in other unlikely directions. The water in the area moves as you direct it, but once it moves beyond the spell''s area, it resumes its flow based on the terrain conditions. The water continues to move in the direction you chose until the spell ends or you choose a different effect.<br> <b>Whirlpool</b>: This effect requires a body of water at least 50 feet square and 25 feet deep. You cause a whirlpool to form in the center of the area. The whirlpool forms a vortex that is 5 feet wide at the base, up to 50 feet wide at the top, and 25 feet tall. Any creature or object in the water and within 25 feet of the vortex is pulled 10 feet toward it. A creature can swim away from the vortex by making a Strength (Athletics) check against your spell save DC.<br> When a creature enters the vortex for the first time on a turn or starts its turn there, it must make a Strength saving throw. On a failed save, the creature takes 2d8 bludgeoning damage and is caught in the vortex until the spell ends. On a successful save, the creature takes half damage, and isn''t caught in the vortex. A creature caught in the vortex can use its action to try to swim away from the vortex as described above, but has disadvantage on the Strength (Athletics) check to do so.<br> The first time each turn that an object enters the vortex, the object takes 2d8 bludgeoning damage, this damage occurs each round it remains in the vortex.', 'a drop of water and a pinch of dust', '300 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5048ca3c-b777-4d3d-b8cc-564601ac9d3f', 'Tasha''s Mind Whip', 'You psychically lash out at one creature you can see within range. The target must make an Intelligence saving throw. On a failed save, the target takes 3d6 psychic damage, and it can''t take a reaction until the end of its next turn. Moreover, on its next turn, it must choose whether it gets a move, an action, or a bonus action', '', '90 feet', 'V', NULL, '1 action', false, '1 round', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6ca58d9e-269b-45f5-ac76-63cd3c12cab7', 'Snilloc''s Snowball Swarm', 'A flurry of magic snowballs erupts from a point you choose within range. Each creature in a 5-foot-radius sphere centered on that point must make a Dexterity saving throw. A creature takes 3d6 cold damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d6 for each slot level above 2nd.', 'a piece of ice or a small white rock chip', '90 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('92dfc23c-cf99-4e1b-8661-9c6eef619345', 'Aura of Vitality', 'Healing energy radiates from you in an aura with a 30-foot radius. Until the spell ends, the aura moves with you, centered on you. You can use a bonus action to cause one creature in the aura (including you) to regain 2d6 hit points.', '', 'Self (30-foot radius)', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8dcc98f2-e947-4463-bc9f-e83554686c94', 'Guardian of Faith', 'A Large spectral guardian appears and hovers for the duration in an unoccupied space of your choice that you can see within range. The guardian occupies that space and is indistinct except for a gleaming sword and shield emblazoned with the symbol of your deity.<br> Any creature hostile to you that moves to a space within 10 feet of the guardian for the first time on a turn must succeed on a Dexterity saving throw. The creature takes 20 radiant damage on a failed save, or half as much damage on a successful one. The guardian vanishes when it has dealt a total of 60 damage.', '', '30 feet', 'V', NULL, '1 action', false, '8 hours', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('75edd9bf-5d68-424c-9b24-30ffb9625620', 'Mass Healing Word', 'As you call out words of restoration, up to six creatures of your choice that you can see within range regain hit points equal to 1d4 + your spellcasting ability modifier. This spell has no effect on undead or constructs.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the healing increases by 1d4 for each slot level above 3rd.', '', '60 feet', 'V', NULL, '1 bonus action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4f8744a2-8f4c-4e01-9d78-5454af851b2b', 'Stone Shape', 'You touch a stone object of Medium size or smaller or a section of stone no more than 5 feet in any dimension and form it into any shape that suits your purpose. So, for example, you could shape a large rock into a weapon, idol, or coffer, or make a small passage through a wall, as long as the wall is less than 5 feet thick. You could also shape a stone door or its frame to seal the door shut. The object you create can have up to two hinges and a latch, but finer mechanical detail isn''t possible.', 'soft clay, which must be worked into roughly the desired shape of the stone object', 'Touch', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('429a8b38-be11-43fe-9d8a-b21d7bc66a09', 'Commune', 'You contact your deity or a divine proxy and ask up to three questions that can be answered with a yes or no. You must ask your questions before the spell ends. You receive a correct answer for each question.<br> Divine beings aren''t necessarily omniscient, so you might receive unclear as an answer if a question pertains to information that lies beyond the deity''s knowledge. In a case where a one-word answer could be misleading or contrary to the deity''s interests, the DM might offer a short phrase as an answer instead.<br> If you cast the spell two or more times before finishing your next long rest, there is a cumulative 25 percent chance for each casting after the first that you get no answer. The DM makes this roll in secret.', 'incense and a vial of holy or unholy water', 'Self', 'VSM', NULL, '1 minute', true, '1 minute', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3dd37170-d135-4b18-9205-ee14ab377be5', 'Contagion', 'Your touch inflicts disease. Make a melee spell attack against a creature within your reach. On a hit, you afflict the creature with a disease of your choice from any of the ones described below.<br> At the end of each of the target''s turns, it must make a Constitution saving throw. After failing three of these saving throws, the disease''s effects last for the duration, and the creature stops making these saves. After succeeding on three of these saving throws, the creature recovers from the disease, and the spell ends.<br> Since this spell induces a natural disease in its target, any effect that removes a disease or otherwise ameliorates a disease''s effects apply to it.<br> <b>Blinding Sickness</b>: Pain grips the creature''s mind, and its eyes turn milky white. The creature has disadvantage on Wisdom checks and Wisdom saving throws and is blinded.<br> <b>Filth Fever</b>: A raging fever sweeps through the creature''s body. The creature has disadvantage on Strength checks, Strength saving throws, and attack rolls that use Strength.<br> <b>Flesh Rot</b>: The creature''s flesh decays. The creature has disadvantage on Charisma checks and vulnerability to all damage.<br> <b>Mindfire</b>: The creature''s mind becomes feverish. The creature has disadvantage on Intelligence checks and Intelligence saving throws, and the creature behaves as if under the effects of the confusion spell during combat.<br> <b>Seizure</b>: The creature is overcome with shaking. The creature has disadvantage on Dexterity checks, Dexterity saving throws, and attack rolls that use Dexterity.<br> <b>Slimy Doom</b>: The creature begins to bleed uncontrollably. The creature has disadvantage on Constitution checks and Constitution saving throws. In addition, whenever the creature takes damage, it is stunned until the end of its next turn.', '', 'Touch', 'VS', NULL, '1 action', false, '7 days', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2050f680-e600-4257-b468-8036e95a092b', 'Dispel Evil and Good', 'Shimmering energy surrounds and protects you from fey, undead, and creatures originating from beyond the Material Plane. For the duration, celestials, elementals, fey, fiends, and undead have disadvantage on attack rolls against you.<br> You can end the spell early by using either of the following special functions.<br> <b>Break Enchantment</b>: As your action, you touch a creature you can reach that is charmed, frightened, or possessed by a celestial, an elemental, a fey, a fiend, or an undead. The creature you touch is no longer charmed, frightened, or possessed by such creatures.<br> <b>Dismissal</b>: As your action, make a melee spell attack against a celestial, an elemental, a fey, a fiend, or an undead you can reach. On a hit, you attempt to drive the creature back to its home plane. The creature must succeed on a Charisma saving throw or be sent back to its home plane (if it isn''t there already). If they aren''t on their home plane, undead are sent to the Shadowfell, and fey are sent to the Feywild.', 'holy water or powdered silver and iron', 'Self', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('28676842-c27c-499e-932a-4c5646b44244', 'Bigby''s Hand', 'You create a Large hand of shimmering, translucent force in an unoccupied space that you can see within range. The hand lasts for the spell''s duration, and it moves at your command, mimicking the movements of your own hand.<br> The hand is an object that has AC 20 and hit points equal to your hit point maximum. If it drops to 0 hit points, the spell ends. It has a Strength of 26 (+8) and a Dexterity of 10 (+0). The hand doesn''t fill its space.<br> When you cast the spell and as a bonus action on your subsequent turns, you can move the hand up to 60 feet and then cause one of the following effects with it.<br> <b>Clenched Fist</b>. The hand strikes one creature or object within 5 feet of it. Make a melee spell attack for the hand using your game statistics. On a hit, the target takes 4d8 force damage.<br> <b>Forceful Hand</b>. The hand attempts to push a creature within 5 feet of it in a direction you choose. Make a check with the hand''s Strength contested by the Strength (Athletics) check of the target. If the target is Medium or smaller, you have advantage on the check. If you succeed, the hand pushes the target up to 5 feet plus a number of feet equal to five times your spellcasting ability modifier. The hand moves with the target to remain within 5 feet of it.<br> <b>Grasping Hand</b>. The hand attempts to grapple a Huge or smaller creature within 5 feet of it. You use the hand''s Strength score to resolve the grapple. If the target is Medium or smaller, you have advantage on the check. While the hand is grappling the target, you can use a bonus action to have the hand crush it. When you do so, the target takes bludgeoning damage equal to 2d6 + your spellcasting ability modifier.<br> <b>Interposing Hand</b>. The hand interposes itself between you and a creature you choose until you give the hand a different command. The hand moves to stay between you and the target, providing you with half cover against the target. The target can''t move through the hand''s space if its Strength score is less than or equal to the hand''s Strength score. If its Strength score is higher than the hand''s Strength score, the target can move toward you through the hand''s space, but that space is difficult terrain for the target.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage from the clenched fist option increases by 2d8 and the damage from the grasping hand increases by 2d6 for each slot level above 5th.', 'an eggshell and a snakeskin glove', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('74ff6492-6643-43c0-a5c3-79973c085794', 'Flame Strike', 'A vertical column of divine fire roars down from the heavens in a location you specify. Each creature in a 10-foot radius, 40-foot-high cylinder centered on a point within range must make a Dexterity saving throw. A creature takes 4d6 fire damage and 4d6 radiant damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the fire damage or the radiant damage (your choice) increases by 1d6 for each slot level above 5th.', 'pinch of sulfur', '60 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('058b5eeb-1bc9-4878-89a5-e0797ce892fa', 'Hallow', 'You touch a point and infuse an area around it with holy (or unholy) power. The area can have a radius up to 60 feet, and the spell fails if the radius includes an area already under the effect a <i>hallow</i> spell. The affected area is subject to the following effects.<br> First, celestials, elementals, fey, fiends, and undead can''t enter the area, nor can such creatures charm, frighten, or possess creatures within it. Any creature charmed, frightened, or possessed by such a creature is no longer charmed, frightened, or possessed upon entering the area. You can exclude one or more of those types of creatures from this effect.<br> Second, you can bind an extra effect to the area. Choose the effect from the following list, or choose an effect offered by the DM. Some of these effects apply to creatures in the area, you can designate whether the effect applies to all creatures, creatures that follow a specific deity or leader, or creatures of a specific sort, such as ores or trolls. When a creature that would be affected enters the spell''s area for the first time on a turn or starts its turn there, it can make a Charisma saving throw. On a success, the creature ignores the extra effect until it leaves the area.<br> <b>Courage</b>: Affected creatures can''t be frightened while in the area.<br> <b>Darkness</b>: Darkness fills the area. Normal light, as well as magical light created by spells of a lower level than the slot you used to cast this spell, can''t illuminate the area.<br> <b>Daylight</b>: Bright light fills the area. Magical darkness created by spells of a lower level than the slot you used to cast this spell can''t extinguish the light.<br> <b>Energy Protection</b>: Affected creatures in the area have resistance to one damage type of your choice, except for bludgeoning, piercing, or slashing.<br> <b>Energy Vulnerability</b>: Affected creatures in the area have vulnerability to one damage type of your choice, except for bludgeoning, piercing, or slashing.<br> <b>Everlasting Rest</b>: Dead bodies interred in the area can''t be turned into undead.<br> <b>Extradimensional Interference</b>: Affected creatures can''t move or travel using teleportation or by extradimensional or interplanar means.<br> <b>Fear</b>: Affected creatures are frightened while in the area.<br> <b>Silence</b>: No sound can emanate from within the area, and no sound can reach into it.<br> <b>Tongues</b>: Affected creatures can communicate with any other creature in the area, even if they don''t share a common language.', 'herbs, oils, and incense worth at least 1,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '24 hours', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7d5a3096-df71-4ad2-87cd-41fa68e73661', 'Blade Barrier', 'You create a vertical wall of whirling, razor-sharp blades made of magical energy. The wall appears within range and lasts for the duration. You can make a straight wall up to 100 feet long, 20 feet high, and 5 feet thick, or a ringed wall up to 60 feet in diameter, 20 feet high, and 5 feet thick. The wall provides three-quarters cover to creatures behind it, and its space is difficult terrain.<br> When a creature enters the wall''s area for the first time on a turn or starts its turn there, the creature must make a Dexterity saving throw. On a failed save, the creature takes 6d10 slashing damage. On a successful save, the creature takes half as much damage.', '', '90 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c5da424b-2a8b-4f54-becc-d122c7a75aa7', 'Psychic Scream', 'You unleash the power of your mind to blast the intellect of up to ten creatures of your choice that you can see within range. Creatures that have an Intelligence score of 2 or lower are unaffected.<br> Each target must mak e an Intelligence saving throw. On a failed save, a target takes 14d6 psychic damage and is stunned. On a successful save, a target takes half as much damage and isn''t stunned. If a target is killed by this damage, its head explodes, assuming it has one.<br> A stunned target can make an Intelligence saving throw at the end ofeach of its turns. On a successful save, the stunning effect ends.', '', '90 feet', 'S', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('86e026aa-b557-4b49-861b-168ade1a2d51', 'Create Undead', 'You can cast this spell only at night. Choose up to three corpses of Medium or Small humanoids within range. Each corpse becomes a ghoul under your control. (The DM has game statistics for these creatures.)<br> As a bonus action on each of your turns, you can mentally command any creature you animated with this spell if the creature is within 120 feet of you (if you control multiple creatures, you can command any or all of them at the same time, issuing the same command to each one). You decide what action the creature will take and where it will move during its next turn, or you can issue a general command, such as to guard a particular chamber or corridor. If you issue no commands, the creature only defends itself against hostile creatures. Once given an order, the creature continues to follow it until its task is complete.<br> The creature is under your control for 24 hours, after which it stops obeying any command you have given it. To maintain control of the creature for another 24 hours, you must cast this spell on the creature before the current 24-hour period ends. This use of the spell reasserts your control over up to three creatures you have animated with this spell, rather than animating new ones.<br> <b>At Higher Levels</b>: When you cast this spell using a 7th-level spell slot, you can animate or reassert control over four ghouls. When you cast this spell using an 8th-level spell slot, you can animate or reassert control over five ghouls or two ghasts or wights. When you cast this spell using a 9th-level spell slot, you can animate or reassert control over six ghouls, three ghasts or wights, or two mummies', 'one clay pot filled with grave dirt, one clay pot filled with brackish water, and one 150 gp black onyx stone for each corpse.', '10 feet', 'VSMgp', NULL, '1 minute', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('83a7fbc3-d440-4bc6-8c64-2d82b5b6d29d', 'Control Flames', 'You choose nonmagical flame that you can see within range and that fits within a 5-foot cube. You affect it in one of the following ways:<br>  You instantaneously expand the flame 5 feet in one direction, provided that wood or other fuel is present in the new location.<br>  You instantaneously extinguish the flames within the cube.<br>  You double or halve the area of bright light and dim light cast by the flame, change its color, or both . The change lasts for 1 hour.<br>  You cause simple shapes-such as the vague form of a creature, an inanimate object, or a location- to appear within the flames and animate as you like. The shapes last for 1 hour.<br> If you cast this spell multiple times, you can have up to three non-instantaneous effects created by it active at a time, and you can dismiss such an effect as an action.', '', '60 feet', 'S', NULL, '1 action', false, 'Instantaneous or 1 hour (see below)', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d8118e85-9d6a-4248-8995-890579b8a87e', 'Conjure Barrage', 'You throw a nonmagical weapon or fire a piece of nonmagical ammunition into the air to create a cone of identical weapons that shoot forward and then disappear. Each creature in a 60-foot cone must succeed on a Dexterity saving throw. A creature takes 3d8 damage on a failed save, or half as much damage on a successful one. The damage type is the same as that of the weapon or ammunition used as a component.', 'one piece of ammunition or a thrown weapon', 'Self (60-foot cone)', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('27b3ede0-5bf7-47cc-8f19-c3f740cd0e50', 'Forbiddance', 'You create a ward against magical travel that protects up to 40,000 square feet of floor space to a height of 30 feet above the floor. For the duration, creatures can''t teleport into the area or use portals, such as those created by the gate spell, to enter the area. The spell proofs the area against planar travel, and therefore prevents creatures from accessing the area by way of the Astral Plane, Ethereal Plane, Feywild, Shadowfell, or the <i>plane shift</i> spell.<br> In addition, the spell damages types of creatures that you choose when you cast it. Choose one or more of the following: celestials, elementals, fey, fiends, and undead. When a chosen creature enters the spell''s area for the first time on a turn or starts its turn there, the creature takes 5d10 radiant or necrotic damage (your choice when you cast this spell).<br> When you cast this spell, you can designate a password. A creature that speaks the password as it enters the area takes no damage from the spell.<br> The spell''s area can''t overlap with the area of another <i>forbiddance</i> spell. If you cast <i>forbiddance</i> every day for 30 days in the same location, the spell lasts until it is dispelled, and the material components are consumed on the last casting.', 'a sprinkling of holy water, rare incense, and powdered ruby worth at least 1,000 gp', 'Touch', 'VSMgp', NULL, '10 minutes', true, 'up to 1 minute', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a2b95015-d665-40c1-a363-14544aa707b5', 'Harm', 'You unleash a virulent disease on a creature that you can see within range. The target must make a Constitution saving throw. On a failed save, it takes 14d6 necrotic damage, or half as much damage on a successful save. The damage can''t reduce the target''s hit points below 1. If the target fails the saving throw, its hit point maximum is reduced for 1 hour by an amount equal to the necrotic damage it took. Any effect that removes a disease allows a creature''s hit point maximum to return to normal before that time passes.', '', '60 feet', 'VS', NULL, '1 action', false, '24 hours', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e8fb80b9-c7e1-414c-aa20-4627a018b199', 'Heal', 'Choose a creature that you can see within range. A surge of positive energy washes through the creature, causing it to regain 70 hit points. The spell also ends blindness, deafness, and any diseases affecting the target. This spell has no effect on constructs or undead.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the amount of healing increases by 10 for each slot level above 6th.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bfa087fd-2a59-4cb2-86e5-b94536aa52c0', 'Planar Ally', 'You beseech an otherworldly entity for aid. The being must be known to you - a god, a primordial, a demon prince, or some other being of cosmic power. That entity sends a celestial, an elemental, or a fiend loyal to it to aid you, making the creature appear in an unoccupied space within range. If you know a specific creature''s name, you can speak that name when you cast this spell to request that creature, though you might get a different creature anyway (DM''s choice).<br> When the creature appears, it is under no compulsion to behave in any particular way. You can ask the creature to perform a service in exchange for payment, but it isn''t obliged to do so. The requested task could range from simple (fly us across the chasm, or help us fight a battle) to complex (spy on our enemies, or protect us during our foray into the dungeon). You must be able to communicate with the creature to bargain for its services.<br> Payment can take a variety of forms. A celestial might require a sizable donation of gold or magic items to an allied temple, while a fiend might demand a living sacrifice or a gift of treasure. Some creatures might exchange their service for a quest undertaken by you.<br> As a rule of thumb, a task that can be measured in minutes requires a payment worth 100 gp per minute. A task measured in hours requires 1,000 gp per hour. And a task measured in days (up to 10 days) requires 10,000 gp per day. The DM can adjust these payments based on the circumstances under which you cast the spell. If the task is aligned with the creature''s ethos, the payment might be halved or even waived. Nonhazardous tasks typically require only half the suggested payment, while especially dangerous tasks might require a greater gift. Creatures rarely accept tasks that seem suicidal.<br> After the creature completes the task, or when the agreed-upon duration of service expires, the creature returns to its home plane after reporting back to you, if appropriate to the task and if possible. If you are unable to agree on a price for the creature''s service, the creature immediately returns to its home plane.<br> A creature enlisted to join your group counts as a member of it, receiving a full share of experience points awarded.', '', '60 feet', 'VS', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1cbb6fa9-19c6-49c8-9c08-777383374fd4', 'Friends', 'For the duration, you have advantage on all Charisma checks directed at one creature of your choice that isn''t hostile toward you. When the spell ends, the creature realizes that you used magic to influence its mood and becomes hostile toward you. A creature prone to violence might attack you. Another creature might seek retribution in other ways (at the DM''s discretion), depending on the nature of your interaction with it.', '', 'Self', 'SM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('60c11e88-3e1e-4152-88f9-f6d4f6341cf5', 'Frostbite', 'You cause numbing frost to form on one creature that you can see within range. The target must make a Constitution saving throw. On a failed save, the target takes 1d6 cold damage, and it has disadvantage on the next weapon attack roll it makes before the end of its next turn.<br> The spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('39683077-a64a-463f-a81b-36a699b85e76', 'Word of Recall', 'You and up to five willing creatures within 5 feet of you instantly teleport to a previously designated sanctuary. You and any creatures that teleport with you appear in the nearest unoccupied space to the spot you designated when you prepared your sanctuary (see below). If you cast this spell without first preparing a sanctuary, the spell has no effect.<br> You must designate a sanctuary by casting this spell within a location, such as a temple, dedicated to or strongly linked to your deity. If you attempt to cast the spell in this manner in an area that isn''t dedicated to your deity, the spell has no effect.', '', '5 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b48429c7-536d-4bb3-b82a-4694a1853617', 'Crusader''s Mantle', 'Holy power radiates from you in an aura with a 30-foot radius, awakening boldness in friendly creatures. Until the spell ends, the aura moves with you, centered on you. While in the aura, each non-hostile creature in the aura (including you) deals an extra 1d4 radiant damage when it hits with a weapon attack.', '', 'Self', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fa7b1e3d-6f21-4cdb-8486-414dc6156833', 'Silent Image', 'You create the image of an object, a creature, or some other visible phenomenon that is no larger than a 15-foot cube. The image appears at a spot within range and lasts for the duration. The image is purely visual, it isn''t accompanied by sound, smell, or other sensory effects.<br> You can use your action to cause the image to move to any spot within range. As the image changes location, you can alter its appearance so that its movements appear natural for the image. For example, if you create an image of a creature and move it, you can alter the image so that it appears to be walking.<br> Physical interaction with the image reveals it to be an illusion, because things can pass through it. A creature that uses its action to examine the image can determine that it is an illusion with a successful Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the creature can see through the image.', 'A bit of fleece', '60 feet', 'VSM', NULL, '1 action', false, 'up to 10 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d70d65b4-5d1c-407e-b98b-a505f5ff1270', 'Green-Flame Blade', 'You brandish the weapon used in the spell''s casting and make a melee attack with it against one creature within 5 feet of you. On a hit, the target suffers the weapon attack''s normal effects, and you can cause green fire to leap from the target to a different creature of your choice that you can see within 5 feet of it. The second creature takes fire damage equal to your spellcasting ability modifier.<br>At Higher Levels. At 5th level, the melee attack deals an extra 1d8 fire damage to the target on a hit, and the fire damage to the second creature increases to 1d8 + your spellcasting ability modifier. Both damage rolls increase by 1d8 at 11th level (2d8 and 2d8) and 17th level (3d8 and 3d8).', 'a melee weapon worth at least 1 sp', 'Self (5-foot radius)', 'VM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('01dbe1b1-c7b9-4aa2-8e72-42d9bab9151f', 'Gust', 'You seize the air and compel it to create one of the following effects at a point you can see within range:<br>  One Medium or smaller creature that you choose must succeed on a Strength saving throw or be pushed up to 5 feet away from you.<br>  You create a small blast of air capable of moving one object that is neither held nor carried and that weighs no more than 5 pounds. The object is pushed up to 10 feet away from you. It isn''t pushed with enough force to cause damage.<br>  You create a harm less sensory effect using air, such as causing leaves to rustle, wind to slam shutters closed, or your clothing to ripple in a breeze.', '', '30 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('263bf554-00b9-4025-8aa4-eae7842b36c8', 'Infestation', 'You cause a cloud of mites, fleas, and other parasites to appear momentarily on one creature you can see within range. The target must succeed on a Constitution saving throw, or it takes 1d6 poison damage and moves 5 feet in a random direction if it can move and its speed is at least 5 feet. Roll a d4 for the direction: 1, north: 2, south: 3, east: or 4, west. This movement doesn''t provoke opportunity attacks, and if the direction rolled is blocked, the target doesn''t move.<br> The spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', 'a living flea', '30 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2a787414-6622-4851-a9a7-50b0cbe2d1de', 'Lightning Lure', 'You create a lash of lightning energy that strikes at one creature of your choice that you can see within 15 feet of you. The target must succeed on a Strength saving throw or be pulled up to 10 feet in a straight line toward you and then take 1d8 lightning damage if it is within 5 feet of you.<br><b>At Higher Levels</b>: This spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', 'Self (15-foot radius)', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f8d82e8a-8681-4fcc-b7da-0bbe37ccc6cb', 'Magic Stone', 'You touch one to three pebbles and imbue them with magic. You or someone else can make a ranged spell attack with one of the pebbles by throwing it or hurling it with a sling. lf thrown, a pebble has a range of 60 feet. If someone else attacks with a pebble, that attacker adds your spellcasting ability modifier, not the attacker''s, to the attack roll. On a hit, the target takes bludgeoning damage equal to 1d6 +your spellcasting ability modifier. Whether the attack hits or misses, the spell then ends on the stone.<br> If you cast this spell again, the spell ends on any pebbles still affected by your previous casting.', '', 'Touch', 'VS', NULL, '1 bonus action', false, '1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bb68f181-ef76-4d69-be92-f0c0981e251c', 'Divine Word', 'You utter a divine word, imbued with the power that shaped the world at the dawn of creation. Choose any number of creatures you can see within range. Each creature that can hear you must make a Charisma saving throw. On a failed save, a creature suffers an effect based on its current hit points.<br>  50 hit points or fewer: deafened for 1 minute<br>  40 hit points or fewer: deafened and blinded for 10 minutes<br>  30 hit points or fewer: blinded, deafened, and stunned for 1 hour<br>  20 hit points or fewer: killed instantly<br> Regardless of its current hit points, a celestial, an elemental, a fey, or a fiend that fails its save is forced back to its plane of origin (if it isn''t there already) and can''t return to your current plane for 24 hours by any means short of a <i>wish</i> spell.', '', '30 feet', 'V', NULL, '1 bonus action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f9e2062e-b27a-426d-945b-2ef63a597714', 'Leomund''s Tiny Hut', 'A 10-foot-radius immobile dome of force springs into existence around and above you and remains stationary for the duration. The spell ends if you leave its area.<br> 9 creatures of Medium size or smaller can fit inside the dome with you. The spell fails if its area includes a larger creature or more than 9 creatures. Creatures and objects within the dome when you cast this spell can move through it freely. All other creatures and objects are barred from passing through it. Spells and other magical effects can''t extend through the dome or be cast through it. The atmosphere inside the space is comfortable and dry, regardless of the weather outside.<br> Until the spell ends, you can command the interior to become dimly lit or dark. The dome is opaque from the outside, of any color you choose, but it is transparent from the inside.', 'A small crystal bead', 'Self (10-foot-radius hemisphere)', 'VSM', NULL, '1 minute', true, '8 hours', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2a8c1c5a-6887-4265-80b8-4d98766a4c3f', 'Fire Storm', 'A storm made up of sheets of roaring flame appears in a location you choose within range. The area of the storm consists of up to ten 10-foot cubes, which you can arrange as you wish. Each cube must have at least one face adjacent to the face of another cube. Each creature in the area must make Dexterity saving throw. It takes 7d10 fire damage on a failed save, or half as much damage on a successful one.<br> The fire damages objects in the area and ignites flammable objects that aren''t being worn or carried. If you choose, plant life in the area is unaffected by this spell.', '', '150 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('809a2f0d-1eeb-4a06-9477-17db2ebc00c1', 'Mind Sliver', 'You drive a disorienting spike of psychic energy into the mind of one creature you can see within range. The target must succeed on an Intelligence saving throw or take 1d6 psychic damage and subtract 1d4 from the next saving throw it makes before the end of your next turn.<br><b>At Higher Levels</b>: This spell''s damage increases by 1d6 when you reach certain levels: 5th level (2d6), 11th level (3d6), and 17th level (4d6).', '', '60 feet', 'V', NULL, '1 action', false, '1 round', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('43e7d9e1-f9f8-46b9-ba34-784bfd282b14', 'Spare the Dying', 'You touch a living creature that has 0 hit points. The creature becomes stable. This spell has no effect on undead or constructs.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('af005d54-dfbd-4885-87a7-d8e27bb7d833', 'Plane Shift', 'You and up to eight willing creatures who link hands in a circle are transported to a different plane of existence. You can specify a target destination in general terms, such as the City of Brass on the Elemental Plane of Fire or the palace of Dispater on the second level of the Nine Hells, and you appear in or near that destination. If you are trying to reach the City of Brass, for example, you might arrive in its Street of Steel, before its Gate of Ashes, or looking at the city from across the Sea of Fire, at the DM''s discretion.<br> Alternatively, if you know the sigil sequence of a teleportation circle on another plane of existence, this spell can take you to that circle. If the teleportation circle is too small to hold all the creatures you transported, they appear in the closest unoccupied spaces next to the circle.<br> You can use this spell to banish an unwilling creature to another plane. Choose a creature within your reach and make a melee spell attack against it. On a hit, the creature must make a Charisma saving throw. If the creature fails the save, it is transported to a random location on the plane of existence you specify. A creature so transported must find its own way back to your current plane of existence.', 'a forked, metal rod worth at least 250 gp, attuned to a particular plane of existence', 'Touch', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7fb8103c-8675-4e19-b476-837f44b003b5', 'Mold Earth', 'You choose a portion of dirt or stone that you can see within range and that fits within a 5-foot cube. You manipulate it in one of the following ways:<br>  If you target an area of loose earth, you can instantaneously excavate it, move it along the ground, and deposit it up to 5 feet away. This movement doesn''t involve enough force to cause damage.<br>  You cause shapes, colors, or both to appear on the dirt or stone, spelling out words, creating images, or shaping patterns. The changes last for 1 hour.<br>  If the dirt or stone you target is on the ground, you cause it to become difficult terrain. Alternatively, you can cause the ground to become normal terrain if it is already difficult terrain . This change lasts for 1 hour.<br> If you cast this spell multiple times, you can have no more than two of its non-instantaneous effects active at a time, and you can dismiss such an effect as an action.', '', '30 feet', 'S', NULL, '1 action', false, 'Instantaneous or 1 hour (see below)', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6b165c6c-82f9-4e13-9e30-5129ac6e9015', 'Primal Savagery', 'You channel primal magic to cause your teeth or fingernails to sharpen, ready to deliver a corrosive attack. Make a melee spell attack against one creature within 5 feet of you. On a hit, the target takes 1d10 acid damage. After you make the attack, your teeth or fingernails return to normal.<br> The spell''s damage increases by 1d10 when you reach 5th level (2d10), 11th level (3d10), a nd 17th level (4d10).', '', 'Self', 'S', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fe80424c-0d40-46c4-b598-0bd2dcd734b1', 'Antimagic Field', 'A 10-foot-radius invisible sphere of antimagic surrounds you. This area is divorced from the magical energy that suffuses the multiverse. Within the sphere, spells can''t be cast, summoned creatures disappear, and even magic items become mundane. Until the spell ends, the sphere moves with you, centered on you.<br> Spells and other magical effects, except those created by an artifact or a deity, are suppressed in the sphere and can''t protrude into it. A slot expended to cast a suppressed spell is consumed. While an effect is suppressed, it doesn''t function, but the time it spends suppressed counts against its duration.<br> <b>Targeted Effects</b>: Spells and other magical effects, such as <i>magic missile</i> and <i>charm person</i>, that target a creature or an object in the sphere have no effect on that target.<br> <b>Areas of Magic</b>: The area of another spell or magical effect, such as <i>fireball</i>, can''t extend into the sphere. If the sphere overlaps an area of magic, the part of the area that is covered by the sphere is suppressed. For example, the flames created by a <i>wall of fire</i> are suppressed within the sphere, creating a gap in the wall if the overlap is large enough.<br> <b>Spells</b>: Any active spell or other magical effect on a creature or an object in the sphere is suppressed while the creature or object is in it.<br> <b>Magic Items</b>: The properties and powers of magic items are suppressed in the sphere. For example, a <i>+1 long sword</i> in the sphere functions as a nonmagical long sword. A magic weapon''s properties and powers are suppressed if it is used against a target in the sphere or wielded by an attacker in the sphere. If a magic weapon or piece of magic ammunition fully leaves the sphere (For example, if you fire a magic arrow or throw a magic spear at a target outside the sphere), the magic of the item ceases to be suppressed as soon as it exits.<br> <b>Magical Travel</b>: Teleportation and planar travel fail to work in the sphere, whether the sphere is the destination or the departure point for such magical travel. A portal to another location, world, or plane of existence, as well as an opening to an extradimensional space such as that created by the <i>rope trick</i> spells, temporarily closes while in the sphere.<br> <b>Creatures and Objects</b>: A creature or object summoned or created by magic temporarily winks out of existence in the sphere. Such a creature instantly reappears once the space the creature occupied is no longer within the sphere.<br> <b>Dispel Magic</b>: Spells and magical effects such as <i>dispel magic</i> have no effect on the sphere. Likewise, the spheres created by different <i>antimagic field</i> spells don''t falseify each other.', 'a pinch of powdered iron or iron filings', 'Self (10-foot-radius sphere)', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ea081df7-b502-47f4-a7e8-3dd3bba6020b', 'Sending', 'You send a short message of twenty-five words or less to a creature with you are familiar. The creature hears the message in its mind, recognizes you as the sender if it knows you, and can answer in a like manner immediately. The spell enables creatures with Intelligence scores of at least 1 to understand the meaning of your message.<br> You can send the message across any distance and even to other planes of existence, but if the target is on a different plane than you, there is a 5 percent chance that the message doesn''t arrive.', 'A short piece of fine copper wire', 'Unlimited', 'VSM', NULL, '1 action', false, '1 round', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2802c73f-151d-4a2b-955d-0b996eb2cf09', 'Jump', 'You touch a creature. The creature''s jump distance is tripled until the spell ends.', 'a grasshopper''s hind leg', 'Touch', 'VSM', NULL, '1 action', false, '1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('85f1699d-3c8f-457b-8c01-a76d91991f9b', 'Control Weather', 'You take control of the weather within 5 miles of you for the duration. You must be outdoors to cast this spell. Moving to a place where you don''t have a clear path to the sky ends the spell early.<br> When you cast the spell, you change the current weather conditions, which are determined by the DM based on the climate and season.<br> You can change precipitation, temperature, and wind. It takes 1d4 x 10 minutes for the new conditions to take effect. Once they do so, you can change the conditions again. When the spell ends, the weather gradually returns to normal. When you change the weather conditions, find a current condition on the following tables and change its stage by one, up or down. When changing the wind, you can change its direction.<br> <b>Precipitation</b>: Stage 1 - Clear Stage 2 - Light clouds Stage 3 - Overcast or ground fog Stage 4 - Rain, hail or snow Stage 5 - Torrential rain, driving hail or blizzard<br> <b>Temperature</b>: Stage 1 - Unbearable heat Stage 2 - Hot Stage 3 - Warm Stage 4 - Cool Stage 5 - Cold Stage 6 - Arctic cold<br> <b>Wind</b>: Stage 1 - Calm Stage 2 - Moderate wind Stage 3 - Strong wind Stage 4 - Gale Stage 5 - Storm', 'burning incense and bits of earth and wood mixed in water', 'Self (5-mile radius)', 'VSM', NULL, '10 minutes', false, 'up to 8 hours', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ef4a0728-afdf-46d7-8f80-f7bcdc188cc9', 'Shape Water', 'You choose an area of water that you can see within range and that fits within a 5-foot cube. You manipulate it in one of the following ways:<br>  You instantaneously move or otherwise change the flow of the water as you direct, up to 5 feet in any direction. This movement doesn''t have enough force to cause damage.<br>  You cause the water to form into simple shapes and animate at your direction. This change lasts for 1 hour.<br>  You change the water''s color or opacity. The water must be changed in the same way throughout. This change lasts for 1 hour.<br>  You freeze the water, provided that there are no creatures in it. The water unfreezes in 1 hour.<br> If you cast this spell multiple times,you can have no more than two of its non-instantaneous effects active at a time, and you can dismiss such an effect as an action.', '', '30 feet', 'S', NULL, '1 action', false, 'Instantaneous or 1 hour (see below)', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('39cdaa8e-0e48-4d64-8b41-b898e7ae16d4', 'Thorn Whip', 'You create a long, vine-like whip covered in thorns that lashes out at your command toward a creature in range. Make a melee spell attack against the target. If the attack hits, the creature takes 1d6 piercing damage, and if the creature is Large or smaller, you pull the creature up to 10 feet closer to you.<br> This spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', 'the stem of a plant with thorns', '30 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('020d4aa7-0046-4854-961e-712031e4ca3b', 'Toll the Dead', 'You point at one creature you can see within range, and the sound of a dolorous bell fills the air around it for a moment. The target must succeed on a Wisdom saving throw or take ld8 necrotic damage. If the target is missing any of its hit points, it instead takes 1d12 necrotic damage.<br> The spell''s damage increases by one die when you reach 5th level (2d8 or 2d12), 11th level (3d8 or 3d12), and 17th level (4d8 or 4d12).', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6bf3aaaa-e55e-45ae-9e7c-27b3de64aa04', 'Word of Radiance', 'You utter a divine word, and burning radiance erupts from you. Each creature of your choice that you can see within range must succeed on a Constitution saving throw or take 1d6 radiant damage.<br> The spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', 'a holy symbol', '5 feet', 'VM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('812fc3e7-4eb1-453e-bd2c-a5bfc683c198', 'Absorb Elements', 'The spell captures some of the incoming energy, lessening its effect on you and storing it for your next melee attack. You have resistance to the triggering damage type until the start of your next turn. Also, the first time you hit with a melee attack on your next turn, the target takes an extra 1d6 damage of the triggering type, and the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the extra damage increases by 1d6 for each slot level above 1st.', '', 'Self', 'S', NULL, '1 reaction', false, '1 round', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fb1cf92b-d4a8-4418-9735-2c733a287a05', 'Earthquake', 'You create a seismic disturbance at a point on the ground that you can see within range. For the duration, an intense tremor rips through the ground in a 100-foot- radius circle centered on that point and shakes creatures and structures in contact with the ground in that area.<br> The ground in the area becomes difficult terrain. Each creature on the ground that is concentrating must make a Constitution saving throw. On a failed save, the creature''s concentration is broken.<br> When you cast this spell and at the end of each turn you spend concentrating on it, each creature on the ground in the area must make a Dexterity saving throw. On a failed save, the creature is knocked prone.<br> This spell can have additional effects depending on the terrain in the area, as determined by the DM.<br> <b>Fissures</b>: Fissures open throughout the spell''s area at the start of your next turn after you cast the spell. A total of 1d6 such fissures open in locations chosen by the DM. Each is 1d10 x 10 feet deep, 10 feet wide, and extends from one edge of the spell''s area to the opposite side. A creature standing on a spot where a fissure opens must succeed on a Dexterity saving throw or fall in. A creature that successfully saves moves with the fissure''s edge as it opens.<br> A fissure that opens beneath a structure causes it to automatically collapse (see below).<br> <b>Structures</b>: The tremor deals 50 bludgeoning damage to any structure in contact with the ground in the area when you cast the spell and at the start of each of your turns until the spell ends. If a structure drops to 0 hit points, it collapses and potentially damages nearby creatures. A creature within half the distance of a structure''s height must make a Dexterity saving throw. On a failed save, the creature takes 5d6 bludgeoning damage, is knocked prone, and is buried in the rubble, requiring a DC 20 Strength (Athletics) check as an action to escape. The DM can adjust the DC higher or lower, depending on the nature of the rubble. On a successful save, the creature takes half as much damage and doesn''t fall prone or become buried.', 'a pinch of dirt, a piece of rock, and a lump of clay', '500 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bae94a9e-d7bf-4f8a-bb2b-5dd513204e49', 'Holy Aura', 'Divine light washes out from you and coalesces in a soft radiance in a 30-foot radius around you. Creatures of your choice in that radius when you cast this spell shed dim light in a 5-foot radius and have advantage on all saving throws, and other creatures have disadvantage on attack rolls against them until the spell ends. In addition, when a fiend or an undead hits an affected creature with a melee attack, the aura flashes with brilliant light. The attacker must succeed on a Constitution saving throw or be blinded until the spell ends.', 'a tiny reliquary worth at least 1,000 gp containing a sacred relic, such as a scrap of cloth from a saint''s robe or a piece of parchment from a religious text', 'Self', 'VSMgp', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8b8ff409-257e-47ce-98d3-347573f902c9', 'Astral Projection', 'You and up to eight willing creatures within range project your astral bodies into the Astral Plane (the spell fails and the casting is wasted if you are already on that plane). The material body you leave behind is unconscious and in a state of suspended animation, it doesn''t need food or air and doesn''t age.<br> Your astral body resembles your mortal form in almost every way, replicating your game statistics and possessions. The principal difference is the addition of a silvery cord that extends from between your shoulder blades and trails behind you, fading to invisibility after 1 foot. This cord is your tether to your material body. As long as the tether remains intact, you can find your way home. If the cord is cut-something that can happen only when an effect specifically states that it does-your soul and body are separated, killing you instantly.<br> Your astral form can freely travel through the Astral Plane and can pass through portals there leading to any other plane. If you enter a new plane or return to the plane you were on when casting this spell, your body and possessions are transported along the silver cord, allowing you to re-enter your body as you enter the new plane. Your astral form is a separate incarnation. Any damage or other effects that apply to it have no effect on your physical body, nor do they persist when you return to it.<br> The spell ends for you and your companions when you use your action to dismiss it. When the spell ends, the affected creature returns to its physical body, and it awakens. The spell might also end early for you or one of your companions. A successful <i>dispel magic</i> spell used against an astral or physical body ends the spell for that creature. If a creature''s original body or its astral form drops to 0 hit points, the spell ends for that creature. If the spell ends and the silver cord is intact, the cord pulls the creature''s astral form back to its body, ending its state of suspended animation.<br> If you are returned to your body prematurely, your companions remain in their astral forms and must find their own way back to their bodies, usually by dropping to 0 hit points.', 'for each creature you affect with this spell, you must provide one jacinth worth at least 1,000 gp and one ornately carved bar of silver worth at least 100 gp, all of which the spell consumes', '10 feet', 'VSMgp', NULL, '1 hour', false, 'Special', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1b559dc4-a370-4d8d-9b89-7fdd403e2e79', 'Armor of Agathys', 'A protective magical force surrounds you, manifesting as a spectral frost that covers you and your gear. You gain 5 temporary hit points for the duration. If a creature hits you with a melee attack while you have these hit points, the creature takes 5 cold damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, both the temporary hit points and the cold damage increase by 5 for each slot level above 1st.', 'a cup of water', 'Self', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('45c4cf1d-01bf-48a4-b189-e274d52812bb', 'Arms of Hadar', 'You invoke the power of Hadar, the Dark Hunger. Tendrils of dark energy erupt from you and batter all creatures within 10 feet of you. Each creature in that area must make a Strength saving throw. On a failed save, a target takes 2d6 necrotic damage and can''t take reactions until its next turn. On a successful save, the creature takes half damage, but suffers no other effect.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', 'Self (10-foot radius)', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2da9ad54-6cee-45d9-ad81-6fe2004bd054', 'Beast Bond', 'You establish a telepathic link with one beast you touch that is friendly to you or charmed by you. The spell fails if the beast''s Intelligence is 4 or higher. Until the spell ends, the link is active while you and the beast are within line of sight of each other. Through the link, the beast can understand your telepathic messages to it, and it can telepathically communicate simple emotions and concepts back to you. While the link is active, the beast gains advantage on attack rolls against any creature within 5 feet of you that you can see.', 'a bit of fur wrapped in a cloth', 'Touch', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('df4968a4-ae43-433c-b225-4c4f7385f859', 'Catapult', 'Choose one object weighing 1 to 5 pounds within range that isn''t being worn or carried. The object flies in a straight line up to 90 feet in a direction you choose before falling to the ground, stopping early if it impacts against a solid surface. If the object would strike a creature, that creature must make a Dexterity saving throw. On a failed save, the object strikes the target and stops moving. When the object strikes something, the object and what it strikes each take 3d8 bludgeoning damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the maximum weight of objects that you can target with this spell increases by 5 pounds, and the damage increases by 1d8, for each slot level above 1st.', '', '60 feet', 'S', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('27d454a6-a2b3-41fd-a30a-b7e80af40330', 'Cause Fear', 'You awaken the sense of mortality in one creature you can see within range. A construct or an undead is immune to this effect. The target must succeed on a Wisdom saving throw or become frightened of you until the spell ends. The frightened target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target one additional creature for each slot level above 1st. The creatures must be within 30 feet of each other when you target them.', '', '60 feet', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9786eb24-cfcc-4b39-a04e-93c8e40ec3f4', 'Ceremony', 'You perform a special religious ceremony that is infused with magic. When you cast the spell, choose one of the following rites, the target of which must be within 10 feet of you throughout the casting.<br> <b>Atonement</b>: You touch one willing creature whose alignment has changed, and you make a DC 20 Wisdom (Insight) check. On a successful check, you restore the target to its original alignment.<br> <b>Bless Water</b>: You touch one vial of water and cause it to become holy water.<br> <b>Coming of Age</b>: You touch one humanoid who is a young adult. For the next 24 hours, whenever the target makes an ability check, it can roll a d4 and add the number rolled to the ability check. A creature can benefit from this rite only once.<br> <b>Dedication</b>: You touch one humanoid who wishes to be dedicated to your god''s service. For the next 24 hours, whenever the target makes a saving throw, it can roll a d4 and add the number rolled to the save. A creature can benefit from this rite only once.<br> <b>Funeral Rite</b>: You touch one corpse, and for the next 7 days, the target can''t become undead by any means short of a <i>wish</i> spell.<br> <b>Wedding</b>: You touch adult humanoids willing to be bonded together in marriage. For the next 7 days, each target gains a +2 bonus to AC while they are within 30 feet of each other. A creature can benefit from this rite again only if widowed.', '25 gp worth of powdered silver, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 hour', true, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('176db229-2555-4ef1-8278-26ca6b6566f5', 'Water Breathing', 'This spell grants up to ten willing creatures you can see within range the ability to breathe underwater until the spell ends. Affected creatures also retain their normal mode of respiration.', 'a short reed or piece of straw', '30 feet', 'VSM', NULL, '1 action', true, '24 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5ee0739c-3245-4859-8c2a-b200a8d6cba4', 'Chaos Bolt', 'You hurl an undulating, warbling mass of chaotic energy at one creature in range. Make a ranged spell attack against the target. On a hit, the target takes 2d8 + 1d6 damage. Choose one of the d8s. The number rolled on that die determines the attack''s damage type. 1 Acid. 2 Cold. 3 Fire. 4 Force. 5 Lightning. 6 Poison. 7 Psychic. 8 Thunder.<br> If you roll the same number on both d8s, the chaotic energy leaps from the target to a different creature of your choice within 30 feet of it. Make a new attack roll against the new target, and make a new damage roll, which could cause the chaotic energy to leap again.<br> A creature can be targeted only once by each casting of this spell.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, each target takes 1d6 extra damage of the type rolled for each slot level above 1st.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7c281a33-f4c6-4aee-8a7d-c350b1f35e64', 'Gate', 'You conjure a portal linking an unoccupied space you can see within range to a precise location on a different plane of existence. The portal is a circular opening, which you can make 5 to 20 feet in diameter. You can orient the portal in any direction you choose. The portal lasts for the duration.<br> The portal has a front and a back on each plane where it appears. Travel through the portal is possible only by moving through its front. Anything that does so is instantly transported to the other plane, appearing in the unoccupied space nearest to the portal.<br> Deities and other planar rulers can prevent portals created by this spell from opening in their presence or anywhere within their domains.<br> When you cast this spell, you can speak the name of a specific creature (a pseudonym, title, or nickname doesn''t work). If that creature is on a plane other than the one you are on, the portal opens in the named creature''s immediate vicinity and draws the creature through it to the nearest unoccupied space on your side of the portal. You gain no special power over the creature, and it is free to act as the DM deems appropriate. It might leave, attack you, or help you.', 'a diamond worth at least 5,000 gp', '60 feet', 'VSMgp', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6c8f7c6f-f525-460b-85da-237e2384b046', 'Chromatic Orb', 'You hurl a 4-inch-diameter sphere of energy at a creature that you can see within range. You choose acid, cold, fire, lightning, poison, or thunder for the type of orb you create, and then make a ranged spell attack against the target. If the attack hits, the creature takes 3d8 damage of the type you chose.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d8 for each slot level above 1st.', 'a diamond worth at least 50 gp', '90 feet', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0e99c5e0-33c2-40dc-8dab-9f0765320115', 'Mass Heal', 'A flood of healing energy flows from you into injured creatures around you. You restore up to 700 hit points, divided as you choose among any number of creatures that you can see within range. Creatures healed by this spell are also cured of all diseases and any effect making them blinded or deafened. This spell has no effect on undead or constructs.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8ce7b6ed-6799-475d-8e10-e3ad33502aea', 'True Resurrection', 'You touch a creature that has been dead for no longer than 200 years and that died for any reason except old age. If the creature''s soul is free and willing, the creature is restored to life with all its hit points.<br> This spell closes all wounds, neutralizes any poison, cures all diseases, and lifts any curses affecting the creature when it died. The spell replaces damaged or missing organs or limbs.<br> The spell can even provide a new body if the original no longer exists, in which case you must speak the creature''s name. The creature then appears in an unoccupied space you choose within 10 feet of you.', 'a sprinkle of holy water and diamonds worth at least 25,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 hour', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2ab5f749-a4b0-460b-a49a-059f2ff8b573', 'Druidcraft', 'Whispering to the spirits of nature, you create one of the following effects within range.<br>  You create a tiny, harmless sensory effect that predicts what the weather will be at your location for the next 24 hours. The effect might manifest as a golden orb for clear skies, a cloud for rain, falling snowflakes for snow, and so on. This effect persists for 1 round.<br>  You instantly make a flower blossom, a seed pod open, or a leaf bud bloom.<br>  You create an instantaneous, harmless sensory effect, such as falling leaves, a puff of wind, the sound of a small animal, or the faint odor of skunk. The effect must fit in a 5-foot cube.<br>  You instantly light or snuff out a candle, a torch, or a small campfire.', '', '30 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2088615e-e202-455b-83a9-4d6df8788124', 'Compelled Duel', 'You attempt to compel a creature into a duel. One creature that you can see within range must make a Wisdom saving throw. On a failed save, the creature is drawn to you, compelled by your divine demand. For the duration, it has disadvantage on attack rolls against creatures other than you, and must make a Wisdom saving throw each time it attempts to move to a space that is more than 30 feet away from you, if it succeeds on this saving throw, this spell doesn''t restrict the target''s movement for that turn.<br> The spell ends if you attack any other creature, if you cast a spell that targets a hostile creature other than the target, if a creature friendly to you damages the target or casts a harmful spell on it, or if you end your turn more than 30 feet away from the target.', '', '30 feet', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('777d5776-9c8a-4585-b868-56eb895e05ad', 'Poison Spray', 'You extend your hand toward a creature you can see within range and project a puff of noxious gas from your palm. The creature must succeed on a Constitution saving throw or take 1d12 poison damage.<br> This spell''s damage increases by 1d12 when you reach 5th level (2d12), 11th level (3d12), and 17 level (4d12).', '', '10 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ab2379ff-95ad-4964-80ff-ba144cfa4bb2', 'Entangle', 'Grasping weeds and vines sprout from the ground in a 20-foot square starting from a point within range. For the duration, these plants turn the ground in the area into difficult terrain.<br> A creature in the area when you cast the spell must succeed on a Strength saving throw or be restrained by the entangling plants until the spell ends. A creature restrained by the plants can use its action to make a Strength check against your spell save DC. On a success, it frees itself.<br> When the spell ends, the conjured plants wilt away.', '', '90 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ae3db65b-e9a3-4ba0-8d27-7e840fd7ee96', 'Sleep', 'This spell sends creatures into a magical slumber. Roll 5d8, the total is how many hit points of creatures this spell can affect. Creatures within 20 feet of a point you choose within range are affected in ascending order of their current hit points (ignoring unconscious creatures).<br> Starting with the creature that has the lowest current hit points, each creature affected by this spell falls unconscious until the spell ends, the sleeper takes damage, or someone uses an action to shake or slap the sleeper awake. Subtract each creature''s hit points from the total before moving on to the creature with the next lowest hit points. A creature''s hit points must be equal to or less than the remaining total for that creature to be affected.<br> Undead and creatures immune to being charmed aren''t affected by this spell.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, roll an additional 2d8 for each slot level above 1st.', 'a pinch of find sand, rose petals, or a cricket', '90 feet', 'VSM', NULL, '1 action', false, '1 minute', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4c6cc356-c6c9-484d-a3be-1763d9f0561f', 'Speak with Animals', 'You gain the ability to comprehend and verbally communicate with beasts for the duration. The knowledge and awareness of many beasts is limited by their intelligence, but at minimum, beasts can give you information about nearby locations and monsters, including whatever they can perceive or have perceived within the past day. You might be able to persuade a beast to perform a small favor for you, at the DM''s discretion.', '', 'Self', 'VS', NULL, '1 action', true, '10 minutes', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cb4a6186-1660-4f5b-b3d9-c3bed44d88b6', 'Fog Cloud', 'You create a 20-foot-radius sphere of fog centered on a point within range. The sphere spreads around corners, and its area is heavily obscured. It lasts for the duration or until a wind of moderate or greater speed (at least 10 miles per hour) disperses it.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the radius of the fog increases by 20 feet for each slot level above 1st.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7b1a660e-2fae-4157-89a8-e412e5ff6406', 'Goodberry', 'Up to ten berries appear in your hand and are infused with magic for the duration. A creature can use its action to eat one berry. Eating a berry restores 1 hit point, and the berry provides enough nourishment to sustain a creature for one day.<br> The berries lose their potency if they have not been consumed within 24 hours of the casting of this spell.', 'a sprig of mistletoe', 'Touch', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6905f90f-7079-4488-994f-fa449296109f', 'Dissonant Whispers', 'You whisper a discordant melody that only one creature of your choice within range can hear, wracking it with terrible pain. The target must make a Wisdom saving throw. On a failed save, it takes 3d6 psychic damage and must immediately use its reaction, if available, to move as far as its speed allows away from you. The creature doesn''t move into obviously dangerous ground, such as a fire or a pit. On a successful save, the target takes half as much damage and doesn''t have to move away. A deafened creature automatically succeeds on the save.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7a95b610-0940-4bc3-bea2-13c922e0d72c', 'Darkvision', 'You touch a willing creature to grant it the ability to see in the dark. For the duration, that creature has darkvision out to a range of 60 feet.', 'either a pinch of dried carrot or an agate', 'Touch', 'VSM', NULL, '1 action', false, '8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fe2a3d35-ee76-4cb6-a7df-cb42935952de', 'Earth Tremor', 'You cause a tremor in the ground within range. Each creature other than you in that area must make a Dexterity saving throw. On a failed save, a creature takes 1d6 bludgeoning damage and is knocked prone. If the ground in that area is loose earth or stone, it becomes difficult terrain until cleared, with each 5-foot-diameter portion requiring at least 1 minute to clear by hand.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', '10 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('47b85904-db83-4053-adc6-2bcf4c876c8d', 'Ensnaring Strike', 'The next time you hit a creature with a weapon attack before this spell ends, a writhing mass of thorny vines appears at the point of impact, and the target must succeed on a Strength saving throw or be restrained by the magical vines until the spell ends. A Large or larger creature has advantage on this saving throw. If the target succeeds on the save, the vines shrivel away.<br> While restrained by this spell, the target takes 1d6 piercing damage at the start of each of its turns. A creature restrained by the vines or one that can touch the creature can use its action to make a Strength check against your spell save DC. On a success, the target is freed.<br> <b>At Higher Levels</b>: If you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b6a9a37f-91ee-4562-80a6-37fd36fa3f71', 'Flame Blade', 'You evoke a fiery blade in your free hand. The blade is similar in size and shape to a scimitar, and it lasts for the duration. If you let go of the blade, it disappears, but you can evoke the blade again as a bonus action.<br> You can use your action to make a melee spell attack with the fiery blade. On a hit, the target takes 3d6 fire damage.<br> The flaming blade sheds bright light in a 10-foot radius and dim light for an additional 10 feet.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d6 for every two slot levels above 2nd.', 'leaf of sumac', 'Self', 'VSM', NULL, '1 bonus action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7dfd1988-1901-46a2-812c-f9d764c9b217', 'Thunderwave', 'A wave of thunderous force sweeps out from you. Each creature in a 15-foot cube originating from you must make a Constitution saving throw. On a failed save, a creature takes 2d8 thunder damage and is pushed 10 feet away from you. On a successful save, the creature takes half as much damage and isn''t pushed.<br> In addition, unsecured objects that are completely within the area of effect are automatically pushed 10 feet away from you by the spell''s effect, and the spell emits a thunderous boom audible out to 300 feet.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d8 for each slot level above 1st.', '', 'Self (15-foot cube)', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a6906284-4102-4db3-ad89-e798a6a75b17', 'Death Ward', 'You touch a creature and grant it a measure of protection from death.<br> The first time the target would drop to 0 hit points as a result of taking damage, the target instead drops to 1 hit point, and the spell ends.<br> If the spell is still in effect when the target is subjected to an effect that would kill it instantaneously without dealing damage, that effect is instead negated against the target, and the spells ends.', '', 'Touch', 'VS', NULL, '1 action', false, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e1abd827-5cea-42a4-820b-7bd44b17f604', 'Flaming Sphere', 'A 5-foot-diameter sphere of fire appears in an unoccupied space of your choice within range and lasts for the duration. Any creature that ends its turn within 5 feet of the sphere must make a Dexterity saving throw. The creature takes 2d6 fire damage on a failed save, or half as much damage on a successful one.<br> As a bonus action, you can move the sphere up to 30 feet. If you ram the sphere into a creature, that creature must make the saving throw against the sphere''s damage, and the sphere stops moving this turn.<br> When you move the sphere, you can direct it over barriers up to 5 feet tall and jump it across pits up to 10 feet wide. The sphere ignites flammable objects not being worn or carried, and it sheds bright light in a 20-foot radius and dim light for an additional 20 feet.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d6 for each slot level above 2nd.', 'a bit of tallow, a pinch of brimstone, and a dusting of powdered iron', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8e146893-8dfa-4e4f-801d-b2e75f417fad', 'Moonbeam', 'A silvery beam of pale light shines down in a 5-foot radius, 40-foot-high cylinder centered on a point within range. Until the spell ends, dim light fills the cylinder.<br> When a creature enters the spell''s area for the first time on a turn or starts its turn there, it is engulfed in ghostly flames that cause searing pain, and it must make a Constitution saving throw. It takes 2d10 radiant damage on a failed save, or half as much damage on a successful one.<br> A shapechanger makes its saving throw with disadvantage. If it fails, it also instantly reverts to its original form and can''t assume a different form until it leaves the spell''s light.<br> On each of your turns after you cast this spell, you can use an action to move the beam 60 feet in any direction.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d10 for each slot level above 2nd.', 'several seeds of any moonseed plant and a piece of opalescent feldspar', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a8ef4218-a281-48cc-b471-bc85f11dcf27', 'Pass without Trace', 'A veil of shadows and silence radiates from you, masking you and your companions from detection. For the duration, each creature you choose within 30 feet of you (including you) has a +10 bonus to Dexterity (Stealth) checks and can''t be tracked except by magical means. A creature that receives this bonus leaves behind no tracks or other traces of its passage.', 'ashes from a burned leaf of mistletoe and a sprig of spruce', 'Self', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9562539f-ec04-4842-ad99-75aa04fa63a6', 'Spike Growth', 'The ground in a 20-foot radius centered on a point within range twists and sprouts hard spikes and thorns. The area becomes difficult terrain for the duration. When a creature moves into or within the area, it takes 2d4 piercing damage for every 5 feet it travels.<br> The transformation of the ground is camouflaged to look natural. Any creature that can''t see the area at the time the spell is case must make a Wisdom (Perception) check against your spell save DC to recognize the terrain as hazardous before entering it.', 'seven sharp thorns or seven small twigs, each sharpened to a point', '150 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('25bc88e7-0dc5-4f47-ae62-47ef15db9ea7', 'Hail of Thorns', 'The next time you hit a creature with a ranged weapon attack before the spell ends, this spell creates a rain of thorns that sprouts from your ranged weapon or ammunition. In addition to the normal effect of the attack, the target of the attack and each creature within 5 feet of it must make a Dexterity saving throw. A creature takes 1d10 piercing damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: If you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d10 for each slot level above 1st (to a maximum of 6d10).', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cdec245c-73b9-46f3-b489-a2bf8a3459fb', 'Elemental Bane', 'Choose one creature you can see within range, and choose one of the following damage types - acid, cold, fire, lightning, or thunder. The target must succeed on a Constitution saving throw or be affected by the spell for its duration. The first time each turn the affected target takes damage of the chosen type, the target takes an extra 2d6 damage of that type. Moreover, the target loses any resistance to that damage type until the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, you can target one additional creature for each slot level above 4th. The creatures must be within 30 feet of each other when you target them.', '', '90 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f688b8b5-4083-4802-bb6d-b84a635bafad', 'Call Lightning', 'A storm cloud appears in the shape of a cylinder that is 10 feet tall with a 60-foot radius, centered on a point you can see 100 feet directly above you. The spell fails if you can''t see a point in the air where the storm cloud could appear (for example, if you are in a room that can''t accommodate the cloud).<br> When you cast the spell, choose a point you can see within range. A bolt of lightning flashes down from the cloud to that point. Each creature within 5 feet of that point must make a Dexterity saving throw. A creature takes 3d10 lightning damage on a failed save, or half as much damage on a successful one. On each of your turns until the spell ends, you can use your action to call down lightning in this way again, targeting the same point or a different one.<br> If you are outdoors in stormy conditions when you cast this spell, the spell gives you control over the existing storm instead of creating a new one. Under such conditions, the spell''s damage increases by 1d10.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher level, the damage increases by 1d10 for each slot level above 3rd.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3e2faf45-696c-41b5-9b8b-117ff424952d', 'Hex', 'You place a curse on a creature that you can see within range. Until the spell ends, you deal an extra 1d6 necrotic damage to the target whenever you hit it with an attack. Also, choose one ability when you cast the spell. The target has disadvantage on ability checks made with the chosen ability.<br> If the target drops to 0 hit points before this spell ends, you can use a bonus action on a subsequent turn of yours to curse a new creature. A <i>remove curse</i> cast on the target ends this spell early.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd or 4th level, you can maintain your concentration on the spell for up to 8 hours. When you use a spell slot of 5th level or higher, you can maintain your concentration on the spell for up to 24 hours.', 'the petrified eye of a newt', '90 feet', 'VSM', NULL, '1 bonus action', false, 'up to 1 hour', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3100cf2b-2af3-458b-8035-57724b3e3bb2', 'Ice Knife', 'You create a shard of ice and fling it at one creature within range. Make a ranged spell attack against the target. On a hit, the target takes 1d10 piercing damage. Hit or miss, the shard then explodes. The target and each creature within 5 feet of it must succeed on a Dexterity saving throw or take 2d6 cold damage.<br> <b>At Higher Levels</b>: When you cast this spell using aspell slot of 2nd level or higher, the cold damage increases by 1d6 for each slot level above 1st.', 'a drop of water or piece of ice', '60 feet', 'SM', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('79d4e1f2-59e7-409a-b62e-933fef4ca096', 'Conjure Animals', 'You summon fey spirits that take the form of beasts and appear in unoccupied spaces that you can see within range. Choose one of the following options for what appears.<br>  One beast of challenge rating 2 or lower<br>  Two beasts of challenge rating 1 or lower<br>  Four beasts of challenge rating 1/2 or lower<br>  Eight beasts of challenge rating 1/4 or lower<br> Each beast is also considered fey, and it disappears when it drops to 0 hit points or when the spell ends.<br> The summoned creatures are friendly to you and your companions. Roll initiative for the summoned creatures as a group, which has its own turns. They obey any verbal commands that you issue to them (no action required by you). If you don''t issue any commands to them, they defend themselves from hostile creatures, but otherwise take no actions. The DM has the creatures'' statistics.<br> <b>At Higher Levels</b>: When you cast this spell using certain higher-level spell slots, you choose one of the summoning options above, and more creatures appear - twice as many with a 5th-level slot, three times as many with a 7th-level slot, and four times as many with a 9th-level slot.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e69f3556-d307-4208-8f2c-7df1c3502ae1', 'Wind Wall', 'A wall of strong wind rises from the ground at a point you choose within range. You can make the wall up to 50 feet long, 15 feet high, and 1 foot thick. You can shape the wall in any way you choose so long as it makes one continuous path along the ground. The wall lasts for the duration.<br> When the wall appears, each creature within its area must make a Strength saving throw. A creature takes 3d8 bludgeoning damage on a failed save, or half as much damage on a successful one.<br> The strong wind keeps fog, smoke, and other gases at bay. Small or smaller flying creatures or objects can''t pass through the wall. Loose, lightweight materials brought into the wall fly upward. Arrows, bolts, and other ordinary projectiles launched at targets behind the wall are deflected upward and automatically miss. (Boulders hurled by giants or siege engines, and similar projectiles, are unaffected.) Creatures in gaseous form can''t pass through it.', 'a tiny fan and a feather of exotic origin', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a55a6135-2212-4cd4-afd1-c9d6c0557ce5', 'Evard''s Black Tentacles', 'Squirming, ebony tentacles fill a 20-foot square on ground that you can see within range. For the duration, these tentacles turn the ground in the area into difficult terrain.<br> When a creature enters the affected area for the first time on a turn or starts its turn there, the creature must succeed on a Dexterity saving throw or take 3d6 bludgeoning damage and be restrained by the tentacles until the spell ends. A creature that starts its turn in the area and is already restrained by the tentacles takes 3d6 bludgeoning damage.<br> A creature restrained by the tentacles can use its action to make a Strength or Dexterity check (its choice) against your spell save DC. On a success, it frees itself.', 'a piece of tentacle from a giant octopus or a giant squid', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c6266687-5763-455b-b84d-fc841af75bcf', 'Sunburst', 'Brilliant sunlight flashes in a 60-foot radius centered on a point you choose within range. Each creature in that light must make a Constitution saving throw. On a failed save, a creature takes 12d6 radiant damage and is blinded for 1 minute. On a successful save, it takes half as much damage and isn''t blinded by this spell. Undead and oozes have disadvantage on this saving throw.<br> A creature blinded by this spell makes another Constitution saving throw at the end of each of its turns. On a successful save, it is no longer blinded.<br> This spell dispels any darkness in its area that was created by a spell.', 'fire and a piece of sunstone', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a31f9a48-e4e9-4b4b-84db-843acbbc6594', 'Blight', 'Necromantic energy washes over a creature of your choice that you can see within range, draining moisture and vitality from it. The target must make a Constitution saving throw. The target takes 8d8 necrotic damage on a failed save, or half as much damage on a successful one. This spell has no effect on undead or constructs.<br> If you target a plant creature or a magical plant, it makes the saving throw with disadvantage, and the spell deals maximum damage to it.<br> If you target a nonmagical plant that isn''t a creature, such as a tree or shrub, it doesn''t make a saving throw, it simply withers and dies.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the damage increases by 1d8 for each slot level above 4th.', '', '30 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5f1cfb5a-c9e6-4fc4-9133-d4e8135d49d2', 'Conjure Minor Elementals', 'You summon elementals that appear in unoccupied spaces that you can see within range. You choose one the following options for what appears.<br>  One elemental of challenge rating 2 or lower<br>  Two elementals of challenge rating 1 or lower<br>  Four elementals of challenge rating 1/2 or lower<br>  Eight elementals of challenge rating 1/4 or lower.<br> An elemental summoned by this spell disappears when it drops to 0 hit points or when the spell ends.<br> The summoned creatures are friendly to you and your companions. Roll initiative for the summoned creatures as a group, which has its own turns. They obey any verbal commands that you issue to them (no action required by you). If you don''t issue any commands to them, they defend themselves from hostile creatures, but otherwise take no actions.<br> The DM has the creatures'' statistics.<br> <b>At Higher Levels</b>: When you cast this spell using certain higher-level spell slots, you choose one of the summoning options above, and more creatures appear - twice as many with a 6th-level slot and three times as many with an 8th-level slot .', '', '90 feet', 'VS', NULL, '1 minute', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d70e72ef-d1cd-42bb-a28d-7535b326c27d', 'Longstrider', 'You touch a creature. The target''s speed increases by 10 feet until the spell ends.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you can target 1 additional creature for each slot level above 1st.', 'A pinch of dirt', 'Touch', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2e2ff38b-d74e-4639-adc8-90567415b8c4', 'Mordenkainen''s Faithful Hound', 'You conjure a phantom watchdog in an unoccupied space that you can see within range, where it remains for the duration, until you dismiss it as an action, or until you move more than 100 feet away from it.<br> The hound is invisible to all creatures except you and can''t be harmed. When a Small or larger creature comes within 30 feet of it without first speaking the password that you specify when you cast this spell, the hound starts barking loudly. The hound sees invisible creatures and can see into the Ethereal Plane. It ignores illusions.<br> At the start of each of your turns, the hound attempts to bite one creature within 5 feet of it that is hostile to you. The hound''s attack bonus is equal to your spellcasting ability modifier + your proficiency bonus. On a hit, it deals 4d8 piercing damage.', 'a tiny silver whistle, a piece of bone, and a thread', '30 feet', 'VSM', NULL, '1 action', false, '8 hours', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bc62b7da-ff4d-4074-939a-3be2cc17247f', 'Ray of Sickness', 'A ray of sickening greenish energy lashes out toward a creature within range. Make a ranged spell attack against the target. On a hit, the target takes 2d8 poison damage and must make a Constitution saving throw. On a failed save, it is also poisoned until the end of your next turn.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d8 for each slot level above 1st.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2274ff47-f29e-4336-a7ef-19121aed2365', 'Searing Smite', 'The next time you hit a creature with a melee weapon attack during the spell''s duration, your weapon flares with white-hot intensity, and the attack deals an extra 1d6 fire damage to the target and causes the target to ignite in flames. At the start of each of its turns until the spell ends, the target must make a Constitution saving throw. On a failed save, it takes 1d6 fire damage. On a successful save, the spell ends. If the target or a creature within 5 feet of it uses an action to put out the flames, or if some other effect douses the flames (such as the target being submerged in water), the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the initial extra damage dealt by the attack increases by 1d6 for each slot above the 1st.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a4c40732-84e7-42ff-898d-36f72a9ce6e0', 'Conjure Woodland Beings', 'You summon fey creatures that appear in unoccupied spaces that you can see within range. Choose one of the following options for what appears.<br>  One fey creature of challenge rating 2 or lower<br>  Two fey creatures of challenge rating 1 or lower<br>  Four fey creatures of challenge rating 1/2 or lower<br>  Eight fey creatures of challenge rating 1/4 or lower<br> A summoned creature disappears when it drops to 0 hit points or when the spell ends.<br> The summoned creatures are friendly to you and your companions. Roll initiative for the summoned creatures as a group, which have their own turns. They obey any verbal commands that you issue to them (no action required by you). If you don''t issue any commands to them, they defend themselves from hostile creatures, but otherwise take no actions.<br> The DM has the creatures'' statistics.<br> <b>At Higher Levels</b>: When you cast this spell using certain higher-level spell slots, you choose one of the summoning options above, and more creatures appear - twice as many with a 6th-level slot and three times as many with an 8th-level slot.', 'one holly berry per creature summoned', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4cbe127a-94da-40b0-8997-d315f4c09b0d', 'Snare', 'As you cast this spell, you use the rope to create a circle with a 5-foot radius on the ground or the floor. When you finish casting, the rope disappears and the circle becomes a magic trap.<br> This trap is nearly invisible, requiring a successful Intelligence (Investigation) check against your spell save DC to be discerned.<br> The trap triggers when a Small, Medium, or Large creature moves onto the ground or the floor in the spell''s radius. That creature must succeed on a Dexterity saving throw or be magically hoisted into the air, leaving it hanging upside down 3 feet above the ground or the floor. The creature is restrained there until the spell ends.<br> A restrained creature can make a Dexterity saving throw at the end of each of its turns, ending the effect on itself on a success. Alternatively, the creature or someone else who can reach it can use an action to make an Intelligence (Arcana) check against your spell save DC. On a success, the restrained effect ends.<br> After the trap is triggered, the spell ends when no creature is restrained by it.', '25 feet of rope, which the spell consumes', 'Touch', 'SMgp', NULL, '1 minute', false, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9168596e-3be9-44b8-a305-620134ed802f', 'Dominate Beast', 'You attempt to beguile a beast that you can see within range. It must succeed on a Wisdom saving throw or be charmed by you for the duration. If you or creatures that are friendly to you are fighting it, it has advantage on the saving throw.<br> While the beast is charmed, you have a telepathic link with it as long as the two of you are on the same plane of existence. You can use this telepathic link to issue commands to the creature while you are conscious (no action required), which it does its best to obey. You can specify a simple and general course of action, such as Attack that creature, Run over there, or Fetch that object. If the creature completes the order and doesn''t receive further direction from you, it defends and preserves itself to the best of its ability.<br> You can use your action to take total and precise control of the target. Until the end of your next turn, the creature takes only the actions you choose, and doesn''t do anything that you don''t allow it to do. During this time, you can also cause the creature to use a reaction, but this requires you to use your own reaction as well.<br> Each time the target takes damage, it makes a new Wisdom saving throw against the spell. If the saving throw succeeds, the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell with a 5th-level spell slot, the duration is concentration, up to 10 minutes. When you use a 6th-level spell slot, the duration is concentration, up to 1 hour. When you use a spell slot of 7th level or higher, the duration is concentration, up to 8 hours.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7d46c45e-605b-4661-9aac-2f05fe7175b2', 'Giant Insect', 'You transform up to ten centipedes, three spiders, five wasps, or one scorpion within range into giant versions of their natural forms for the duration. A centipede becomes a giant centipede, a spider becomes a giant spider, a wasp becomes a giant wasp, and a scorpion becomes a giant scorpion.<br> Each creature obeys your verbal commands, and in combat, they act on your turn each round. The DM has the statistics for these creatures and resolves their actions and movement.<br> A creature remains in its giant size for the duration, until it drops to 0 hit points, or until you use an action to dismiss the effect on it.<br> The DM might allow you to choose different targets. For example, if you transform a bee, its giant version might have the same statistics as a giant wasp.', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c3cc937f-6d46-45e7-850e-e3ea9e8dfe14', 'Unseen Servant', 'This spell creates an invisible, mindless, shapeless force that performs simple tasks at your command until the spell ends. The servant springs into existence in an unoccupied space on the ground within range. It has AC 10, 1 hit point, and a Strength of 2, and it can''t attack. If it drops to 0 hit points, the spell ends.<br> Once on each of your turns as a bonus action, you can mentally command the servant to move up to 15 feet and interact with an object. The servant can perform simple tasks that a human servant could do, such as fetching things, cleaning, mending, folding clothes, lighting fires, serving food, and pouring wine. Once you give the command, the servant performs the task to the best of its ability until it completes the task, then waits for your next command.<br> If you command the servant to perform a task that would move it more than 60 feet away from you, the spell ends.', 'A piece of string and a bit of wood', '60 feet', 'VSM', NULL, '1 action', true, '1 hour', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5eac9c74-2705-4e2d-b9c4-48be37ceff1b', 'Expeditious Retreat', 'This spell allows you to move at an incredible pace. When you cast this spell, and then as a bonus action on each of your turns until the spell ends, you can take the Dash action.', '', 'Self', 'VS', NULL, '1 bonus action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a9937d3b-2a74-4e9f-8ab7-632257f7f13b', 'Witch Bolt', 'A beam of crackling, blue energy lances out toward a creature within range, forming a sustained arc of lightning between you and the target. Make a ranged spell attack against that creature. On a hit, the target takes 1d12 lightning damage, and on each of your turns for the duration, you can use your action to deal 1d12 lightning damage to the target automatically. The spell ends if you use your action to do anything else. The spell also ends if the target is ever outside the spell''s range or if it has total cover from you.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the initial damage increases by 1d12 for each slot level above 1st.', 'a twig from a tree that has been struck by lightning', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('93a9f322-da8b-4058-849f-28d9a4ff7f30', 'Wrathful Smite', 'The next time you hit with a melee weapon attack during this spell''s duration, your attack deals an extra 1d6 psychic damage. Additionally, if the target is a creature, it must make a Wisdom saving throw or be frightened of you until the spell ends. As an action, the creature can make a Wisdom check against your spell save DC to steel its resolve and end this spell.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7f375436-73b1-4f77-8e0a-e6a14e895eff', 'Zephyr Strike', 'You move like the wind. Until the spell ends, your movement doesn''t provoke opportunity attacks.<br>Once before the spell ends, you can give yourself advantage on one weapon attack roll on your turn. That attack deals an extra 1d8 force damage on a hit. Whether you hit or miss, your walking speed increases by 30 feet until the end of that turn.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5f328ae9-a715-425c-a2ab-690933fc7ea2', 'Ice Storm', 'A hail of rock-hard ice pounds to the ground in a 20-foot-radius, 40-foot-high cylinder centered on a point within range. Each creature in the cylinder must make a Dexterity saving throw. A creature takes 2d8 bludgeoning damage and 4d6 cold damage on a failed save, or half as much damage on a successful one.<br> Hailstones turn the storm''s area of effect into difficult terrain until the end of your next turn.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the bludgeoning damage increases by 1d8 for each slot level above 4th.', 'a pinch of dust and a few drops of water', '300 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('34ad230c-0bea-428a-a8c6-a55c66a438ce', 'Wall of Fire', 'You create a wall of fire on a solid surface within range. You can make the wall up to 60 feet long, 20 feet high, and 1 foot think, or a ringed wall up to 20 feet in diameter, 20 feet high, and 1 foot think. The wall is opaque and lasts for the duration.<br> When the wall appears, each creature within its area must make a Dexterity saving throw. On a failed save, a creature takes 5d8 fire damage, or half as much damage on a successful save.<br> One side of the wall, selected by you when you cast this spell, deals 5d8 fire damage to each creature that ends its turn within 10 feet of that side or inside the wall. A creature takes the same damage when it enters the wall for the first time on a turn or ends its turn there. The other side of the wall deals no damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the damage increases by 1d8 for each slot level above 4th.', 'a small piece of phosphorus', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2e199eca-1f76-42f6-ab62-62e24d8e2373', 'Antilife Shell', 'A shimmering barrier extends out from you in a 10-foot radius and moves with you, remaining centered on you and hedging out creatures other than undead and constructs.<br> The barrier lasts for the duration. The barrier prevents an affected creature from passing or reaching through. An affected creature can cast spells or make attacks with ranged or reach weapons through the barrier.<br> If you move so that an affect creature is forced to pass through the barrier, the spell ends.', '', 'Self (10-foot radius)', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b14a1cda-429a-4c3c-a54f-033aed92006e', 'Scorching Ray', 'You create three rays of fire and hurl them at targets within range. You can hurl them at one target or several.<br> Make a ranged spell attack for each ray. On a hit, the target takes 2d6 fire damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, you create one additional ray for each slot level above 2nd.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('45d48b82-700d-4bda-909c-c10d396224c3', 'Conjure Elemental', 'You call forth an elemental servant. Choose an area of air, earth, fire, or water that fills a 10-foot cube within range. An elemental of challenge rating 5 or lower appropriate to the area you chose appears in an unoccupied space within 10 feet of it. For example, a fire elemental emerges from a bonfire, and an earth elemental rises up from the ground. The elemental disappears when it drops to 0 hit points or when the spell ends.<br> The elemental is friendly to you and your companions for the duration. Roll initiative for the elemental, which has its own turns. It obeys any verbal commands that you issue to it (no action required by you). If you don''t issue any commands to the elemental, it defends itself from hostile creatures but otherwise takes no actions.<br> If your concentration is broken, the elemental doesn''t disappear. Instead, you lose control of the elemental, it becomes hostile toward you and your companions, and it might attack. An uncontrolled elemental can''t be dismissed by you, and it disappears 1 hour after you summoned it.<br> The DM has the elemental''s statistics.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the challenge rating increases by 1 for each slot level above 5th.', 'burning incense for air, soft clay for earth, sulfur and phosphorus for fire, or water and sand for water', '90 feet', 'VSM', NULL, '1 minute', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fbe0bca4-bc8e-4dcd-8fd5-7e718bf5d7ee', 'Beast Sense', 'You touch a willing beast. For the duration of the spell, you can use your action to see through the beast''s eyes and hear what it hears, and continue to do so until you use your action to return to your normal senses.', '', 'Touch', 'S', NULL, '1 action', true, 'up to 1 hour', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4633eaf6-ce9e-481a-986f-f50737b5a3b8', 'Reincarnate', 'You touch a dead humanoid or a piece of a dead humanoid. Provided that the creature has been dead no longer than 10 days, the spell forms a new adult body for it and then calls the soul to enter that body. If the target''s soul isn''t free or willing to do so, the spell fails.<br> The magic fashions a new body for the creature to inhabit, which likely causes the creature''s race to change. The DM rolls a d100 and consults the following table to determine what form the creature takes when restored to life, or the DM chooses a form. 01-04 Dragonborn. 05-13 Dwarf, hill. 14-21 Dwarf, mountain. 22-25 Elf, dark 26-34 Elf, high. 35-42 Elf, wood. 43-46 Gnome, forest. 47-52 Gnome, rock. 53-56 Half-elf. 57-60 Half-orc. 61-68 Halfling, lightfoot. 69-76 Halfling, stout. 77-96 Human. 97-00 Tiefling.<br> The reincarnated creature recalls its former life and experiences. It retains the capabilities it had in its original form, except it exchanges its original race for the new one and changes its racial traits accordingly.', 'rare oils and unguents worth at least 1,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 hour', false, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f80ad17a-0f12-4891-a9ad-98a981114ab4', 'Tree Stride', 'You gain the ability to enter a tree and move from inside it to inside another tree of the same kind within 500 feet. Both trees must be living and at least the same size as you. You must use 5 feet of movement to enter a tree. You instantly know the location of all other trees of the same kind within 500 feet and, as part of the move used to enter the tree, can either pass into one of those trees or step out of the tree you''re in. You appear in a spot of your choice within 5 feet of the destination tree, using another 5 feet of movement. If you have no movement left, you appear within 5 feet of the tree you entered.<br> You can use this transportation ability once per round for the duration. You must end each turn outside a tree.', '', 'Self', 'VS', NULL, '1 action', false, 'Until dispelled', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a8d54b74-e955-47eb-8a3a-6fb3ea8ab102', 'Barkskin', 'You touch a willing creature. Until the spell ends, the target''s skin has a rough, bark-like appearance, and the target''s AC can''t be less than 16, regardless of what kind of armor it is wearing.', 'a handful of oak bark', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3ae34f53-1391-4ee9-8011-15d66e123443', 'Earthbind', 'Choose one creature you can see within range. Yellow strips of magical energy loop around the creature. The target must succeed on a Strength saving throw, or its flying speed (if any) is reduced to 0 feet for the spell''s duration. An airborne creature affected by this spell safely descends at 60 feet per round until it reaches the ground or the spell ends.', '', '300 feet', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('74227736-8499-4c6c-9ca8-250770485075', 'Commune with Nature', 'You briefly become one with nature and gain knowledge of the surrounding territory. In the outdoors, the spell gives you knowledge of the land within 3 miles of you. In caves and other natural underground settings, the radius is limited to 300 feet. The spell doesn''t function where nature has been replaced by construction, such as in dungeons and towns.<br> You instantly gain knowledge of up to three facts of your choice about any of the following subjects as they relate to the area:<br>  terrain and bodies of water<br>  prevalent plants, minerals, animals, or peoples<br>   powerful celestials, fey, fiends, elementals, or undead<br>  influence from other planes of existence<br>  buildings.<br> For example, you could determine the location of powerful undead in the area, the location of major sources of safe drinking water, and the location of any nearby towns.', '', 'Self', 'VS', NULL, '1 minute', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4a8867cd-4597-4e32-a138-f8290ff605e1', 'Wall of Stone', 'A nonmagical wall of solid stone springs into existence at a point you choose within range. The wall is 6 inches thick and is composed of ten 10-foot-by-10-foot panels. Each panel must be contiguous with at least on other panel. Alternatively, you can create 10-foot-by-20-foot panels that are only 3 inches thick.<br> If the wall cuts through a creature''s space when it appears, the creature is pushed to one side of the wall (your choice). If a creature would be surrounded on all sides by the wall (or the wall and another solid surface), that creature can make a Dexterity saving throw. On a success, it can use its reaction to move up to its speed so that it is no longer enclosed by the wall.<br> The wall can have any shape you desire, though it can''t occupy the same space as a creature or object. The wall doesn''t need to be vertical or resting on any firm foundation. It must, however, merge with and be solidly supported by existing stone. Thus you can use this spell to bridge a chasm or create a ramp.<br> If you create a span greater than 20 feet in length, you must halve the size of each panel to create supports. You can crudely shape the wall to create crenellations, battlements, and so on.<br> The wall is an object made of stone that can be damaged and thus breached. Each panel has AC 15 and 30 hit points per inch of thickness. Reducing a panel to 0 hit points destroys it and might cause connected panels to collapse at the DM''s discretion.<br> If you maintain your concentration on this spell for its whole duration, the wall becomes permanent and can''t be dispelled. Otherwise, the wall disappears when the spell ends.', 'a small block of granite', '120 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9d6abbe6-e87e-460d-a095-8eee1276e83a', 'Cloud of Daggers', 'You fill the air with spinning daggers in a cube 5 feet on each side, centered on a point you choose within range. A creature takes 4d4 slashing damage when it enters the spell''s area for the first time on a turn or starts its turn there.<br><br><b>At Higher Levels</b>: when you cast this spell using a spell slot of 3rd level or higher, the damage increases by 2d4 for each slot level above 2nd.', 'A sliver of glass', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a5760a95-169f-4ef4-845e-a1351a28961f', 'Conjure Fey', 'You summon a fey creature of challenge rating 6 or lower, or a fey spirit that takes the form of a beast of challenge rating 6 or lower. It appears in an unoccupied space that you can see within range. The fey creature disappears when it drops to 0 hit points or when the spell ends.<br> The fey creature is friendly to you and your companions for the duration. Roll initiative for the creature, which has its own turns. It obeys any verbal commands that you issue to it (no action required by you), as long as they don''t violate its alignment. If you don''t issue any commands to the fey creature, it defends itself from hostile creatures but otherwise takes no actions.<br> If your concentration is broken, the fey creature doesn''t disappear. Instead, you lose control of the fey creature, it becomes hostile toward you and your companions, and it might attack. An uncontrolled fey creature can''t be dismissed by you, and it disappears 1 hour after you summoned it.<br> The DM has the fey creature''s statistics.<br <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the challenge rating increases by 1 for each slot level above 6th.', '', '90 feet', 'VS', NULL, '1 minute', false, 'Instantaneous', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c3de519e-13a8-4083-8e9f-efd6f19a9121', 'Cordon of Arrows', 'You plant four pieces of nonmagical ammunition - arrows or crossbow bolts - in the ground within range and lay magic upon them to protect an area. Until the spell ends, whenever a creature other than you comes within 30 feet of the ammunition for the first time on a turn or ends its turn there, one piece of ammunition flies up to strike it. The creature must succeed on a Dexterity saving throw or take 1d6 piercing damage. The piece of ammunition is then destroyed. The spell ends when no ammunition remains.<br> When you cast this spell, you can designate any creatures you choose, and the spell ignores them.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the amount of ammunition that can be affected increases by two for each slot level above 2nd.', 'four or more arrows or bolts', '5 feet', 'VSM', NULL, '1 action', false, '8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a4aa58be-2db6-4173-8ecf-22ccc01422b2', 'Crown of Madness', 'One humanoid of your choice that you can see within range must succeed on a Wisdom saving throw or become charmed by you for the duration. While the target is charmed in this way, a twisted crown of jagged iron appears on its head, and a madness glows in its eyes.<br> The charmed target must use its action before moving on each of its turns to make a melee attack against a creature other than itself that you mentally choose. The target can act normally on its turn if you choose no creature or if none are within its reach.<br> On your subsequent turns, you must use your action to maintain control over the target, or the spell ends. Also, the target can make a Wisdom saving throw at the end of each of its turns. On a success, the spell ends.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('024cc92c-4376-46cf-a301-83044cf5a8d4', 'Animal Messenger', 'By means of this spell, you use an animal to deliver a message. Choose a Tiny beast you can see within range, such as a squirrel, a blue ray, or a bird. You specify a location, which you must have visited, and a recipient who matches a general description, such as a man or woman dressed in the uniform of the town guard or a red-haired dwarf wearing a pointed hat. You also speak a message of up to twenty-5 words. The target beast travels for the duration of the spell towards the specified location, covering about 50 miles per 24 hours for a flying messenger or 25 miles for other animals.<br> When the messenger arrives, it delivers your message to the creature that you described, replicating the sound of your voice. The messenger speaks only to a creature matching the description you gave. If the messenger doesn''t reach its destination before the spell ends, the message is lost, and the beast makes its way back to where you cast this spell.<br><br><b>At Higher Levels</b>: If you cast this spell using a spell slot of 3rd level or higher, the duration of the spell increases by 48 hours for each slot level above 2nd.', 'A morsel of food', '30 feet', 'VSM', NULL, '1 action', true, '24 hours', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('be71de18-0c24-4ed2-b83d-9aed77fe3dd5', 'Blindness/Deafness', 'You can blind or deafen a foe. Choose 1 creature that you can see within range to make a Constitution saving throw. If it fails, the target is either blinded or deafened (your choice) for the duration. At the end of each of its turns, the target can make a Constitution saving throw. On a success, the spell ends.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, you can target 1 additional creature for each slot level above 2nd.', '', '30 feet', 'V', NULL, '1 action', false, '1 minute', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e9512e1a-2b10-4363-afc1-ddf340a04c66', 'Dust Devil', 'Choose an unoccupied 5-foot cube of air that you can see within range. An elemental force that resembles a dust devil appears in the cube and lasts for the spell''s duration.<br> Any creature that ends its turn within 5 feet of the dust devil must make a Strength saving throw. On a failed save, the creature takes 1d8 bludgeoning damage and is pushed 10 feet away from the dust devil. On a successful save, the creature takes half as much damage and isn''t pushed.<br> As a bonus action, you can move the dust devil up to 30 feet in any direction. If the dust devil moves over sand, dust, loose dirt, or light gravel, it sucks up the material and forms a 10-foot-radius cloud of debris around itself that lasts until the start of your next turn. The cloud heavily obscures its area.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d8 for each slot level above 2nd.', 'a pinch of dust', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8de76b66-b814-457c-8edc-6db46d4299cc', 'Move Earth', 'Choose an area of terrain no larger than 40 feet on a side within range. You can reshape dirt, sand, or clay in the area in any manner you choose for the duration. You can raise or lower the area''s elevation, create or fill in a trench, erect or flatten a wall, or form a pillar. The extent of any such changes can''t exceed half the area''s largest dimension. So, if you affect a 40-foot square, you can create a pillar up to 20 feet high, raise or lower the square''s elevation by up to 20 feet, dig a trench up to 20 feet deep, and so on. It takes 10 minutes for these changes to complete.<br> At the end of every 10 minutes you spend concentrating on the spell, you can choose a new area of terrain to affect.<br> Because the terrain''s transformation occurs slowly, creatures in the area can''t usually be trapped or injured by the ground''s movement.<br> This spell can''t manipulate natural stone or stone construction. Rocks and structures shift to accommodate the new terrain. If the way you shape the terrain would make a structure unstable, it might collapse.<br> Similarly, this spell doesn''t directly affect plant growth. The moved earth carries any plants along with it.', 'an iron blade and a small bag containing a mixture of soils - clay, loam, and sand', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b0221bfa-3ce1-4a9f-a978-f595023260eb', 'Transport via Plants', 'This spell creates a magical link between a Large or larger inanimate plant within range and another plant, at any distance, on the same plane of existence. You must have seen or touched the destination plant at least once before. For the duration, any creature can step into the target plant and exit from the destination plant by using 5 feet of movement.', '', '10 feet', 'VS', NULL, '1 action', false, '1 round', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d7f7b0e2-752c-4b7b-a104-1af9c99f8784', 'Wall of Thorns', 'You create a wall of tough, pliable, tangled brush bristling with needle-sharp thorns. The wall appears within range on a solid surface and lasts for the duration. You choose to make the wall up to 60 feet long, 10 feet high, and 5 feet thick or a circle that has a 20-foot diameter and is up to 20 feet high and 5 feet thick. The wall blocks line of sight.<br> When the wall appears, each creature within its area must make a Dexterity saving throw. On a failed save, a creature takes 7d8 piercing damage, or half as much damage on a successful save.<br> A creature can move through the wall, albeit slowly and painfully. For every 1 foot a creature moves through the wall, it must spend 4 feet of movement. Furthermore, the first time a creature enters the wall on a turn or ends its turn there, the creature must make a Dexterity saving throw. It takes 7d8 slashing damage on a failed save, or half as much on a successful save.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, both types of damage increase by 1d8 for each slot level above 6th.', 'a handful of thorns', '120 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6d6d136f-1e34-44ca-874c-b8b2f3f08cd3', 'Wish', 'Wish is the mightiest spell a mortal creature can cast. By simply speaking aloud, you can alter the very foundations of reality in accord with your desires.<br> The basic use of this spell is to duplicate any other spell of 8th level or lower. You don''t need to meet any requirements in that spell, including costly components. The spell simply takes effect. Alternatively, you can create one of the following effects of your choice.<br>  You create one object of up to 25,000 gp in value that isn''t a magic item. The object can be no more than 300 feet in any dimension, and it appears in an unoccupied space you can see on the ground.<br>  You allow up to twenty creatures that you can see to regain all hit points, and you end all effects on them described in the <i>greater restoration</i> spell.<br>  You grant up to ten creatures that you can see resistance to a damage type you choose.<br>  You grant up to ten creatures you can see immunity to a single spell or other magical effect for 8 hours. For instance, you could make yourself and all your companions immune to a lich''s life drain attack.<br>  You undo a single recent event by forcing a reroll of any roll made within the last round (including your last turn). Reality reshapes itself to accommodate the new result. For example, a <i>wish</i> spell could undo an opponent''s successful save, a foe''s critical hit, or a friend''s failed save. You can force the reroll to be made with advantage or disadvantage, and you can choose whether to use the reroll or the original roll.<br> You might be able to achieve something beyond the scope of the above examples. State your wish to the DM as precisely as possible. The DM has great latitude in ruling what occurs in such an instance, the greater the wish, the greater the likelihood that something goes wrong. This spell might simply fail, the effect you desire might only be partly achieved, or you might suffer some unforeseen consequence as a result of how you worded the wish. For example, wishing that a villain were dead might propel you forward in time to a period when that villain is no longer alive, effectively removing you from the game. Similarly, wishing for a legendary magic item or artifact might instantly transport you to the presence of the item''s current owner.<br> The stress of casting this spell to produce any effect other than duplicating another spell weakens you. After enduring that stress, each time you cast a spell until you finish a long rest, you take 1d10 necrotic damage per level of that spell. This damage can''t be reduced or prevented in any way. In addition, your Strength drops to 3, if it isn''t 3 or lower already, for 2d4 days. For each of those days that you spend resting and doing nothing more than light activity, your remaining recovery time decreases by 2 days. Finally, there is a 33 percent chance that you are unable to cast <i>wish</i> ever again if you suffer this stress.', '', 'Self', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ae419518-106f-494e-84d8-b630ef6bc4e0', 'Wind Walk', 'You and up to ten willing creatures you can see within range assume a gaseous form for the duration, appearing as wisps of cloud. While in this cloud form, a creature has a flying speed of 300 feet and has resistance to damage from nonmagical weapons. The only actions a creature can take in this form are the Dash action or to revert to its normal form. Reverting takes 1 minute, during which time a creature is incapacitated and can''t move. Until the spell ends, a creature can revert to cloud form, which also requires the 1-minute transformation.<br> If a creature is in cloud form and flying when the effect ends, the creature descends 60 feet per round for 1 minute until it lands, which it does safely. If it can''t land after 1 minute, the creature falls the remaining distance.', 'fire and holy water', '30 feet', 'VSM', NULL, '1 minute', false, '8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0db43bdc-f168-4ea9-9b76-a156b4d1b8ae', 'Reverse Gravity', 'This spell reverses gravity in a 50-foot-radius, 100-foot high cylinder centered on a point within range. All creatures and objects that aren''t somehow anchored to the ground in the area fall upward and reach the top of the area when you cast this spell. A creature can make a Dexterity saving throw to grab onto a fixed object it can reach, thus avoiding the fall.<br> If some solid object (such as a ceiling) is encountered in this fall, falling objects and creatures strike it just as they would during a normal downward fall. If an object or creature reaches the top of the area without striking anything, it remains there, oscillating slightly, for the duration.<br> At the end of the duration, affected objects and creatures fall back down.', 'a lodestone and iron filings', '100 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0d37ddd0-7039-439e-858e-3ed1632d31f7', 'Animal Shapes', 'Your magic turns others into beasts. Choose any number of willing creatures that you can see within range. You transform each target into the form of a large or smaller beast with a challenge rating of 4 or lower. On subsequent turns, you can use your actions to transform affected creatures into new forms.<br> The transformation lasts for the duration for each target, or until the target drops to 0 hit points or dies. You can choose a different form for each target. A target''s game statistics are replaced by the statistics of the chosen beast, though the target retains its alignment and Intelligence, Wisdom, and Charisma scores. The target assumes the hit points of its new form, and when it reverts to its normal form, it returns to the number of hit point it had before it transformed. If it reverts as a result of dropping to 0 hit points, any excess damage carries over to its normal form. As long as the excess damage doesn''t reduce the creature''s normal form to 0 hit points, it isn''t knocked unconscious. The creature is limited in the actions it can perform by the nature of its new form, and it can''t speak or cast spells.<br> The target''s gear melds into the new form. The target can''t activate, wield, or otherwise benefit from any of its equipment.', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 24 hours', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('aee1fbdc-3730-46c0-9159-756abace3488', 'Antipathy/Sympathy', 'This spell attracts or repels creatures of your choice. You target something within range, either a Huge or smaller object or creature or an area that is no larger than a 200-foot cube. Then specify a kind of intelligent creature, such as red dragons, goblins, or vampires. You invest the target with an aura that either attracts or repels the specified creatures for the duration. Choose antipathy or sympathy as the aura''s effect.<br> <b>Antipathy</b>: The enchantment causes creatures of the kind you designated to feel an intense urge to leave the area and avoid the target. When such a creature can see the target or comes within 60 feet of it, the creature must succeed on a Wisdom saving throw or become frightened. The creature remains frightened while it can see the target or is within 60 feet of it. While frightened by the target, the creature must use its movement to move to the nearest safe spot from which it can''t see the target. If the creature moves more than 60 feet from the target and can''t see it, the creature is no longer frightened, but the creature becomes frightened again if it regains sight of the target or moves within 60 feet of it.<br> <b>Sympathy</b>: The enchantment causes the specified creatures to feel an intense urge to approach the target while within 60 feet of it or able to see it. When such a creature can see the target or comes within 60 feet of it, the creature must succeed on a Wisdom saving throw or use its movement on each of its turns to enter the area or move within reach of the target. When the creature has done so, it can''t willingly move away from the target. If the target damages or otherwise harms an affected creature, the affected creature can make a Wisdom saving throw to end the effect, as described below.<br> <b>Ending the Effect</b>: If an affected creature ends its turn while not within 60 feet of the target or able to see it, the creature makes a Wisdom saving throw. ON a successful save, the creature is no longer affected by the target and recognizes the feeling of repugnance or attraction as magical. In addition, a creature affected by the spells is allowed another Wisdom saving throw every 24 hours while the spell persists. A creature that successfully saves against this effect is immune to it for 1 minute, after which time it can be affected again.', 'either a lump of alum soaked in vinegar for the antipathy effect or a drop of honey for the sympathy effect', '60 feet', 'VSM', NULL, '1 hour', false, '10 days', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c23bc6c9-810b-41d5-b796-f24c454ed400', 'Healing Spirit', 'You call forth a nature spirit to soothe the wounded. The intangible spirit appears in a space that is a 5-foot cube you can see within range. The spirit looks like a transparent beast or fey (your choice).<br> Until the spell ends, whenever you or a creature you can see moves into the spirit''s space for the first time on a turn or starts its turn there, you can cause the spirit to restore 1d6 hit points to that creature (no action required). The spirit can''t heal constructs or undead.<br> As a bonus action on your turn, you can move the spirit up to 30 feet to a space you can see.<br> <b>At Higher Levels</b>. When you cast this spell using a spell slot of 3rd level or higher, the healing increases by 1d6 for each slot level above 2nd.', '', '60 feet', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f4c61433-4989-4505-bbbc-f736f292460c', 'Shapechange', 'You assume the form of a different creature for the duration. The new form can be any creature with a challenge rating equal to your level or lower. The creature can''t be a construct or an undead, and you must have seen the sort of creature at least once. You transform into an average example of that creature, one without any class levels or the Spellcasting trait.<br> Your game statistics are replaced by the statistics of the chosen creature, though you retain your alignment and Intelligence, Wisdom, and Charisma scores You also retain all of your skill and saving throw proficiencies, in addition to gaining those of the creature, If the creature has the same proficiency as you, and the bonus listed in its statistics is higher than yours, use the creature''s bonus in place of yours. You can''t use any legendary actions or lair actions of the new form.<br> You assume the hit points and Hit Dice of the new form. When you revert to your normal, you return to the number of hit points you had before you transformed. If you revert as a result of dropping to 0 hit points, any excess damage carries over to your normal form. As long as the excess damage doesn''t reduce your normal form to 0 hit points, you aren''t knocked unconscious.<br> You retain the benefit of any features from your class, race, or other source and can use them, provided that your new form is physically capable of doing so. You can''t use any special senses you have (for example, darkvision) unless your new form also has that sense. You can only speak if the creature can normally speak.<br> When you transform, you choose whether your equipment falls to the ground, merges into the new form, or is worn by it. Worn equipment functions as normal. The DM determines whether it is practical for the new form to wear a piece of equipment, based on the creature''s shape and size. Your equipment doesn''t change shape or size to match the new form, and any equipment that the new form can''t wear must either fall to the ground or merge into your new form. Equipment that merges has no effect in that state.<br> During this spell''s duration, you can use your action to assume a different form following the same restrictions and rules for the original form, with one exception - if your new form has more hit pints than your current one, your hit points remain at their current value.', 'a jade circlet worth at least 1,500 gp, which you must place on your head before you cast the spell', 'Self', 'VSMgp', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a95fded7-4fd9-4a8e-b42c-db40113e4efb', 'Ray of Enfeeblement', 'A black beam of enervating energy springs from your finger toward a creature within range. Make a ranged spell attack against the target. On a hit, the target deals only half damage with weapon attacks that use Strength until the spell ends.<br> At the end of each of the target''s turns, it can make a Constitution saving throw against the spell. On a success, the spell ends.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3e07f5bb-97ad-4a01-8ada-f886b851ce7b', 'Storm of Vengeance', 'A churning storm cloud forms, centered on a point you can see and spreading to a radius of 360 feet. Lightning flashes in the area, thunder booms, and strong winds roar. Each creature under the cloud (no more than 5,000 feet beneath the cloud) when it appears must make a Constitution saving throw. On a failed save, a creature takes 2d6 thunder damage and becomes deafened for 5 minutes.<br> Each round you maintain concentration on this spell, the storm produces additional effects on your turn.<br> <b>Round 2</b>: Acidic rain falls from the cloud. Each creature and object under the cloud takes 1d6 acid damage.<br> <b>Round 3</b>: You call six bolts of lightning from the cloud to strike six creatures or objects of your choice beneath the cloud. A given creature or object can''t be struck by more than one bolt. A struck creature must make a Dexterity saving throw. The creature takes 10d6 lightning damage on a failed save, or half as much damage on a successful one.<br> <b>Round 4</b>: Hailstones rain down from the cloud. Each creature under the cloud takes 2d6 bludgeoning damage.<br> <b>Round 5-10</b>: Gusts and freezing rain assail the area under the cloud. the area becomes difficult terrain and is heavily obscured. Each creature there takes 1d6 cold damage. Ranged weapon attacks in the area are impossible. The wind and rain count as a severe distraction for the purposes of maintaining concentration on spells. Finally, gusts of strong wind (ranging from 20 to 50 miles per hour) automatically disperse fog, mists, and similar phenomena in the area whether mundane or magical.', '', 'Sight', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('53ba82ce-9840-4fbf-9059-08f0ae382471', 'Branding Smite', 'The next time you hit a creature with a weapon attack before this spell ends, the weapon gleams with astral radiance as you strike. The attack deals an extra 2d6 radiant damage to the target, which becomes visible if it is invisible, and the target sheds dim light in a 5-foot radius and can''t become invisible until the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the extra damage increases by 1d6 for each slot level above 2nd.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f41ad2e2-c18a-43ff-b744-0c16da6382ac', 'Find Steed', 'You summon a spirit that assumes the form of an unusually intelligent, strong, and loyal steed, creating a long-lasting bond with it. Appearing in an unoccupied space within range, the steed takes on a form that you choose, such as a warhorse, a pony, a camel, an elk, or a mastiff. (Your DM might allow other animals to be summoned as steeds.) The steed has the statistics of the chosen form, though it is a celestial, fey, or fiend (your choice) instead of its normal type. Additionally, if your steed has an Intelligence of 5 or less, its Intelligence becomes 6, and it gains the ability to understand one language of your choice that you speak.<br> Your steed serves you as a mount, both in combat and out, and you have an instinctive bond with it that allows you to fight as a seamless unit. While mounted on your steed, you can make any spell you cast that targets only you also target your steed.<br> When the steed drops to 0 hit points, it disappears, leaving behind no physical form. You can also dismiss your steed at any time as an action, causing it to disappear. In either case, casting this spell again summons the same steed, restored to its hit point maximum.<br> While your steed is within 1 mile of you, you can communicate with it telepathically.<br> You can''t have more than one steed bonded by this spell at a time. As an action, you can release the steed from its bond at any time, causing it to disappear.', '', '30 feet', 'VS', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('98e7c6f4-9b72-43c8-b36f-543a2f97868f', 'Magic Weapon', 'You touch a nonmagical weapon. Until the spell ends, that weapon becomes a magic weapon with a +1 bonus to attack rolls and damage rolls.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the bonus increases to +2. When you use a spell slot of 6th level or higher, the bonus increases to +3.', '', 'Touch', 'VS', NULL, '1 bonus action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e82b0392-15d4-47f8-bf72-bc455d744996', 'Alarm', 'You set an alarm against unwanted intrusion. Choose a door, a window, or an area within range that is no larger than a 20-foot cube. Until the spell ends, an alarm alerts you whenever a tiny or larger creature touches or enters the warded area. When you cast the spell, you can designate creatures that won''t set off the alarm. You also choose whether the alarm is mental or audible.<br> A mental alarm alerts you with a ping in your mind if you are within 1 mile of the warded area. This ping awakens you if you are sleeping.<br> An audible alarm produces the sound of a hand bell for 10 seconds within 60 feet.', 'a tiny bell and a piece of fine silver wire', '30 feet', 'VSM', NULL, '1 minute', true, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c1c19aee-c428-4356-bea2-84691f2e1e1f', 'Mind Spike', 'You reach into the mind of one creature you can see within range. The target must make a Wisdom saving throw, taking 3d8 psychic damage on a failed save, or half as much damage on a successful one. On a failed save, you also always know the target''s location until the spell ends, but only while the two of you are on the same plane of existence. While you have this knowledge, the target can''t become hidden from you, and if it''s invisible, it gains no benefit from that condition against you.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, the damage increases by 1d6 for each slot level above 2nd.', '', '60 feet', 'S', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e7dd3fdf-3f59-4aab-94f8-3c5833adb7bd', 'Fire Bolt', 'You hurl a mote of fire at a creature or object within range. Make a ranged spell attack against the target. On a hit, the target takes 1d10 fire damage. A flammable object hit by this spell ignites if it isn''t being worn or carried.<br> This spell''s damage increases by 1d10 when you reach 5th level (2d10), 11th level (3d10), and 17th level (4d10).', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3a6a340b-73dc-49b3-8304-e74577dc2538', 'Maelstrom', 'A swirling mass of 5-foot-deep water appears in a 30-foot radius centered on a point you can see within range. The point must be on ground or in a body of water. Until the spell ends, that area is difficult terrain, and any creature that starts its turn there must succeed on a Strength saving throw or take 6d6 bludgeoning damage and be pulled 10 feet toward the center.', 'paper or leaf in the shape of a funnel', '120 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a129e1ac-f331-4eba-acd3-e2b3d0dc2a5e', 'Shadow Blade', 'You weave together threads of shadow to create a sword of solidified gloom in your hand. This magic sword lasts until the spell ends. It counts as a simple melee weapon with which you are proficient. It deals 2d8 psychic damage on a hit and has the finesse, light, and thrown properties (range 20/60). In addition, when you use the sword to attack a target that is in dim light or darkness, you make the attack roll with advantage.<br> If you drop the weapon or throw it, it dissipates at the end of the turn. Thereafter, while the spell persists, you can use a bonus action to cause the sword to reappear in your hand.<br> <b>At Higher Levels</b>: When you cast this spell using a 3rd- or 4th-level spell slot, the damage increases to 3d8. When you cast it using a 5th- or 6th-level spell slot, the damage increases to 4d8. When you cast it using a spell slot of 7th level or higher, the damage increases to 5d8.', '', 'Self', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c5a04f38-7b8b-4d0c-8f72-8d8d855c9083', 'Ray of Frost', 'A frigid beam of blue-white light streaks toward a creature within range. Make a ranged spell attack against the target. On a hit, it takes 1d8 cold damage, and its speed is reduced by 10 feet until the start of your next turn.<br> The spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f6755f47-317f-4d51-8ad8-9bff91639e57', 'Flame Arrows', 'You touch a quiver containing arrows or bolts. When a target is hit by a ranged weapon attack using a piece of ammunition drawn from the quiver, the target takes an extra 1d6 fire damage. The spell''s magic ends on the piece of ammunition when it hits or misses, and the spell ends when twelve pieces of ammunition have been drawn from the quiver.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the number of pieces of ammunition you can affect with this spell increases by two for each slot level above 3rd.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f210a01d-6660-458a-be68-d88522992021', 'Shocking Grasp', 'Lightning springs from your hand to deliver a shock to a creature you try to touch. Make a melee spell attack against the target. You have advantage on the attack roll if the target is wearing armor made of metal. On a hit, the target takes 1d8 lightning damage, and it can''t take reactions until the start of its next turn.<br> The spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5d131f91-d505-4e6a-8e00-fdf04934094f', 'Rary''s Telepathic Bond', 'You forge a telepathic link among up to eight willing creatures of your choice within range, psychically linking each creature to all the others for the duration. Creatures with Intelligence scores of 2 or less aren''t affected by this spell.<br> Until the spell ends, the targets can communicate telepathically through the bond whether or not they have a common language. The communication is possible over any distance, though it can''t extend to other planes of existence.', 'pieces of eggshell from two different kinds of creatures', '30 feet', 'VSM', NULL, '1 action', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7d43f2d8-7600-46e5-9e94-67d6919a2c89', 'Phantasmal Force', 'You craft an illusion that takes root in the mind of a creature that you can see within range. The target must make an Intelligence saving throw. On a failed save, you create a phantasmal object, creature, or other visible phenomenon of your choice that is no larger than a 10-foot cube and that is perceivable only to the target for the duration. This spell has no effect on undead or constructs.<br> The phantasm includes sound, temperature, and other stimuli, also evident only to the creature.<br> The target can use its action to examine the phantasm with an Intelligence (Investigation) check against your spell save DC. If the check succeeds, the target realizes that the phantasm is an illusion, and the spell ends.<br> While a target is affected by the spell, the target treats the phantasm as if it were real. The target rationalizes any illogical outcomes from interacting with the phantasm. For example, a target attempting to walk across a phantasmal bridge that spans a chasm falls once it steps onto the bridge. If the target survives the fall, it still believes that the bridge exists and comes up with some other explanation for its fall - it was pushed, it slipped, or a strong wind might have knocked it off.<br> An affected target is so convinced of the phantasm''s reality that it can even take damage from the illusion. A phantasm created to appear as a creature can attack the target. Similarly, a phantasm created to appear as fire, a pool of acid, or lava can burn the target. Each round on your turn, the phantasm can deal 1d6 psychic damage to the target if it is in the phantasm''s area or within 5 feet of the phantasm, provided that the illusion is of a creature or hazard that could logically deal damage, such as by attacking. The target perceives the damage as a type appropriate to the illusion.', 'A bit of fleece', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('adc6698e-5a00-436f-b18c-644c5411bba7', 'Burning Hands', 'As you hold your hands with thumbs touching and fingers spread, a thin sheet of flames shoots forth from your outstretched fingertips. Each creature in a 15-foot cone must make a Dexterity saving throw. A creature takes 3d6 fire damage on a failed save, or half as much damage on a successful one.<br> The fire ignites any flammable objects in the area that aren''t being worn or carried.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d6 for each slot level above 1st.', '', 'Self (15-foot cone)', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c221093b-35cb-47f3-945d-a538efc0c881', 'Color Spray', 'A dazzling array of flashing, colored light springs from your hand. Roll 6d10, the total is how many hit points of creatures this spell can effect. Creatures in a 15-foot cone originating from you are affected in ascending order of their current hit points (ignoring unconscious creatures and creatures that can''t see).<br> Starting with the creature that has the lowest current hit points, each creature affected by this spell is blinded until the spell ends. Subtract each creature''s hit points from the total before moving on to the creature with the next lowest hit points. A creature''s hit points must be equal to or less than the remaining total for the creature to be affected.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, roll an additional 2d10 for each slot level above 1st.', 'a pinch of powder or sand that is colored red, yellow, and blue', 'Self (15-foot cone)', 'VSM', NULL, '1 action', false, '1 round', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('04b3b518-fd04-46c7-9f19-92efd3703b55', 'Pyrotechnics', 'Choose an area of nonmagical flame that you can see and that can fit within a 5-foot cube within range. You can extinguish the fire in that area, and you create either fireworks or smoke when you do so.<br> <b>Fireworks</b>: The target explodes with a dazzling display of colors. Each creature within 10 feet of the target must succeed on a Constitution saving throw or become blinded until the end of your next turn.<br> <b>Smoke</b>: Thick black smoke spreads out from the target in a 20-foot radius, moving around corners. The area of the smoke is heavily obscured. The smoke persists for 1 minute or until a strong wind disperses it.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9c80dfe4-f3ef-44b8-afa6-b2cbaaf61284', 'False Life', 'Bolstering yourself with a necromantic facsimile of life, you gain 1d4+4 temporary hit points for the duration.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, you gain 5 additional temporary hit points for each slot level above 1st.', 'a small amount of alcohol or distilled spirits', 'Self', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('91e25f90-6b56-4170-8e97-5f6fba0a3521', 'Mage Armor', 'You touch a willing creature who isn''t wearing armor, and a protective magical force surrounds it until the spell ends. The target''s base AC becomes 13 + its Dexterity modifier. The spell ends it if the target dons armor or if you dismiss the spell as an action.', 'a piece of cured leather', 'Touch', 'VSM', NULL, '1 action', false, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('03d6459b-9751-4c0e-80fc-fe61c1bedb91', 'Magic Missile', 'You create three glowing darts of magical force. Each dart hits a creature of your choice that you can see within range. A dart deals 1d4+1 force damage to its target. The darts all strike simultaneously and you can direct them to hit one creature or several.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the spell creates one more dart for each slot above 1st.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('890d323c-d315-4c08-9294-ba6861316c2c', 'Shield', 'An invisible barrier of magical force appears and protects you. Until the start of your next turn, you have a +5 bonus to AC, including against the triggering attack, and you take no damage from <i>magic missile</i>.', '', 'Self', 'VS', NULL, '1 reaction', false, '1 round', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ff0088ea-6ff4-442c-8142-94bdc84870bc', 'Alter Self', 'You assume a different form. When you cast the spell, choose one of the following options, the effects of which last for the duration of the spell. While the spell lasts, you can end one option as an action to gain the benefits of a different one.<br> <b>Aquatic Adaptation</b>: You adapt your body to an aquatic environment, sprouting gills, and growing webbing between your fingers. You can breathe underwater and gain a swimming speed equal to your walking speed.<br> <b>Change Appearance</b>: You transform your appearance. You decide what you look like, including your height, weight, facial features, sound of your voice, hair length, coloration, and distinguishing characteristics, if any. You can make yourself appear as a member of another race, though none of your statistics change. You also don''t appear as a creature of a different size than you, and your basic shape stays the same, if you''re bipedal, you can''t use this spell to become quadrupedal, for instance. At any time for the duration of the spell, you can use your action to change your appearance in this way again.<br> <b>Natural Weapons</b>: You grow claws, fangs, spines, horns, or a different natural weapon of your choice. Your unarmed strikes deal 1d6 bludgeoning, piercing, or slashing damage, as appropriate to the natural weapon you chose, and you are proficient with you unarmed strikes. Finally, the natural weapon is magic and you have a +1 bonus to the attack and damage rolls you make using it.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8b7def02-8524-492f-bfdd-b6b40afcb0c8', 'Blur', 'Your body becomes blurred, shifting and wavering to all who can see you. For the duration, any creature has disadvantage on attack rolls against you. An attacker is immune to this effect if it doesn''t rely on sight, as with blindsight, or can see through illusions, as with truesight.', '', 'Self', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4f783057-b947-4304-b910-45bbaf5b55bf', 'Darkness', 'Magical darkness spreads from a point you choose within range to fill a 15-foot radius sphere for the duration. The darkness spreads around corners. A creature with darkvision can''t see through this darkness, and nonmagical light can''t illuminate it.<br> If the point you choose is on an object you are holding or one that isn''t being worn or carried, the darkness emanates from the object and moves with it. Completely covering the source of the darkness with an opaque object, such as a bowl or a helm, blocks the darkness.<br> If any of this spell''s area overlaps with an area of light created by a spell of or lower, the spell that created the light is dispelled.', 'bat fur and a drop of pitch or piece of coal', '60 feet', 'VM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1cd6a0a6-11ba-4740-9186-f03167c21844', 'Summon Beast', 'You call forth a bestial spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Bestial Spirit stat block. When you cast the spell, choose an environment: Air, Land, or Water. The creature resembles an animal of your choice that is native to the chosen environment, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, use the higher level where the spell''s level appears in the stat block.', 'a feather, tuft of fur, and fish tail inside a gilded acorn worth at least 200 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('aae7a1cb-84be-451b-a37c-3f524ec66fbd', 'Aid', 'Your spell bolsters your allies with toughness and resolve. Choose up to three creatures within range. Each target''s hit point maximum and current hit points increase by 5 for the duration.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd level or higher, a target''s hit points increase by an additional 5 for each slot level above 2nd.', 'a tiny strip of white cloth', '30 feet', 'VSM', NULL, '1 action', false, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('928c06df-8ab2-4b33-9c61-7a68cb634b48', 'Light', 'You touch 1 object that is no larger than 10 feet in any dimension. Until the spell ends, the object sheds bright light in a 20-foot radius and dim light for an additional 20 feet. The light can be colored as you like. Completely covering the object with something opaque blocks the light. The spell ends if you cast it again or dismiss it as an action.<br> If you target an object held or worn by a hostile creature, that creature must succeed on a Dexterity saving throw to avoid the spell.', 'A firefly or phosphorescent moss', 'Touch', 'VM', NULL, '1 action', false, '1 hour', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ccc63d0a-4090-40b4-9277-56e9649fc874', 'Mage Hand', 'A spectral, floating hand appears at a point you choose within range. The hand lasts for the duration or until you dismiss it as an action. The hand vanishes if it is ever more than 30 feet away from you or if you cast this spell again.<br> You can use your action to control the hand. You can use the hand to manipulate an object, open an unlocked door or container, stow or retrieve an item from an open container, or pour the contents out of a vial. You can move the hand up to 30 feet each time you use it.<br> The hand can''t attack, activate magical items, or carry more than 10 pounds.', '', '30 feet', 'VS', NULL, '1 action', false, '1 minute', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f5b91f52-e72c-407d-92d4-198480008f38', 'Mending', 'This spell repairs a single break or tear in an object you touch, such as broken chain link, 2 halves of a broken key, a torn clack, or a leaking wineskin. As long as the break or tear is no larger than 1 foot in any dimension, you mend it, leaving no trace of the former damage.<br> This spell can physically repair a magic item or construct, but the spell can''t restore magic to such an object.', '2 lodestones', 'Touch', 'VSM', NULL, '1 minute', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b5e9a2bd-1f0e-4ee3-80d3-e9b44871f3ae', 'Message', 'You point your finger toward a creature within range and whisper a message. The target (and only the target) hears the message and can reply in a whisper that only you can hear.<br> You can cast this spell through solid objects if you are familiar with the target and know it is beyond the barrier. Magical silence, 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood blocks the spell. The spell doesn''t have to follow a straight line and can travel freely around corners or through openings.', 'a short piece of copper wire', '120 feet', 'VSM', NULL, '1 action', false, '1 round', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('aa9cb938-b145-4afd-893f-c74afbd1da35', 'Enlarge/Reduce', 'You cause a creature or an object you can see within range to grow larger or smaller for the duration. Choose either a creature or an object that is neither worn nor carried. If the target is unwilling, it can make a Constitution saving throw. On a success, the spell has no effect.<br> If the target is a creature, everything it is wearing and carrying changes size with it. Any item dropped by an affected creature returns to normal size at once.<br> <b>Enlarge</b>. The target''s size doubles in all dimensions, and its weight is multiplied by eight. This growth increases its size by one category - from Medium to Large, for example. If there isn''t enough room for the target to double its size, the creature or object attains the maximum possible size in the space available. Until the spell ends, the target also has advantage on Strength checks and Strength saving throws. The target''s weapons also grow to match its new size. While these weapons are enlarged, the target''s attack with them deal 1d4 extra damage.<br> <b>Reduce</b>. The target''s size is halved in all dimensions, and its weight is reduced to one-eighth of normal. This reduction decreases its size by one category - from Medium to Small, for example. Until the spell ends, the target also has disadvantage on Strength checks and Strength saving throws. The target''s weapons also shrink to match its new size. While these weapons are reduced, the target''s attacks with them deal 1d4 less damage (this can''t reduce the damage below 1).', 'a pinch of powdered iron', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b59a874b-3eb3-4aae-8689-b4492776032c', 'Shillelagh', 'The wood of a club or quarterstaff you are holding is imbued with nature''s power. For the duration, you can use your spellcasting ability instead of Strength for the attack and damage rolls of melee attacks using that weapon, and the weapon''s damage die becomes a d8. The weapon also becomes magical, if it isn''t already. The spell ends if you cast it again or if you let go of the weapon.', '', 'Touch', 'VSM', NULL, '1 bonus action', false, '1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3cc053c2-aae0-4ce3-bdfd-ef9a39bd48e1', 'Power Word Kill', 'You utter a word of power that can compel one creature you can see within range to die instantly. If the creature you chose has 100 hit points or fewer, it dies. Otherwise, the spell has no effect.', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4d137f07-7f45-4593-b04b-563708e52b39', 'See Invisibility', 'For the duration, you see invisible creatures and objects as if they were visible, and you can see into the Ethereal Plane. Ethereal creatures and objects appear ghostly and translucent.', 'A pinch of Talc and a small sprinkling of powdered silver', 'Self', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3aafb2ca-4686-4cc5-99d8-10e1974cd8e4', 'Tasha''s Otherworldly Guise', 'Uttering an incantation, you draw on the magic of the Lower Planes or Upper Planes (your choice) to transform yourself. You gain the following benefits until the spell ends:<br>-You are immune to fire and poison damage (Lower Planes) or radiant and necrotic damage (Upper Planes).<br>-You are immune to the poisoned condition (Lower Planes) or the charmed condition (Upper Planes).<br>-Spectral wings appear on your back, giving you a flying speed of 40 feet.<br>-You have a +2 bonus to AC.<br>-All your weapon attacks are magical, and when you make a weapon attack, you can use your spell casting ability modifier instead of Strength or Dexterity for the attack and damage rolls.<br>-You can attack twice, instead of once, when you take the Attack action on your turn. You ignore this benefit if you already have a feature, like Extra Attack, that lets you attack more than once when you take the Attack action on your turn.', 'an object engraved with a symbol of the Outer Planes, worth at least 500 gp', 'Self', 'VSM', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b3715106-09f8-4fbf-8dfe-17f82958c2f2', 'Drawmij''s Instant Summons', 'You touch an object weighing 10 pounds or less whose longest dimension is 6 feet or less. The spell leaves an invisible mark on its surface and invisibly inscribes the name of the item on the sapphire you use as the material component. Each time you cast this spell, you must use a different sapphire.<br> At any time thereafter, you can use your action to speak the item''s name and crush the sapphire. The item instantly appears in your hand regardless of physical or planar distances, and the spell ends.<br> If another creature is holding or carrying the item, crushing the sapphire doesn''t transport the item to you, but instead you learn who the creature possessing the object is and roughly where that creature is located at that moment.<br> <i>Dispel magic</i> or a similar effect successfully applied to the sapphire ends this spell''s effect.', 'a sapphire worth 1,000 gp', 'Touch', 'VSMgp', NULL, '1 minute', true, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b582192e-b2cc-4486-8148-5129015628f5', 'Nondetection', 'For the duration, you hide a target that you touch from divination magic. The target can be a willing creature or a place or an object no larger than 10 feet in any dimension. The target can''t be targeted by any divination magic or perceived through magical scrying sensors.', 'a pinch of diamond dust worth 25 gp sprinkled over the target, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, '8 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8be2b509-a988-4aa9-9d65-9ee129dfa151', 'Confusion', 'This spell assaults and twists creatures'' minds, spawning delusions and provoking uncontrolled actions. Each creature in a 10-foot-radius sphere centered on a point you choose within range must succeed on a Wisdom saving throw when you cast this spell or be affected by it.<br> An affected target can''t take reactions and must roll a d10 at the start of each of its turns to determine its behavior for that turn.<br> 1: The creature uses all its movement to move in a random direction. To determine the direction, roll a d8 and assign a direction to each die face. The creature doesn''t take an action this turn.<br> 2-6: The creature doesn''t move or take actions this turn.<br> 7-8: The creature uses its action to make a melee attack against a randomly determined creature within its reach. If there is no creature within its reach, the creature does nothing this turn.<br> 9-10: The creature can act and move normally. At the end of its turns, an affected target can make a Wisdom saving throw. If it succeeds, this effect ends for that target.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the radius of the sphere increases by 5 feet for each slot above 4th.', 'three nut shells', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('98f11293-98d1-443f-8f34-388dd87bd900', 'Otto''s Irresistible Dance', 'Choose one creature that you can see within range. The target begins a comic dance in place - shuffling, tapping its feet, and capering for the duration. Creatures that can''t be charmed are immune to this spell.<br> A dancing creature must use all its movement to dance without leaving its space and has disadvantage on Dexterity saving throws and attack rolls. While the target is affected by this spell, other creatures have advantage on attack rolls against it. As an action, a dancing creature makes a Wisdom saving throw to regain control of itself. On a successful save, the spell ends.', '', '30 feet', 'V', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b1e3a652-1918-4c9d-b1c8-4f3105f94757', 'Otiluke''s Freezing Sphere', 'A frigid globe of cold energy streaks from your fingertips to a point of your choice within range, where it explodes in a 60 foot radius sphere. Each creature within the area must make a Constitution saving throw. On a failed save, a creature takes 10d6 cold damage. On a successful save, it takes half as much damage.<br>If the globe strikes a body of water or a liquid that is principally water (not including water based creatures), it freezes the liquid to a depth of 6 inches over an area 30 feet square. This ice lasts for 1 minute. Creatures that were swimming on the surface of frozen water are trapped in the ice. A trapped creature can use an action to make a Strength check against your spell save DC to break free.<br>You can refrain from firing the globe after completing the spell, if you wish. A small globe about the size of a sling stone, cool to the touch, appears in your hand. At any time, you or a creature you give the globe to can throw the globe (to a range of 40 feet) or hurl it with a sling (to the sling''s normal range). It shatters on impact, with the same effect as the normal casting of the spell. You can also set the globe down without shattering it. After 1 minute, if the globe hasn''t already shattered, it explodes.<br>At Higher Levels. When you cast this spell using a spell slot of 7th level or higher, the damage increases by 1d6 for each slot level above 6th.', '', '300 feet', 'VSM', NULL, '1 action', false, 'up to 2 hours', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7374fe24-b058-44a0-881b-4193d986e986', 'Awaken', 'After spending the casting time tracing magical pathways within a precious gemstone, you touch a huge or smaller beast or plant. The target must have either no Intelligence score or an Intelligence of 3 or less. The target gains an Intelligence of 10. The target also gains the ability to speak 1 language you know. If the target is a plant, it gains the ability to move its limbs, roots, vines, creepers, and so forth, and it gains senses similar to a human''s. Your DM chooses statistics appropriate for the awakened plant, such as the statistics for the awakened shrub or the awakened tree.<br> The awakened beast or plant is charmed by you for 30 days or until you and your companions do anything harmful to it. When the charmed condition ends, the awakened creature chooses whether to remain friendly to you, based on how you treated it while it was charmed.', 'An agate worth at least 1,000 gp, Consumed', 'Touch', 'VSMgp', NULL, '8 hours', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('da27c2f8-eab8-4bd3-8d30-1ef2f241a3c6', 'Tenser''s Transformation', 'You endow yourself with endurance and martial prowess fueled by magic. Until the spell ends , you can''t cast spells, and you gain the following benefits:<br>  You gain 50 temporary hit points. If any of these remain when the spell ends, they are lost.<br>  You have advantage on attack rolls that you make with simple and martial weapons.<br>  When you hit a target with a weapon attack, that target takes an extra 2d12 force damage.<br>  You have proficiency with all armor, shields, simple weapons, and martial weapons.<br>  You have proficiency in Strength and Constitution saving throws.<br>  You can attack twice, instead of once, when you take the Attack action on your turn. You ignore this benefit if you already have a feature, like Extra Attack, that gives you extra attacks.<br> Immediately after the spell ends, you must succeed on a DC 15 Constitution saving throw or suffer one level of exhaustion.', 'a few hairs from a bull', 'Self', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('40ee8272-1a55-4065-84f6-589376234080', 'Mordenkainen''s Sword', 'You create a sword-shaped plane of force that hovers within range. It lasts for the duration.<br> When the sword appears, you make a melee spell attack against a target of your choice within 5 feet of the sword. On a hit. the target takes 3d10 force damage. Until the spell ends, you can use a bonus action on each of your turns to move the sword up to 20 feet to a spot you can see and repeat this attack against the same target or a different one.', 'A miniature platinum sword with a grip and pommel of copper and zinc, worth 250 gp', '60 feet', 'VSMgp', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('426cb43d-d02c-4956-a2c4-47f2ee6a2318', 'Mordenkainen''s Magnificent Mansion', 'You conjure an extradimensional dwelling in range that lasts for the duration. You choose where its one entrance is located. The entrance shimmers faintly and is 5 feet wide and 10 feet tall. You and any creature you designate when you cast the spell can enter the extradimensional dwelling as long as the portal remains open. You can open or close the portal if you are within 30 feet of it. While closed, the portal is invisible.<br> Beyond the portal is a magnificent foyer with numerous chambers beyond. The atmosphere is clean, fresh, and warm.<br> You can create any floor plan you like, but the space can''t exceed 50 cubes, each cube being 10 feet on each side. The place is furnished and decorated as you choose. It contains sufficient food to serve a nine course banquet for up to 100 people. A staff of 100 near-transparent servants attends all who enter. You decide the visual appearance of these servants and their attire. They are completely obedient to your orders. Each servant can perform any task a normal human servant could perform, but they can''t attack or take any action that would directly harm another creature. Thus the servants can fetch things, clean, mend, fold clothes, light fires, serve food, pour wine, and so on. The servants can go anywhere in the mansion but can''t leave it. Furnishings and other objects created by this spell dissipate into smoke if removed from the mansion. When the spell ends, any creatures inside the extradimensional space are expelled into the open spaces nearest to the entrance.', 'a miniature portal carved from ivory, a small piece of polished marble, and a tiny silver spoon, each item worth at least 5 gp', '300 feet', 'VSMgp', NULL, '1 minute', false, '24 hours', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9a6a600b-0fc9-48b8-92f0-31ee94736cac', 'Mind Blank', 'Until the spell ends, one willing creature you touch is immune to psychic damage, any effect that would sense its emotions or read its thoughts, divination spells, and the charmed condition. The spell even foils <i>wish</i> spells and spells or effects of similar power used to affect the target''s mind or to gain information about the target.', '', 'Touch', 'VS', NULL, '1 action', false, '24 hours', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('604ee489-69b4-4a17-8264-225dd5696b7e', 'Resistance', 'You touch one willing creature. Once before the spell ends, the target can roll a d4 and add the number rolled to one saving throw of its choice. It can roll the die before or after the saving throw. The spell then ends.', 'a miniature cloak', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('179542b4-f896-4be3-ac9f-be253245de3a', 'Abi-Dalzim''s Horrid Wilting', 'You draw the moisture from every creature in a 30-foot cube centered on a point you choose within range. Each creature in that area must make a Constitution saving throw. Constructs and undead aren''t affected, and plants and water elementals make this saving throw with disadvantage. A creature takes 12d8 necrotic damage on a failed save, or half as much damage on a successful one.<br> Nonmagical plants in the area that aren''t creatures, such as trees and shrubs, wither and die instantly.', 'a bit of sponge', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f1b16e96-fcf6-4979-93d0-f3803bdf0510', 'Inflict Wounds', 'Make a melee spell attack against a creature you can reach. On a hit, the target takes 3d10 necrotic damage.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d10 for each slot level above 1st.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ee806467-c1ee-48ed-a44a-0472a0b324d5', 'Beacon of Hope', 'This spell bestows hope and vitality. Choose any number of creatures within range. For the duration, each target has advantage on Wisdom saving throws and death saving throws, and regains the maximum number of hit points possible from any healing.', '', '30 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8890dbb4-32e1-489b-b2fa-83309923f961', 'Water Walk', 'This spell grants the ability to move across any liquid surface - such as water, acid, mud, snow, quicksand, or lava - as if it were harmless solid ground (creatures crossing molten lava can still take damage from the heat). Up to ten willing creatures you can see within range gain this ability for the duration.<br> If your target a creature submerged in a liquid, the spell carries the target to the surface of the liquid at a rate of 60 feet per round.', 'a piece of cork', '30 feet', 'VSM', NULL, '1 action', true, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f51bb72c-7665-4e88-8d34-9b603bf2b4ca', 'Insect Plague', 'Swarming, biting locusts fill a 20-foot-radius sphere centered on a point you choose within range. The sphere spreads around corners. The sphere remains for the duration, and its area is lightly obscured. The sphere''s area is difficult terrain.<br> When the area appears, each creature in it must make a Constitution saving throw. A creature takes 4d10 piercing damage on a failed save, or half as much damage on a successful one. A creature must also make this saving throw when it enters the spell''s area for the first time on a turn or ends its turn there.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage increases by 1d10 for each slot level above 5th.', 'a few grains of sugar, some kernels of grain, and a smear of fat', '300 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('36ec2e24-e47e-4cb6-83cd-0fbbd0f63cdc', 'Blade Ward', 'You extend your hand and trace a sigil of warding in the air. Until the end of your next turn, you have resistance against bludgeoning, piercing, and slashing damage dealt by weapon attacks.', '', 'Self', 'VS', NULL, '1 action', false, '1 round', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7a4f9b6a-699b-4b33-b6f2-b2d22aa90aaa', 'Booming Blade', 'You brandish the weapon used in the spell''s casting and make a melee attack with it against one creature within 5 feet of you. On a hit, the target suffers the weapon attack''s normal effects and then becomes sheathed in booming energy until the start of your next turn. If the target willingly moves 5 feet or more before then, the target takes 1d8 thunder damage, and the spell ends.<br>At Higher Levels. At 5th level, the melee attack deals an extra 1d8 thunder damage to the target on a hit, and the damage the target takes for moving increases to 2d8. Both damage rolls increase by 1d8 at 11th level (2d8 and 3d8) and again at 17th level (3d8 and 4d8).', 'a melee weapon worth at least 1 sp', 'Self (5-foot radius)', 'VM', NULL, '1 action', false, '1 round', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9bcaadae-c441-40f4-8af8-6ca90818e067', 'Create Bonfire', 'You create a bonfire on ground that you can see within range. Until the spells ends, the magic bonfire fills a 5-foot cube. Any creature in the bonfire''s space when you cast the spell must succeed on a Dexterity saving throw or take 1d8 fire damage. A creature must also make the saving throw when it enters the bonfire''s space for the first time on a turn or ends its turn there.<br> The bonfire ignites flammable objects in its area that aren''t being worn or carried.<br> The spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('26570e85-5a74-487f-aac3-e161e7da4ccf', 'Conjure Celestial', 'You summon a celestial of challenge rating 4 or lower, which appears in an unoccupied space that you can see within range. The celestial disappears when it drops to 0 hit points or when the spell ends.<br> The celestial is friendly to you and your companions for the duration. Roll initiative for the celestial, which has its own turns. It obeys any verbal commands that you issue to it (no action required by you), as long as they don''t violate its alignment. If you don''t issue any commands to the celestial, it defends itself from hostile creatures but otherwise takes no actions.<br> The DM has the celestial''s statistics.<br> <b>At Higher Levels</b>: When you cast this spell using a 9th-level spell slot, you summon a celestial of challenge rating 5 or lower.', '', '90 feet', 'VS', NULL, '1 minute', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('dfda33d8-ae02-4a50-bf4c-13cebd7aaacf', 'True Strike', 'You extend your hand and point a finger at a target in range. Your magic grants you a brief insight into the target''s defenses. On your next turn, you gain advantage on your first attack roll against the target, provided that this spell hasn''t ended.', '', '30 feet', 'S', NULL, '1 action', false, 'up to 1 round', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8f01f9b2-b6e5-4f8a-b315-1f478f3632d3', 'Thunderclap', 'You create a burst of thunderous sound that can be heard up to 100 feet away. Each creature within range, other than you, must make a Constitution saving throw or take 1d6 thunder damage.<br> The spell''s damage increases by 1d6 when you reach 5th level (2d6), 11th level (3d6), and 17th level (4d6).', '', '5 feet', 'S', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e6bdd45f-18af-49cc-b13e-6a056bd6dad1', 'Produce Flame', 'A flickering flame appears in your hand. The flame remains there for the duration and harms neither you nor your equipment. The flame sheds bright light in a 10-foot radius and dim light for an additional 10 feet. The spell ends if you dismiss it as an action or if you cast it again.<br> You can also attack with the flame, although doing so ends the spell. When you cast this spell, or as an action on a later turn, you can hurl the flame at a creature within 30 feet of you. Make a ranged spell attack. On a hit, the target takes 1d8 fire damage.<br> This spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', 'Self', 'VS', NULL, '1 action', false, '10 minutes', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a5edc35f-09a0-4d5e-94a3-75b29d9fd2e4', 'Gust of Wind', 'A line of strong wind 60 feet long and 10 feet wide blasts from you in a direction you choose for the spell''s duration. Each creature that starts its turn in the line must succeed on a Strength saving throw or be pushed 15 feet away from you in a direction following the line.<br> Any creature in the line must spend 2 feet of movement for every 1 foot it moves when moving closer to you.<br> The gust disperses gas or vapor, and it extinguishes candles, torches, and similar unprotected flames in the area. It causes protected flames, such as those of lanterns, to dance wildly and has a 50 percent chance to extinguish them.<br> As a bonus action on each of your turns before the spell ends, you can change the direction in which the line blasts from you.', 'a legume seed', 'Self (60-foot line)', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('65bda407-7bfd-4bdd-bc10-64eae3c5414e', 'Sleet Storm', 'Until the spell ends, freezing rain and sleet fall in a 20-foot-tall cylinder with a 40-foot radius centered on a point you choose within range. The area is heavily obscured, and exposed flames in the area are doused.<br> The ground in the area is covered with slick ice, making it difficult terrain. When a creature enters the spell''s area for the first time on a turn or starts its turn there, it must make a Dexterity saving throw. On a failed save, it falls prone.<br> If a creature is concentrating in the spell''s area, the creature must make a successful Constitution saving throw against your spell save DC or lose concentration.', 'a pinch of dust and a few drops of water', '150 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f6852bff-2fd6-4192-b44c-71588e00afb1', 'Thunderous Smite', 'The first time you hit with a melee weapon attack during this spell''s duration, your weapon rings with thunder that is audible within 300 feet of you, and the attack deals an extra 2d6 thunder damage to the target. Additionally, if the target is a creature, it must succeed on a Strength saving throw or be pushed 10 feet away from you and knocked prone.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6a99f5b3-afdc-48e8-b84b-ef7ccf63f046', 'Stoneskin', 'This spell turns the flesh of a willing creature you touch as hard as stone. Until the spell ends, the target has resistance to nonmagical bludgeoning, piercing, and slashing damage.', 'diamond dust worth 100 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('79c058aa-96f3-4ed8-b46a-69104d88ebe5', 'Warding Wind', 'A strong wind (20 miles per hour) blows around you in a 10-foot radius and moves with you, remaining centered on you. The wind lasts for the spell''s duration.<br> The wind has the following effects:<br>  It deafens you and other creatures in its area.<br>  It extinguishes unprotected flames in its area that are torch-sized or smaller.<br>  It hedges out vapor, gas, and fog that can be dispersed by strong wind.<br>  The area is difficult terrain for creatures other than you.<br>  The attack rolls of ranged weapon attacks have disadvantage if the attacks pass in or out of the wind.', '', 'Self', 'V', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0da0206f-21e8-49f5-a857-c55bfd82defb', 'Sunbeam', 'A beam of brilliant light flashes out from your hand in a 5-foot-wide, 60-foot-line. Each creature in the line must make a Constitution saving throw. On a failed save, a creature takes 6d8 radiant damage and is blinded until your next turn. On a successful save, it takes half as much damage and isn''t blinded by this spell. Undead and oozes have disadvantage on this saving throw.<br> You can create a new line of radiance as your action on any turn until the spell ends.<br> For the duration, a mote of brilliant radiance shines in your hand. It sheds bright light in a 30-foot radius and dim light for an additional 30 feet. The light is sunlight.', 'a magnifying glass', 'Self (60-foot line)', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9676f5f8-48fa-4f28-9e72-a1a3f14cf748', 'Wall of Sand', 'You create a wall of swirling sand on the ground at a point you can see within range. You can make the wall up to 30 feet long, 10 feet high, and 10 feet thick, and it vanishes when the spell ends. It blocks line of sight but not movement. A creature is blinded while in the wall''s space and must spend 3 feet of movement for every 1 foot it moves there.', 'a handful of sand', '90 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('644e3daf-7578-4720-ae12-7b4319192025', 'Divine Favor', 'Your prayer empowers you with divine radiance. Until the spell ends, your weapon attacks deal and extra 1d4 radiant damage on a hit.', '', 'Self', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ca45cf04-ba43-484b-af3d-65c6380b3035', 'Hunter''s Mark', 'You choose a creature you can see within range and mystically mark it as your quarry. Until the spell ends, you deal an extra 1d6 damage to the target whenever you hit it with a weapon attack, and you have advantage on any Wisdom (Perception) or Wisdom (Survival) check you make to find it. If the target drops to 0 hit points before this spell ends, you can use a bonus action on a subsequent turn of yours to mark a new creature.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 3rd or 4th level, you can maintain your concentration on the spell for up to 8 hours. When you use a spell slot of 5th level or higher, you can maintain your concentration on the spell for up to 24 hours.', '', '90 feet', 'V', NULL, '1 bonus action', false, 'up to 1 hour', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('551457ba-0ea6-45df-bf29-4ae243425af4', 'Blinding Smite', 'The next time you hit a creature with a melee weapon attack during this spell''s duration, you weapon flares with a bright light, and the attack deals an extra 3d8 radiant damage to the target. Additionally, the target must succeed on a Constitution saving throw or be blinded until the spell ends.<br> A creature blinded by this spell makes another Constitution saving throw at the end of each of its turns. On a successful save, it is no longer blinded.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('375feec6-b17a-4f74-af11-f08e2f1b3f38', 'Levitate', 'One creature or object of your choice that you can see within range rises vertically, up to 20 feet, and remains suspended there for the duration. The spell can levitate a target that weighs up to 500 pounds. An unwilling creature that succeeds on a Constitution saving throw is unaffected.<br> The target can move only by pushing or pulling against a fixed object or surface within reach (such as a wall or a ceiling), which allows it to move as if it were climbing. You can change the target''s altitude by up to 20 feet in either direction on your turn. If you are the target, you can move up or down as part of your move. Otherwise, you can use your action to move the target, which must remain within the spell''s range.<br> When the spell ends, the target floats gently to the ground if it is still aloft.', 'either a small leather loop or a piece of golden wire bent into a cup shape with a long shank on one end', '60 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b964d7c8-c156-400c-adbb-e0ed11d48e02', 'Cone of Cold', 'A blast of cold air erupts from your hands. Each creature in a 60-foot cone must make a Constitution saving throw. A creature takes 8d8 cold damage on a failed save, or half as much damage on a successful one.<br> A creature killed by this spell becomes a frozen statue until it thaws.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage increases by 1d8 for each slot level above 5th.', 'a small crystal or glass cone', 'Self (60-foot cone)', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7eaa1abf-efbe-4212-947f-8b952bd5c76e', 'Chain Lightning', 'You create a bolt of lightning that arcs toward a target of your choice that you can see within range. Three bolts then leap from that target to as many as three other targets, each of which must be within 30 feet of the first target. A target can be a creature or an object and can be targeted by only one of the bolts.<br> A target must make a Dexterity saving throw. The target takes 10d8 lightning damage on a failed save, or half as much on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, one additional bolt leaps from the first target to another target for each slot level above 6th.', 'a bit of fur, a piece of amber, glass, or crystal rod, and three silver pins', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0bde7819-e08c-4e72-bcd5-b586de723bac', 'Demiplane', 'You create a shadowy door on a flat solid surface that you can see within range. The door is large enough to allow Medium creatures to pass through unhindered. When opened, the door leads to a demiplane that appears to be an empty room 30 feet in each dimension, made of wood or stone. When the spell ends, the door disappears, and any creatures or objects inside the demiplane remain trapped there, as the door also disappears from the other side.<br> Each time you cast this spell, you can create a new demiplane, or have the shadowy door connect to a demiplane you created with a previous casting of this spell. Additionally, if you know the nature and contents of a demiplane created by a casting of this spell by another creature, you can have the shadowy door connect to its demiplane instead.', '', '60 feet', 'S', NULL, '1 action', false, '1 hour', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9632c723-aedd-487a-8377-417fa8fb15ee', 'Passwall', 'A passage appears at a point of your choice that you can see on a wooden, plaster, or stone surface (such as a wall, a ceiling, or a floor) within range, and lasts for the duration. You choose the opening''s dimensions - up to 5 feet wide, 8 feet tall, and 20 feet deep. The passage creates no instability in a structure surrounding it.<br> When the opening disappears, any creatures or objects still in the passage created by the spell are safely ejected to an unoccupied space nearest to the surface on which you cast the spell.', 'a pinch of sesame seeds', '30 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5df2123a-0d01-4e19-af5e-abcdb5fa797d', 'Heroes'' Feast', 'You bring forth a great feast, including magnificent food and drink. The feast takes 1 hour to consume and disappears at the end of that time, and the beneficial effects don''t set in until this hour is over. Up to twelve other creatures can partake of the feast.<br> A creature that partakes of the feast gains several benefits. The creature is cured of all diseases and poison, becomes immune to poison and being frightened, and makes all Wisdom saving throws with advantage. Its hit point maximum also increases by 2d10, and it gains the same number of hit points. These benefits last for 24 hours.', 'a gem-encrusted bowl worth at least 1,000 gp, which the spell consumes', '30 feet', 'VSMgp', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('12122986-a7eb-4760-aa91-38e71c570c4a', 'Mirror Image', 'Three illusory duplicates of yourself appear in your space. Until the spell ends, the duplicates move with you and mimic your actions, shifting position so it''s impossible to track which image is real. You can use your action to dismiss the illusory duplicates.<br> Each time a creature targets you with an attack during the spell''s duration, roll a d20 to determine whether the attack instead targets one of your duplicates.<br> If you have three duplicates, you must roll a 6 or higher to change the attack''s target to a duplicate. With two duplicates, you must roll an 8 or higher. With one duplicate, you must roll an 11 or higher.<br> A duplicate''s AC equals 10 + your Dexterity modifier. If an attack hits a duplicate, the duplicate is destroyed. A duplicate can be destroyed only by an attack that hits it. It ignores all other damage and effects. The spell ends when all three duplicates are destroyed.<br> A creature is unaffected by this spell if it can''t see, if it relies on senses other than sight, such as blindsight, or if it can perceive illusions as false, as with truesight.', '', 'Self', 'VS', NULL, '1 action', false, '1 minute', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0e3b32b4-8db5-43b2-a3cc-0f0dcacef73f', 'Spider Climb', 'Until the spell ends, one willing creature you touch gains the ability to move up, down, and across vertical surfaces and upside down along ceilings, while leaving its hands free. The target also gains a climbing speed equal to its walking speed.', 'a drop of bitumen and a spider', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2e91e08a-6298-4a3c-98f3-5b1a5aa386a1', 'Web', 'You conjure a mass of thick, sticky webbing at a point of your choice within range. The webs fill a 20-foot cube from that point for the duration. The webs are difficult terrain and lightly obscure their area.<br> If the webs aren''t anchored between two solid masses (such as walls or trees) or layered across a floor, wall, or ceiling, the conjured web collapses on itself, and the spell ends at the start of your next turn. Webs layered over a flat surface have a depth of 5 feet.<br> Each creature that starts its turn in the webs or that enters them during its turn must make a Dexterity saving throw. On a failed save, the creature is restrained as long as it remains in the webs or until it breaks free.<br> A creature restrained by the webs can use its actions to make a Strength check against your spell save DC. If it succeeds, it is no longer restrained.<br> The webs are flammable. Any 5-foot cube of webs exposed to fire burns away in 1 round, dealing 2d4 fire damage to any creature that starts its turn in the fire.', 'a bit of spider web', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('98ddffe6-f435-4771-8f08-051b1ee5a5de', 'Shadow of Moil', 'Flame-like shadows wreathe your body until the spell ends, causing you to become heavily obscured to others. The shadows turn dim light within 10 feet of you into darkness, and bright light in the same area to dim light.<br> Until the spell ends, you have resistance to radiant damage. In addition, whenever a creature within 10 feet of you hits you with an attack, the shadows lash out at that creature, dealing it 2d8 necrotic damage.', 'an undead eyeball encased in a gem worth at least 150 gp', 'Self', 'VSMgp', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c7e4aa50-0e9b-4b04-acaa-497a9eab78e4', 'Blink', 'Roll a d20 at the end of each of your turns for the duration of the spell. On a roll of 11 or higher, you vanish from your current plane of existence and appear in the Ethereal Plane (the spell fails and the casting is wasted if you were already on that plane). At the start of you next turn, and when the spell ends if you are on the Ethereal Plane, you return to an unoccupied space of your choice that you can see within 10 feet of the space you vanished from. If no unoccupied space is available within that range, you appear in the nearest unoccupied space (chosen at random if more than one space is equally near). You can dismiss this spell as an action.<br> While on the Ethereal Plane, you can see and hear the plane you originated from, which is cast in shades of gray, and you can''t see anything more than 60 feet away. You can only affect and be affected by other creatures on the Ethereal Plane. Creature that aren''t there can''t perceive you or interact with you, unless they have the ability to do so.', '', 'Self', 'VS', NULL, '1 action', false, '1 minute', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b1b3446b-a90c-4ed1-850e-cd9c381c4c64', 'Fireball', 'A bright streak flashes from your pointing finger to a point you choose within range then blossoms with a low roar into an explosion of flame. Each creature in a 20-foot radius must make a Dexterity saving throw. A target takes 8d6 fire damage on a failed save, or half as much damage on a successful one.<br> The fire spreads around corners. It ignites flammable objects in the area that aren''t being worn or carried.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d6 for each slot level above 3rd.', 'a tiny ball of bat guano and sulfur', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2b77ee67-dc73-41ee-b3a6-abf9a85855f0', 'Elemental Weapon', 'A nonmagical weapon you touch becomes a magic weapon. Choose one of the following damage types - acid, cold, fire, lightning, or thunder. For the duration, the weapon has a +1 bonus to attack rolls and deals an extra 1d4 damage of the chosen type when it hits.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th or 6th level, the bonus to attack rolls increases to +2 and the extra damage increases to 2d4. When you use a spell slot of 7th level or higher, the bonus increases to +3 and the extra damage increases to 3d4.', '', 'Touch', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9da0ae82-22e2-451a-af10-833eb6f8aa5e', 'Fly', 'You touch a willing creature. The target gains a flying speed of 60 feet for the duration. When the spell ends, the target falls if it is still aloft, unless it can stop the fall.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you can target one additional creature for each slot level above 3rd.', 'a wing feather from any bird', 'Touch', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c1b2f15d-3995-4903-b1a8-676b6019d680', 'Gaseous Form', 'You transform a willing creature you touch, along with everything it''s wearing and carrying, into a misty cloud for the duration. The spell ends if the creature drops to 0 hit points. An incorporeal creature isn''t affected.<br> While in this form, the target''s only method of movement is a flying speed of 10 feet. The target can enter and occupy the space of another creature. The target has resistance to nonmagical damage, and it has advantage on Strength, Dexterity, and Constitution saving throws. The target can pass through small holes, narrow openings, and even mere cracks, though it treats liquids as though they were solid surfaces. The target can''t fall and remains hovering in the air even when stunned or otherwise incapacitated.<br> While in the form of a misty cloud, the target can''t talk or manipulate objects, and any objects it was carrying or holding can''t be dropped, used, or otherwise interacted with. The target can''t attack or cast spells.', 'a bit of gauze and a wisp of smoke', 'Touch', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e0ba70c7-1e4d-4c09-a137-9488ff2fe6f3', 'Catnap', 'You make a calming gesture, and up to three willing creatures of your choice that you can see within range fall unconscious for the spell''s duration. The spell ends on a target early if it takes damage or someone uses an action to shake or slap it awake. If a target remains unconscious for the full duration, that target gains the benefit of a short rest, and it can''t be affected by this spell again until it finishes a long rest.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you can target one additional willing creature for each slot level above 3rd.', 'a pinch of sand', '30 feet', 'SM', NULL, '1 action', false, '10 minutes', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('07ff3b7a-78f1-43a7-949e-da4ca9c67dc9', 'Haste', 'Choose a willing creature that you can see within range. Until the spell ends, the target''s speed is doubled, it gains a +2 bonus to AC, it has advantage on Dexterity saving throws, and it gains an additional action on each of its turns. That action can be used only to take the Attack (one weapon attack only), Dash, Disengage, Hide, or Use an Object action.<br> When the spell ends, the target can''t move or take actions until after its next turn, as a wave of lethargy sweeps over it.', 'a shaving of licorice root', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fd759882-d013-4732-91c4-95a8a9c82c41', 'Life Transference', 'You sacrifice some of your health to mend another creature''s injuries. You take 4d8 necrotic damage, and one creature of your choice that you can see within range regains a number of hit points equal to twice the necrotic damage you take. AtHigher Levels. When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d8 for each slot level above 3rd.', '', '30 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('11cee53b-0659-4f93-be28-a0aeab02e6c4', 'Lightning Bolt', 'A stroke of lightning forming a line of 100 feet long and 5 feet wide blasts out from you in a direction you choose. Each creature in the line must make a Dexterity saving throw. A creature takes 8d6 lightning damage on a failed save, or half as much damage on a successful one.<br> The lightning ignites flammable objects in the area that aren''t being worn or carried.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d6 for each slot above 3rd.', 'a bit of fur and a rod of amber, crystal, or glass', 'Self (100-foot line)', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('15e96724-c51b-4109-a60e-130f5383eb9b', 'Slow', 'You alter time around up to six creatures of your choice in a 40-foot cube within range. Each target must succeed on a Wisdom saving throw or be affected by this spell for the duration.<br> An affected target''s speed is halved, it takes a -2 penalty to AC and Dexterity saving throws, and it can''t use reactions. On its turn, it can use either an action or a bonus action, not both. Regardless of the creature''s abilities or magic items, it can''t make more than one melee or ranged attack during its turn.<br> If the creature attempts to cast a spell with a casting time of 1 action, roll a d20. On an 11 or higher, the spell doesn''t take effect until the creature''s next turn, and the creature must use its action on that turn to complete the spell. If it can''t, the spell is wasted.<br> A creature affected by this spell makes another Wisdom saving throw at the end of its turn. On a successful save, the effect ends for it.', 'a drop of molasses', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4de3adbe-1922-4dea-a10a-a8537386fbe2', 'Cloudkill', 'You create a 20-foot-radius sphere of poisonous, yellow-green fog centered on a point you choose within range. The fog spreads around corners. It lasts for the duration or until strong wind disperses the fog, ending the spell. Its area is heavily obscured.<br> When a creature enters the spell''s area for the first time on a turn or starts its turn there, that creature must make a Constitution saving throw. The creature takes 5d8 poison damage on a failed save, or half as much damage on a successful one. Creatures are affected even if they hold their breath or don''t need to breathe.<br> The fog moves 10 feet away from you at the start of each of your turns, rolling along the surface of the ground. The vapors, being heavier than air, sink to the lowest level of the land, even pouring down openings.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage increases by 1d8 for each slot level above 5th.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a81b898d-9ee1-4472-b5af-4c73e6edae57', 'Creation', 'You pull wisps of shadow material from the Shadowfell to create a nonliving object of vegetable matter within range - soft goods, rope, wood, or something similar. You can also use this spell to create mineral objects such as stone, crystal, or metal. The object created must be no larger than a 5-foot cube, and the object must be of a form and material that you have seen before.<br> The duration depends on the object''s material. If the object is composed of multiple materials, use the shortest duration.<br> <b>Duration</b>: Vegetable matter - 1 day. Stone/crystal - 12 hours. Precious metals - 1 hour. Gems - 10 minutes. Adamantine/Mithral - 1 minute.<br> Using any material created by this spell as another spell''s material component causes that spell to fail.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the cube increases by 5 feet for each slot level above 5th.', 'a tiny piece of matter of the same type of the item you plan to create', '30 feet', 'VSM', NULL, '1 minute', false, 'Special', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e2fa5fb1-2aec-4fa9-868b-59f26c391bbe', 'Enemies Abound', 'You reach into the mind of one creature you can see and force it to make an Intelligence saving throw. A creature automatically succeeds if it is immune to being frightened. On a failed save, the target loses the ability to distinguish friend from foe, regarding all creatures it can see as enemies until the spell ends. Each time the target takes damage, it can repeat the saving throw, ending the effect on itself on a success.<br> Whenever the affected creature chooses another creature as a target, it must choose the target at random from among the creatures it can see within range of the attack, spell, or other ability it''s using. lf an enemy provokes an opportunity attack from the affected creature, the creature must make that attack if it is able to.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7310baad-ce31-4f40-9421-210dad5e9eb3', 'Erupting Earth', 'Choose a point you can see on the ground within range. A fountain of churned earth and stone erupts in a 20-foot cube centered on that point. Each creature in that area must make a Dexterity saving throw. A creature takes 3d12 bludgeoning damage on a failed save, or half as much damage on a successful one. Additionally, the ground in that area becomes difficult terrain until cleared. Each 5-foot-square portion of the area requires at least 1 minute to clear by hand.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d12 for each slot level above 3rd.', 'a piece of obsidian', '120 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fa971fbe-8ae3-431d-97e6-fc11b5f99c16', 'Feign Death', 'You touch a willing creature and put it into a cataleptic state that is indistinguishable from death.<br> For the spell''s duration, or until you use an action to touch the target and dismiss the spell, the target appears dead to all outward inspection and to spells used to determine the target''s status. The target is blinded and incapacitated, and its speed drops to 0. The target has resistance to all damage except psychic damage. If the target is diseased or poisoned when you cast the spell, or becomes diseased or poisoned while under the spell''s effect, the disease and poison have no effect until the spell ends.', 'A pinch of graveyard dirt', 'Touch', 'VSM', NULL, '1 action', true, '1 hour', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('33f025ea-a9ca-4146-9c70-ada8183edfde', 'Telekinesis', 'You gain the ability to move or manipulate creatures or objects by thought. When you cast the spell, and as your action each round for the duration, you can exert your will on one creature or object that you can see within range, causing the appropriate effect below. You can affect the same target round after round, or choose a new one at any time. If you switch targets, the prior target is no longer affected by the spell.<br> <b>Creature</b>: You can try to move a Huge or smaller creature. Make an ability check with your spellcasting ability contested by the creature''s Strength check. If you win the contest, you move the creature up to 30 feet in any direction, including upward but not beyond the range of this spell. Until the end of your next turn, the creature is restrained in your telekinetic grip. A creature lifted upward is suspended in mid-air.<br> On subsequent rounds, you can use your action to attempt to maintain your telekinetic grip on the creature by repeating the contest.<br> <b>Object</b>: You can try to move an object that weighs up to 1,000 pounds. If the object isn''t being worn or carried, you automatically move it up to 30 feet in any direction, but not beyond the range of this spell.<br> If the object is worn or carried by a creature, you must make an ability check with your spellcasting ability contested by that creature''s Strength check. If you succeed, you pull the object away from that creature and can move it up to 30 feet in any direction but not beyond the range of this spell.<br> You can exert fine control on objects with your telekinetic grip, such as manipulating a simple tool, opening a door or a container, stowing or retrieving an item from an open container, or pouring the contents from a vial.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('98a95729-fa86-40cb-96d3-27ea1a555a6c', 'Circle of Death', 'A sphere of negative energy ripples out in a 60-foot-radius sphere from a point within range. Each creature in that area must make a Constitution saving throw. A target takes 8d6 necrotic damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the damage increases by 2d6 for each slot level above 6th.', 'the powder of a crushed black pearl worth at least 500 gp', '150 feet', 'VSMgp', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('67b02c58-fb2e-434d-9019-c205e3b412aa', 'Disintegrate', 'A thin green ray springs from your pointing finger to a target that you can see within range. The target can be a creature, an object, or a creation of magical force, such as the wall created by Wall of Force.<br> A creature targeted by this spell must make a Dexterity saving throw. On a failed save, the target takes 10d6+40 force damage. If this damage reduces the target to 0 hit points, it is disintegrated.<br> A disintegrated creature and everything it is wearing and carrying, except magic items, are reduced to a pile of fine gray dust. The creature can be restored to life only by means of a <i>true resurrection</i> or a <i>wish</i> spell.<br> This spell automatically disintegrates a Large or smaller nonmagical object or a creation of magical force. If the target is a Huge or larger object or creation of force, this spell disintegrates a 10-foot-cube portion of it. A magic item is unaffected by this spell.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the damage increases by 3d6 for each slot level above 6th.', 'a lodestone and a pinch of dust', '60 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3ec667eb-3679-4bba-b99a-aee6ab36f6a5', 'Globe of Invulnerability', 'An immobile, faintly shimmering barrier springs into existence in a 10-foot radius around you and remains for the duration.<br> Any spell of 5th level or lower cast from outside the barrier can''t affect creatures or objects within it, even if the spell is cast using a higher level spell slot. Such a spell can target creatures and objects within the barrier, but the spell has no effect on them. Similarly, the area within the barrier is excluded from the areas affected by such spells.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the barrier blocks spells of one level higher for each slot level above 6th.', 'a glass or crystal bead that shatters when the spell ends', 'Self (10-foot radius)', 'VSM', NULL, '1 action', false, '1 day', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cddc799f-fe67-4d6b-9db3-82839e632fe4', 'Hunger of Hadar', 'You open a gateway to the dark between the stars, a region infested with unknown horrors. A 20-foot-radius sphere of blackness and bitter cold appears, centered on a point with range and lasting for the duration. This void is filled with a cacophony of soft whispers and slurping noises that can be heard up to 30 feet away. No light, magical or otherwise, can illuminate the area, and creatures fully within the area are blinded.<br> The void creates a warp in the fabric of space, and the area is difficult terrain. Any creature that starts its turn in the area takes 2d6 cold damage. Any creature that ends its turn in the area must succeed on a Dexterity saving throw or take 2d6 acid damage as milky, otherworldly tentacles rub against it.', 'a pickled octopus tentacle', '150 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('481a3fde-941b-46a5-adf0-2ccd73435d25', 'Intellect Fortress', 'For the duration, you or one willing creature you can see within range has resistance to psychic damage, as well as advantage on Intelligence, Wisdom, and Charisma saving throws.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you can target one additional creature for each slot level above 3rd. The creatures must be within 30 feet of each other when you target them.', '', '30 feet', 'V', NULL, '1 action', false, 'up to 1 hour', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7868a6bf-9bec-4c5d-88e8-76572d4adc47', 'Far Step', 'You teleport up to 60 feet to an unoccupied space you can see. On each of your turns before the spell ends, you can use a bonus action to teleport in this way again.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e360dff8-98f1-483c-9d4a-b89f99af0a10', 'Lightning Arrow', 'The next time you make a ranged weapon attack during the spell''s duration, the weapon''s ammunition, or the weapon itself if it''s a thrown weapon, transforms into a bolt of lightning. Make the attack roll as normal. The target takes 4d8 lightning damage on a hit, or half as much damage on a miss, instead of the weapon''s normal damage.<br> Whether you hit or miss, each creature within 10 feet of the target must make a Dexterity saving throw. Each of these creatures takes 2d8 lightning damage on a failed save, or half as much damage on a successful one.<br> The piece of ammunition or weapon then returns to its normal form.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage for both effects of the spell increases by 1d8 for each slot level above 3rd.', '', 'Self', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('78b69a36-47ab-4034-9f42-d22dfc38d434', 'Delayed Blast Fireball', 'A beam of yellow light flashes from your pointing finger, then condenses to linger at a chosen point within range as a glowing bead for the duration. When the spell ends, either because your concentration is broken or because you decide to end it, the bead blossoms with a low roar into an explosion of flame that spreads around corners. Each creature in a 20-foot-radius sphere centered on that point must make a Dexterity saving throw. A creature takes fire damage equal to the total accumulated damage on a failed save, or half as much damage on a successful one.<br> The spell''s base damage is 12d6. If at the end of your turn the bead has not yet detonated, the damage increases by 1d6.<br> If the glowing bead is touched before the interval has expired, the creature touching it must make a Dexterity saving throw. On a failed save, the spell ends immediately, causing the bead to erupt in flame. On a successful save, the creature can throw the bead up to 40 feet. When it strikes a creature or a solid object, the spell ends, and the bead explodes.<br> The fire damages objects in the area and ignites flammable objects that aren''t being worn or carried.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 8th level or higher, the base damage increases by 1d6 for each slot level above 7th.', 'a tine ball of bat guano and sulfur', '150 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e61c4d30-5f55-40e7-91d1-631957453a2b', 'Prismatic Spray', 'Eight multicolored rays of light flash from your hand. Each ray is a different color and has a different power and purpose. Each creature in a 60-foot cone must make a Dexterity saving throw. For each target, roll a d8 to determine which color ray affects it.<br> <b>1-Red</b>: The target takes 10d6 fire damage on a failed save, or half as much damage on a successful one.<br> <b>2-Orange</b>: The target takes 10d6 acid damage on a failed save, or half as much damage on a successful one.<br> <b>3-Yellow</b>: The target takes 10d6 lightning damage on a failed save, or half as much damage on a successful one.<br> <b>4-Green</b>: The target takes 10d6 poison damage on a failed save, or half as much damage on a successful one.<br> <b>5-Blue</b>: The target takes 10d6 cold damage on a failed save, or half as much damage on a successful one.<br> <b>6-Indigo</b>: On a failed save, the target is restrained. It must then make a Constitution saving throw at the end of each of its turns. If it successfully saves three times, the spell ends. If it fails its save three times, it permanently turns to stone and is subjected to the petrified condition. The successes and failures don''t need to be consecutive, keep track of both until the target collects three of a kind.<br> <b>7-Violet</b>: On a failed save, the target is blinded. It must then make a Wisdom saving throw at the start of your next turn. A successful save ends the blindness. If it fails that save, the creature is transported to another plane of existence of the DM''s choosing and is no longer blinded. (Typically, a creature that is on a plane that isn''t its home plane is banished home, while other creatures are usually cast into the Astral or Ethereal planes.)<br> <b>8-Special</b>: The target is struck by two rays. Roll twice more, rerolling any 8.', '', 'Self (60 foot cone)', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8a2cac97-2c6c-4166-b410-900188de0816', 'Incendiary Cloud', 'A swirling cloud of smoke shot through with white-hot embers appears in a 20-foot-radius sphere centered on a point within range. The cloud spreads around corners and is heavily obscured. It lasts for the duration or until a wind of moderate or greater speed (at least 10 miles per hour) disperses it.<br> When the cloud appears, each creature in it must make a Dexterity saving throw. A creature takes 10d8 fire damage on a failed save, or half as much damage on a successful one. A creature must also make this saving throw when it enters the spell''s area for the first time on a turn or ends its turn there.<br> The cloud moves 10 feet directly away from you in a direction that you choose at the start of each of your turns.', '', '150 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('255eee00-6dd2-4926-bebc-8619b549028a', 'Eldritch Blast', 'A beam of crackling energy streaks toward a creature within range. Make a ranged spell attack against the target. On a hit, the target takes 1d10 force damage.<br> The spell creates more than one beam when you reach higher levels - two beams at 5th level, three beams at 11th level, and four beams at 17th level. you can direct the beams at the same target or at different ones. Make a separate attack roll for each beam.', '', '120 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('796b4cdf-0ff2-408a-a509-e9b4dd2f203c', 'Hellish Rebuke', 'You point your finger, and the creature that damaged you is momentarily surrounded by hellish flames. The creature must make a Dexterity saving throw. It takes 2d10 fire damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the damage increases by 1d10 for each slot level above 1st.', '', '60 feet', 'VS', NULL, '1 reaction', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c172ad83-47aa-40b0-a160-005cef1b2535', 'Counterspell', 'You attempt to interrupt a creature in the process of casting a spell. If the creature is casting a spell of 3rd level or lower, its spell fails and has no effect. If it is casting a spell of 4th level or higher, make an ability check using your spellcasting ability. The DC equals 10 + the spell''s level. On a success, the creature''s spell fails and has no effect.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the interrupted spell has no effect if its level is less than or equal to the level of the spell slot you used.', '', '60 feet', 'S', NULL, '1 reaction', false, 'Instantaneous', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0d465d65-65c2-4c38-a215-ff79e8518e8e', 'Vampiric Touch', 'The touch of your shadow-wreathed hand can siphon force from others to heal your wounds. Make a melee spell attack against a creature within your reach. On a hit, the target takes 3d6 necrotic damage, and you regain hit points equal to half the amount of necrotic damage dealt. Until the spell ends, you can make the attack again on each of your turns as an action.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d6 for each slot level above 3rd.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9df8ba33-aa26-4eb2-9662-82b93a0145d9', 'Contact Other Plane', 'You mentally contact a demigod, the spirit of a long-dead sage, or some other mysterious entity from another plane. Contacting this extraplanar intelligence can strain or even break your mind. When you cast this spell, make a DC 15 Intelligence saving throw. On a failure, you take 6d6 psychic damage and are insane until you finish a long rest. While insane, you can''t take actions, can''t understand what other creatures say, can''t read, and speak only in gibberish. A greater restoration spell cast on you ends this effect.<br> On a successful save, you can ask the entity up to five questions. You must ask your questions before the spell ends. The DM answers each question with one word, such as yes, no, maybe, never, irrelevant, unclear (if the entity doesn''t know the answer to the question). If a one-word answer would be misleading, the DM might instead offer a short phrase as an answer.', '', 'Self', 'V', NULL, '1 minute', true, '1 minute', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('37dcfceb-5727-4b9a-90ff-0df53cc962e6', 'Flesh to Stone', 'You attempt to turn one creature that you can see within range into stone. If the targets body is made of flesh, the creature must make a Constitution saving throw. On a failed save, it is restrained as its flesh begins to harden. On a successful save, the creature isn''t affected.<br> A creature restrained by this spell must make another Constitution saving throw at the end of each of its turns. If it successfully saves against this spell three times, the spell ends. If it fails saves three times, it is turned to stone and subjected to the petrified condition for the duration. The successes and failures don''t need to be consecutive, keep track of both until the target collects three of a kind.<br> If the creature is physically broken while petrified, it suffers from similar deformities if it reverts to its original state.<br> If you maintain your concentration on this spell for the entire possible duration, the creature is turned to stone until the effect is removed.', 'a pinch of lime, water, and earth', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 day', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1e132884-30b6-4548-be92-ed50d803f8d9', 'Imprisonment', 'You create a magical restraint to hold a creature that you can see within range. The target must succeed on a Wisdom saving throw or be bound by the spell, if it succeeds, it is immune to this spell if you cast it again. While affected by this spell, the creature doesn''t need to breathe, eat, or drink, and it doesn''t age. Divination spells can''t locate or perceive the target.<br> When you cast the spell, you choose one of the following forms of imprisonment.<br> <b>Burial</b>: The target is entombed far beneath the earth in a sphere of magical force that is just large enough to contain the target. Nothing can pass through the sphere, nor can any creature teleport or use planar travel to get into or out of it.<br> The special component for this version of the spell is a small mithral orb.<br> <b>Chaining</b>: Heavy chains, firmly rooted in the ground, hold the target in place. The target is restrained until the spell ends, and it can''t move or be moved by any means until then.<br> The special component for this version of the spell is a fine chain of precious metal.<br> <b>Hedged Prison</b>: The spell transports the target into a tiny demiplane that is warded against teleportation and planar travel. The demiplane can be a labyrinth, a cage, a tower, or any similar confined structure or area of your choice.<br> The special component for this version of the spell is a miniature representation of the prison made from jade.<br> <b>Minimus Containment</b>: The target shrinks to a height of 1 inch and is imprisoned inside a gemstone or similar object. Light can pass through the gemstone normally (allowing the target to see out and other creatures to see in), but nothing else can pass through, even by means of teleportation or planar travel. The gemstone can''t be cut or broken while the spell remains in effect.<br> The special component for this version of the spell is a large, transparent gemstone, such as a corundum, diamond, or ruby.<br> <b>Slumber</b>: The target falls asleep and can''t be awoken.<br> The special component for this version of the spell consists of rare soporific herbs.<br> <b>Ending the Spell</b>: During the casting of the spell, in any of its versions, you can specify a condition that will cause the spell to end and release the target. The condition can be as specific or as elaborate as you choose, but the DM must agree that the condition is reasonable and has a likelihood of coming to pass. The conditions can be based on a creature''s name, identity, or deity but otherwise must be based on observable actions or qualities and not based on intangibles such as level, class, or hit points.<br> A <i>dispel magic</i> spell can end the spell only if it is cast as a 9th-level spell, targeting either the prison or the special component used to create it.<br> You can use a particular special component to create only one prison at a time. If you cast the spell again using the same component, the target of the first casting is immediately freed from its binding.', 'a vellum depiction or a carved statuette in the likeness of the target, and a special component that varies according to the version of the spell you choose, worth at least 500 gp per Hit Die of the target', '30 feet', 'VSMgp', NULL, '1 minute', false, 'Until dispelled', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a0b80d65-c75e-4b6b-8b18-bd523a1b63c0', 'Negative Energy Flood', 'You send ribbons of negative energy at one creature you can see within range. Unless the target is undead, it mus t make a Constitution saving throw, taking 5d12 necrotic damage on a failed save, or half as much damage on a successful one. A target killed by this damage rises up as a zombie at the start of your next turn. The zombie pursues whatever creature it can see that is closest to it. Statistics for the zombie are in the <i>Monster Manual</i>.<br> If you target an undead with this spell, the target doesn''t make a saving throw. Instead, roll 5d12. The target gains half the total as temporary hit points.', 'a broken bone and a square of black silk', '60 feet', 'VM', NULL, '1 action', false, 'up to 1 minute', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bfed626c-70c1-4a3c-9ea6-9cf8d8031a30', 'Find Familiar', 'You gain the service of a familiar, a spirit that takes an animal form you choose - bat, cat, crab, frog (toad), hawk, lizard, octopus, owl, poisonous snake, fish (quipper), rat, raven, sea horse, spider, or weasel. Appearing in an unoccupied space within range, the familiar has the statistics of the chosen form, though it is a celestial, fey or fiend (your choice) instead of a beast.<br> Your familiar acts independently of you, but it always obeys your commands. In combat, it rolls its own initiative and acts on its own turn. A familiar can''t attack, but it can take other actions as normal.<br> When the familiar drops to 0 hit points, it disappears, leaving behind no physical form. It reappears after you cast this spell again.<br> While your familiar is within 100 feet of you, you can communicate with it telepathically. Additionally, as an action, you can see through your familiar''s eyes and hear what it hears until the start of your next turn, gaining the benefits of any special senses that the familiar has. During this time, you are deaf and blind with regard to your own senses.<br> As an action, you can temporarily dismiss your familiar. It disappears into a pocket dimension where it awaits you summons. Alternatively, you can dismiss it forever. As an action while it is temporarily dismissed, you can cause it to reappear in any unoccupied space within 30 feet of you.<br> You can''t have more than one familiar at a time. If you cast this spell while you already have a familiar, you instead cause it to adopt a new form. Choose one of the forms from the above list. Your familiar transforms into the chosen creature.<br> Finally, when you cast a spell with a range of touch, your familiar can deliver the spell as if it had cast the spell. Your familiar must be within 100 feet of you, and it must use its reaction to deliver the spell when you cast it. If the spell requires an attack roll, you use your attack modifier for the roll.', '10 gp worth of charcoal, incense, and herbs that must be consumed by fire in a brass brazier', '10 feet', 'VSMgp', NULL, '1 hour', true, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ffa16c80-7ce3-4eb2-97e6-32a2754ea090', 'Spirit Shroud', 'You call forth spirits of the dead, which lit around you for the spell''s duration. The spirits are intangible and invulnerable.<br>Until the spell ends, any attack you make deals 1d8 extra damage when you hit a creature within 10 feet of you. This damage is radiant, necrotic, or cold (your choice when you cast the spell). Any creature that takes this damage can''t regain hit points until the start of your next turn.<br>In addition, any creature of your choice that you can see that starts its turn within 10 feet of you has its speed reduced by 10 feet until the start of your next turn.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d8 for every two slot levels above 3rd.', '', 'Self', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c2120b1a-d5f8-4438-a0ac-c412b4f59ff6', 'Grease', 'Slick grease covers the ground in a 10-foot square centered on a point within range and turns it into difficult terrain for the duration.<br> When the grease appears, each creature standing in its area must succeed on a Dexterity saving throw or fall prone. A creature that enters the area or ends its turn there must also succeed on a Dexterity saving throw or fall prone.', 'a bit of pork rind or butter', '60 feet', 'VSM', NULL, '1 action', false, '1 minute', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d7f6088f-ca2b-4e60-9474-6ab6e07673db', 'Arcane Lock', 'You touch a closed door, window, gate, chest, or other entryway, and it becomes locked for the duration. You and the creatures you designate when you cast this spell can open the object normally. You can also set a password that, when spoken within 5 feet of the object, suppresses this spell for 1 minute. Otherwise, it is impassable until it is broken or the spell is dispelled or suppressed. Casting <i>knock</i> on the object suppresses <i>arcane lock</i> for 10 minutes.<br> While affected by this spell, the object is more difficult to break or force open. The DC to break it or pick any locks on it increases by 10.', 'gold dust worth at least 25 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, 'Until dispelled', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('93f2c209-41db-4bf2-8589-98ae912d4bd4', 'Skill Empowerment', 'Your magic deepens a creature''s understanding of its own talent. You touch 1 willing creature and give it expertise in 1 skill of your choice. Until the spell ends, the creature doubles its proficiency bonus for ability checks it makes that use the chosen skill.<br> You must choose a skill in which the target is proficient and that isn''t already benefiting from an effect, such as Expertise, that doubles its proficiency bonus.', '', 'Touch', 'VS', NULL, '1 action', false, '8 hours', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a2f1de5d-72bc-4556-9950-2de1d1ea9f1f', 'Summon Fey', 'You call forth a fey spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Fey Spirit stat block. When you cast the spell, choose a mood. Fuming, Mirthful, or Tricksy. The creature resembles a fey creature of your choice marked by the chosen mood, which determines one of the traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, use the higher level wherever the spell''s level appears in the stat block.', 'a gilded flower worth at least 300 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e9136ad7-ae78-41b6-ac28-6e8651a80949', 'Summon Lesser Demons', 'You utter foul words, summoning demons from the chaos of the Abyss. Roll a d6 to determine what appears. 1-2: Two demons of challenge rating 1 or lower. 3-4: Four demons of challenge rating 1/2 or lower. 5-6: Eight demons ofchallenge rating 1/4 or lower.<br> The DM chooses the demons, such as manes or dretches, and you choose the unoccupied spaces you can see within range where they appear. A summoned demon disappears when it drops to 0 hit points or when the spell ends.<br> The demons are hostile to all creatures, including you. Roll initiative for the summoned demons as a group, which has its own turns. The demons pursue and attack the nearest non-demons to the best of their ability.<br> As part of casting the spell, you can form a circle on the ground with the blood used as a material component. The circle is large enough to encompass your space. While the spell lasts, the summoned demons can''t cross the circle or harm it, and they can''t target anyone within it. Using the material component in this manner consumes it when the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th or 7th level, you summon twice as many demons. If you cast it using a spell slot of 8th or 9th level, you summon three times as many demons.', 'a vial of blood from a humanoid killed within the past 24 hours', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7dd48f12-a104-41db-b5e8-0499fd41e2df', 'Summon Shadowspawn', 'You call forth a shadowy spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Shadow Spirit stat block. When you cast the spell, choose an emotion: Fury, Despair, or Fear. The creature resembles a misshapen biped marked by the chosen emotion, which determines certain traits in its stat block. The creature determines when it drop to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after your. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and it uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast the spell using a spell slot of 4th level or higher, use the higher level wherever the spell''s level appears on the stat block.', 'tears inside a crystal vial worth at least 300 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('258aeaf7-8720-4104-9c42-6c20317450a7', 'Summon Undead', 'You call forth an undead spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Undead Spirit stat block. When you cast the spell, choose the creature''s form: Ghostly, Putrid, or Skeletal. The spirit resembles an undead creature with the chosen form, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, use the higher level wherever the spell''s level appears in the stat block.', 'a gilded skull worth at least 300 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('430c4d28-ea70-4315-9864-0ea6fe4e23b1', 'Thunder Step', 'You teleport yourself to an unoccupied space you can see within range. Immediately after you disappear, a thunderous boom sounds, and each creature within 10 feet of the space you left must make a Constitution saving throw, taking 3d10 thunder damage on a failed save, or half as much damage on a successful one. The thunder can be heard from up to 300 feet away.<br> You can bring along objects as long as their weight doesn''t exceed what you can carry. You can also teleport one willing creature of your size or smaller who is carrying gear up to its carrying capacity. The creature must be within 5 feet of you when you cast this spell, and there must be an unoccupied space within 5 feet of your destination space for the creature to appear in. Otherwise, the creature is left behind.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, the damage increases by 1d10 for each slot level above 3rd.', '', '90 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('25947f5e-373c-4c16-ba82-013d99a68fc5', 'Tidal Wave', 'You conjure up a wave of water that crashes down on an area within range. The area can be up to 30 feet long, up to 10 feet wide, and up to 10 feet tall. Each creature in that area must make a Dexterity saving throw. On a failed save, a creature takes 4d8 bludgeoning damage and is knocked prone. On a successful save, a creature takes half as much damage and isn''t knocked prone. The water then spreads out across the ground in all directions, extinguishing unprotected flames in its area and within 30 feet of it, and then it vanishes.', 'a drop of water', '120 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9989a055-bc6b-455b-be6c-a08198429a78', 'Tiny Servant', 'You touch one Tiny, nonmagical object that isn''t attached to another object or a surface and isn''t being carried by another creature. The target animates and sprouts little arms and legs, becoming a creature under your control until the spell ends or the creature drops to 0 hit points. See the stat block (XGE p169) for its statistics.<br> As a bonus action, you can mentally command the creature if it is within 120 feet of you. (If you control multiple creatures with this spell, you can command any or all of them at the same time, issuing the same command to each one.) You decide what action the creature will take and where it will move during its next turn, or you can issue a simple, general command, such as to fetch a key, stand watch, or stack some books. If you issue no commands, the servant does nothing other than defend itself against hostile creatures. Once given an order, the servant continues to follow that order until its task is complete.<br> When the creature drops to 0 hit points, it reverts to its original form, and any remaining damage carries over to that form.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, you can animate two additional objects for each slot level above 3rd.', '', 'Touch', 'VS', NULL, '1 minute', false, '8 hours', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0dd05f30-643d-46c8-bd42-d99e152cf772', 'Wall of Water', 'You create a wall of water on the ground at a point you can see within range. You can make the wall up to 30 feet long, 10 feet high, and 1 foot thick, or you can make a ringed wall up to 20 feet in diameter, 20 feet high, and 1 foot thick. The wall vanishes when the spell ends. The wall''s space is difficult terrain.<br> Any ranged weapon attack that enters the wall''s space has disadvantage on the attack roll, and fire damage is halved if the fire effect passes through the wall to reach its target. Spells that deal cold damage that pass through the wall cause the area of the wall they pass through to freeze solid (at least a 5-foot-square section is frozen). Each 5-foot-square frozen section has AC 5 and 15 hit points. Reducing a frozen section to 0 hit points destroys it. When a section is destroyed, the wall''s water doesn''t fill it.', 'a drop of water', '60 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('153ba88a-ab3c-48a2-a950-6b1a0f9c00c8', 'Rope Trick', 'You touch a length of rope that is up to 60 feet long. One end of the rope then rises into the air until the whole rope hangs perpendicular to the ground. At the upper end of the rope, an invisible entrance opens to an extradimensional space that lasts until the spell ends.<br> The extradimensional space can be reached by climbing to the top of the rope. The space can hold as many as eight Medium or smaller creatures. The rope can be pulled into the space, making the rope disappear from view outside the space.<br> Attacks and spells can''t cross through the entrance into or out of the extradimensional space, but those inside can see out of it as if through a 3-foot-by-5-foot window centered on the rope.<br> Anything inside the extradimensional space drops out when the spell ends.', 'powdered corn extract and a twisted loop of parchment', 'Touch', 'VSM', NULL, '1 action', false, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 2);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('fb8b7eb8-0742-422a-9a33-e5f67143b318', 'Phantom Steed', 'A Large quasi-real, horse-like creature appears on the ground in an unoccupied space of your choice within range. You decide the creature''s appearance, but it is equipped with a saddle, bit, and bridle. Any of the equipment created by the spell vanishes in a puff of smoke if it is carried more than 10 feet away from the steed.<br> For the duration, you or a creature you choose can ride the steed. The creature uses the statistics for a riding horse, except it has a speed of 100 feet and can travel 10 miles in an hour, or 13 miles at a fast pace. When the spell ends, the steed gradually fades, giving the rider 1 minute to dismount. The spell ends if you use an action to dismiss it or if the steed takes any damage.', '', '30 feet', 'VS', NULL, '1 minute', true, '1 hour', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 3);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5246a5ca-4325-445f-b50c-32245a0e8125', 'Arcane Eye', 'You create an invisible, magical eye within range that hovers in the air for the duration.<br> You mentally receive visual information from the eye, which has normal vision and darkvision out to 30 feet. The eye can look in every direction.<br> As an action, you can move the eye up to 30 feet in any direction. There is no limit to how far away from you the eye can move, but it can''t enter another plane of existence. A solid barrier blocks the eye''s movement, but the eye can pass through an opening as small as 1 inch in diameter.', 'a bit of bat fur', '30 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8e367ba0-f18e-497c-bea4-87ae2b9a1e98', 'Aura of Life', 'Life-preserving energy radiates from you in an aura with a 30-foot radius. Until the spell ends, the aura moves with you, centered on you. Each non-hostile creature in the aura (including you) has resistance to necrotic damage, and its hit point maximum can''t be reduced. In addition, a non-hostile, living creature regains 1 hit point when it starts its turn in the aura with 0 hit points.', '', 'Self (30-foot radius)', 'V', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8c872fd3-6762-4368-a6f3-a762cd0ec4f3', 'Aura of Purity', 'Purifying energy radiates from you in an aura with a 30-foot radius. Until the spell ends, the aura moves with you, centered on you. Each non-hostile creature in the aura (including you) can''t become diseased, has resistance to poison damage, and has advantage on saving throws against effects that cause any of the following conditions: blinded, charmed, deafened, frightened, paralyzed, poisoned, and stunned.', '', 'Self (30-foot radius)', 'V', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e5503ffb-c531-4155-b896-e855e095bbaf', 'Fabricate', 'You convert raw materials into products of the same material. For example, you can fabricate a wooden bridge from a clump of trees, a rope from a patch of hemp, and clothes from flax or wool.<br> Choose raw materials that you can see within range. You can fabricate a Large or smaller object (contained within a 10-foot cube, or eight connected 5-foot cubes), given a sufficient quantity of raw material. If you are working with metal, stone, or another mineral substance, however, the fabricated object can be no larger than Medium (contained within a single 5-foot cube). The quality of objects made by the spell is commensurate with the quality of the raw materials.<br> Creatures or magic items can''t be created or transmuted by this spell. You also can''t use it to create items that ordinarily require a high degree of craftsmanship, such as jewelry, weapons, glass, or armor, unless you have proficiency with the type of artisan''s tools used to craft such objects.', '', '120 feet', 'VS', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6aa427a1-e5f5-4954-9446-ec9299907d31', 'Fire Shield', 'Thin and wispy flames wreathe your body for the duration, shedding bright light in a 10-foot radius and dim light for an additional 10 feet. You can end the spell early by using an action to dismiss it.<br> The flames provide you with a warm shield or a chill shield, as you choose. The warm shield grants you resistance to cold damage, and the chill shield grants you resistance to fire damage.<br> In addition, whenever a creature within 5 feet of you hits you with a melee attack, the shield erupts with flame. The attacker takes 2d8 fire damage from a warm shield, or 2d8 cold damage from a cold shield.', 'a bit of phosphorous or a firefly', 'Self', 'VSM', NULL, '1 action', false, '10 minutes', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b57b12ab-0b82-4214-bb3b-68ce3cb8ed30', 'Charm Monster', 'You attempt to charm a creature you can see within range. It must make a Wisdom saving throw, and it does so with advantage if you or your companions are fighting it. If it fails the saving throw, it is charmed by you until the spell ends or until you or your companions do anything harmful to it. The charmed creature is friendly to you. When the spell ends, the creature knows it was charmed by you.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, you can target 1 additional creature for each slot level above 4th. The creatures must be within 30 feet of each other when you target them.', '', '30 feet', 'VS', NULL, '1 action', false, '1 hour', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('78d0264f-42f9-4928-a346-fa8abc8878ee', 'Phantasmal Killer', 'You tap into the nightmares of a creature you can see within range and create an illusory manifestation of its deepest fears, visible only to that creature. The target must make a Wisdom saving throw. On a failed save, the target becomes frightened for the duration. At the end of each of the target''s turns before the spell ends, the target must succeed on a Wisdom saving throw or take 4d10 psychic damage. On a successful save, the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the damage increases by 1d10 for each slot level above 4th.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1561b7e7-db50-4f1a-8eb9-57c6566a07e6', 'Wall of Force', 'An invisible wall of force springs into existence at a point you choose within range. The wall appears in any orientation you choose, as a horizontal or vertical barrier or at an angle. It can be free floating or resting on a solid surface. You can form it into a hemispherical dome or a sphere with a radius of up to 10 feet, or you can shape a flat surface made up of ten 10-foot-by-10-foot panels. Each panel must be contiguous with another panel. In any form, the wall is 1/4 inch thick. It lasts for the duration. If the wall cuts through a creature''s space when it appears, the creature is pushed to one side of the wall (your choice which side).<br> Nothing can physically pass through the wall. It is immune to all damage and can''t be dispelled by <i>dispel magic</i>. A <i>disintegrate</i> spell destroys the wall instantly, however. The wall also extends into the Ethereal Plane, blocking ethereal travel through the wall.', 'a pinch of powder made by crushing a clear gemstone', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('081974e1-0779-4e02-b477-2ea9730ca3f6', 'Contingency', 'Choose a spell of 5th level or lower that you can cast, that has a casting time of 1 action, and that can target you. You cast that spell, called the contingent spell, as part of casting <i>contingency</i>, expending spell slots for both, but the contingent spell doesn''t come into effect. Instead, it takes effect when a certain circumstance occurs. You describe that circumstance when you cast the two spells. For example, a <i>contingency</i> cast with <i>water breathing</i> might stipulate that <i>water breathing</i> comes into effect when you are engulfed in water or a similar liquid.<br> The contingent spell takes effect immediately after the circumstance is met for the first time, whether or not you want it to, and then <i>contingency</i> ends.<br> The contingent spell takes effect only on you, even if it can normally target others. You can use only one <i>contingency</i> spell at a time. If you cast this spell again, the effect of another <i>contingency</i> spell on you ends. Also, <i>contingency</i> ends on you if its material component is ever not on your person.', 'a statuette of yourself carved from ivory and decorated with gems worth at least 1,500 gp', 'Self', 'VSMgp', NULL, '10 minutes', false, 'up to 1 hour', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8b15b8eb-821f-4db4-94de-87f67c840b0f', 'Magic Jar', 'Your body falls into a catatonic state as your soul leaves it and enters the container you used for the spell''s material component. While your soul inhabits the container, you are aware of your surroundings as if you were in the container''s space. You can''t move or use reactions. The only action you can take is to project your soul up to 100 feet out of the container, either returning to your living body (and ending the spell) or attempting to possess a humanoids body.<br> You can attempt to possess any humanoid within 100 feet of you that you can see (creatures warded by a <i>protection from evil and good</i> or <i>magic circle</i> spells can''t be possessed). The target must make a Charisma saving throw. On a failure, your soul moves into the target''s body, and the target''s soul becomes trapped in the container. On a success, the target resists your efforts to possess it, and you can''t attempt to possess it again for 24 hours.<br> Once you possess a creature''s body, you control it. Your game statistics are replaced by the statistics of the creature though you retain your alignment and your Intelligence, Wisdom, and Charisma scores. You retain the benefit of your own class feature. If the target has any class levels, you can''t use any of its class features.<br> Meanwhile, the possessed creature''s soul can perceive from the container using its own senses, but it can''t move or take actions at all.<br> While possessing a body, you can use your action to return from the host body to the container if it is within 100 feet of you, returning the host creature''s soul to its body. If the host body dies while you''re in it, the creature dies, and you must make a Charisma saving throw against your own spellcasting DC. On a success, you return to the container if it is within 100 feet of you. Otherwise, you die.<br> If the container is destroyed or the spell ends, your soul immediately returns to your body. If your body is more than 100 feet away from you, or if your body is dead when you attempt to return to it, you die. If another creature''s soul is in the container when it is destroyed, the creature''s soul returns to its body if the body is alive and within 100 feet. Otherwise, that creature dies.<br> When the spell ends, the container is destroyed.', 'a gem, crystal, reliquary, or some other ornamental container worth at least 500 gp', 'Self', 'VSMgp', NULL, '1 minute', false, 'up to 10 minutes', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('47edab90-d136-4e19-9210-1eea5d7cfadc', 'Wall of Ice', 'You create a wall of ice on a solid surface within range. You can form it into a hemispherical dome or a sphere with radium of up to 10 feet, or you can shape a flat surface made up of ten 10-foot-square panels. Each panel must be contiguous with another panel. In any form, the wall is 1 foot thick and lasts for the duration.<br> If the wall cuts through a creature''s space when it appears, the creature within its area is pushed to one side of the wall and must make a Dexterity saving throw. On a failed save, the creature takes 10d6 cold damage, or half as much damage on a successful save.<br> The wall is an object that can be damaged and thus breached. It has AC 12 and 30 hit points per 10-foot section, and it is vulnerable to fire damage. Reducing a 10-foot section of wall to 0 hit points destroys it and leaves behind a sheet of frigid air in the space the wall occupied. A creature moving through the sheet of frigid air for the first time on a turn must make a Constitution saving throw. The creature takes 5d6 cold damage on a failed save, or half as much damage on a successful one.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, the damage the wall deals when it appears increases by 2d6, and the damage from passing through the sheet of frigid air increases by 1d6 for each slot level above 6th.', 'a small piece of quartz', '120 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b2bf2ebe-8486-49ed-a29d-699ae32c0df1', 'Sequester', 'By means of this spell, a willing creature or an object can be hidden away, safe from detection for the duration. When you cast the spell and touch the target, it becomes invisible and can''t be targeted by divination spells or perceived through scrying sensors created by the divination of spells.<br> If the target is a creature, it falls into a state of suspended animation. Time ceases to flow for it, and it doesn''t grow older.<br> You can set a condition for the spell to end early. The condition can be anything you choose, but it must occur or be visible within 1 mile of the target. Examples include after 1,000 years or when the tarrasque awakes. This spells also ends if the target takes any damage.', 'a powder composed of diamond, emerald, ruby, and sapphire dust worth at least 5,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 action', false, 'Until dispelled', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('8857b296-7c87-43a6-a1dc-56fcc59a716b', '3', 'Your magic and an offering put you in contact with a god or a god''s servants. You ask a single question concerning a specific goal, event, or activity to occur within 7 days. The DM offers a truthful reply. The reply might be a short phrase, a cryptic rhyme, or an omen.<br> The spell doesn''t take into account any possible circumstances that might change the outcome, such as the casting of additional spells or the loss or gain of a companion.<br> If you cast this spell two or more times before finishing your next long rest, there is a cumulative 25 percent chance for each casting after the first that you get a random reading. The DM makes this roll in secret.', 'incense and a sacrificial offering appropriate to your religion, together worth at least 25 gp, which the spell consumes', 'Self', 'VSMgp', NULL, '1 action', true, 'Instantaneous', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bf1198b7-5a68-4985-8e00-48730f462a6d', 'Simulacrum', 'You shape an illusory duplicate of one beast or humanoid that is within range for the entire casting time of the spell. The duplicate is a creature, partially real and formed from ice or snow, and it can take actions and otherwise be affected as a normal creature. It appears to be the same as the original, but it has half the creature''s hit point maximum and is formed without any equipment. Otherwise, the illusion uses all the statistics of the creature it duplicates.<br> The simulacrum is friendly to you and creatures you designate. It obeys your spoken commands, moving and acting in accordance with your wishes and acting on your turn in combat. The simulacrum lacks the ability to learn or become more powerful, so it never increases its level or other abilities, nor can it regain expended spell slots.<br> If the simulacrum is damaged, you can repair it in an alchemical laboratory, using rare herbs and minerals worth 100 gp per hit point it regains. The simulacrum lasts until it drops to 0 hit points, at which point it reverts to snow and melts instantly.<br> If you cast this spell again, any currently active duplicates you created with this spell are instantly destroyed.', 'snow or ice in quantities sufficient to made a life-size copy of the duplicated creature some hair, fingernail clippings, or other piece of that creature''s body placed inside the snow or ice and powdered ruby worth 1,500 gp, sprinkled over the duplicate and consumed by the spell', 'Touch', 'VSMgp', NULL, '12 hours', false, 'Until dispelled', false, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('567b100e-3ea4-430e-a59c-d9a3d9c63ae8', 'Find Greater Steed', 'You summon a spirit that assumes the form of a loyal, majestic mount. Appearing in an unoccupied space within range, the spirit takes on a form you choose: a griffon, a pegasus, a peryton , a dire wolf, a rhinoceros, or a saber-toothed tiger. The creature has the statistics provided in the <i>Monster Manual</i> for the chosen form, though it is a celestial, a fey, or a fiend (your choice) instead of its normal creature type. Additionally, if it has an Intelligence score of 5 or lower, its Intelligence becomes 6, and it gains the ability to understand one language of your choice that you speak.<br> You control the mount in combat. While the mount is within 1 mile of you, you can communicate with it telepathically. While mounted on it, you can make any spell you cast that targets only you also target the mount.<br> The mount disappears temporarily when it drops to 0 hit points or when you dismiss it as an action. Casting this spell again re-summons the bonded mount, with all its hit points restored and any conditions removed.<br> You can''t have more than one mount bonded by this spell or <i>find steed</i> at the same time. As an action, you can release a mount from its bond, causing it to disappear permanently.<br> Whenever the mount disappears, it leaves behind any objects it was wearing or carrying.', '', '30 feet', 'VS', NULL, '10 minutes', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('781cea11-df5f-460d-8ea3-d90fec0dfda9', 'Clone', 'This spell grows an inert duplicate of a living creature as a safeguard against death. This clone forms inside as a sealed vessel and grows to full size and maturity after 120 days you can also choose to have the clone be a younger version of the same creature. It remains inert and endures indefinitely, as long as its vessel remains undisturbed.<br> At any time after the clone matures, if the original creature dies, its soul transfers to the clone, provided that the soul is free and willing to return. The clone is physically identical to the original and has the same personality, memories, and abilities, but none of the original''s equipment. The original creature''s physical remains, if they still exist, become inert and can''t thereafter be restored to life, since the creature''s soul is elsewhere.', 'a diamond worth at least 1,000 gp and at least 1 cubic inch of flesh of the creature that is to be cloned, which the spell consumes, and a vessel worth at least 2,000 gp that has a sealable lid and is large enough to hold a Medium creature, such as a huge urn, coffin, mud-filled cyst in the ground, or crystal container filled with salt water', 'Touch', 'VSMgp', NULL, '1 hour', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('127ee50d-f445-424a-ad78-3c6da26ac67c', 'Grasping Vine', 'You conjure a vine that sprouts from the ground in an unoccupied space of your choice that you can see within range. When you cast this spell, you can direct the vine to lash out at a creature within 30 feet of it that you can see. That creature must succeed on a Dexterity saving throw or be pulled 20 feet directly toward the vine.<br> Until the spell ends, you can direct the vine to lash out at the same creature or another one as a bonus action on each of your turns.', '', '30 feet', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('00d00260-f91b-4a4e-b103-e5768edf5be4', 'Mental Prison', 'You attempt to bind a creature within an illusory cell that only it perceives. One creature you can see within range must make an Intelligence saving throw. The target succeeds automatically if it is immune to being charmed. On a successful save, the target takes 5d10 psychic damage, and the spell ends. On a failed save, the target takes 5d10 psychic damage, and you make the area immediately around the target''s space appear dangerous to it in some way. You might cause the target to perceive itself as being surrounded by fire, floating razors, or hideous maws filled with dripping teeth. Whatever form the illusion takes, the target can''t see or hear anything beyond it and is restrained for the spell''s duration. If the target is moved out of the illusion, makes a melee attack through it, or reaches any part of its body through it, the target takes 10d10 psychic damage, and the spell ends.', '', '60 feet', 'S', NULL, '1 action', false, '24 hours', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('711a11bb-6245-4282-8818-dc1e0c6ee35f', 'Primordial Ward', 'You have resistance to acid, cold, fire, lightning, and thunder damage for the spell''s duration.<br> When you take damage of one of those types, you can use your reaction to gain immunity to that type of damage, including against the triggering damage. If you do so, the resistances end, and you have the immunity until the end of your next turn, at which time the spell ends.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('34d2652c-4622-4afd-9952-6201bf70f822', 'Prismatic Wall', 'A shimmering, multicolored plane of light forms a vertical opaque wall - up to 90 feet long, 30 feet high, and 1 inch thick - entered on a point you can see within range. Alternatively, you can shape the wall into a sphere up to 30 feet in diameter centered on a point you choose within range. The wall remains in place for the duration. If you position the wall so that it passes through a space occupied by a creature, the spell fails, and your action and the spell slot are wasted.<br> The wall sheds bright light out to a range of 100 feet and dim light for an additional 100 feet. You and creatures you designate at the time you cast the spell can pass through and remain near the wall without harm. If another creature that can see the wall moves to within 20 feet of it or starts its turn there, the creature must succeed on a Constitution saving throw or become blinded for 1 minute.<br> The wall consists of seven layers, each with a different color. When a creature attempts to reach into or pass through the wall, it does so one layer at a time through all the wall''s layers. As it passes or reaches through each layer, the creature must make a Dexterity saving throw or be affected by that layer''s properties as described below.<br> The wall can be destroyed, also one layer at a time, in order from red to violet, by means specific to each layer. Once a layer is destroyed, it remains so for the duration of the spell. A <i>rod of cancellation</i> destroys a <i>prismatic wall</i>, but an <i>antimagic field</i> has no effect on it.<br> <b>Red</b>: The creature takes 10d6 fire damage on a failed save, or half as much damage on a successful one. While this layer is in place, nonmagical ranged attacks can''t pass through the wall. The layer can be destroyed by dealing at least 25 cold damage to it.<br> <b>Orange</b>: The creature takes 10d6 acid damage on a failed save, or half as much damage on a successful one. While this layer is in place, magical ranged attacks can''t pass through the wall. The layer is destroyed by a strong wind.<br> <b>Yellow</b>: The creature takes 10d6 lightning damage on a failed save, or half as much damage on a successful one. This layer can be destroyed by dealing at least 60 force damage to it.<br> <b>Green</b>: The creature takes 10d6 poison damage on a failed save, or half as much damage on a successful one. A <i>passwall</i> spell, or another spell of equal or greater level that can open a portal on a solid surface, destroys this layer.<br> <b>Blue</b>: The creature takes 10d6 cold damage on a failed save, or half as much damage on a successful one. This layer can be destroyed by dealing at least 25 fire damage to it.<br> <b>Indigo</b>: On a failed save, the creature is restrained. It must then make a Constitution saving throw at the end of each of its turns. If it successfully saves three times, the spell ends. If it fails its save three times, it permanently turns to stone and is subjected to the petrified condition. The successes and failures don''t need to be consecutive, keep track of both until the creature collects three of a kind.<br> While this layer is in place, spells can''t be cast through the wall. The layer is destroyed by bright light shed by a <i>daylight</i> spell or a similar spell of equal or higher level.<br> <b>Violet</b>: On a failed save, the creature is blinded. It must then make a Wisdom saving throw at the start of your next turn. A successful save ends the blindness. If it fails that save, the creature is transported to another plane of the DM''s choosing and is no longer blinded. (Typically, a creature that is on a plane that isn''t its home plane is banished home, while other creatures are usually cast into the Astral or Ethereal planes.) This layer is destroyed by a <i>dispel magic</i> spell or a similar spell of equal or higher level that can end spells and magical effects.', '', '60 feet', 'VS', NULL, '1 action', false, '10 minutes', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f8ec9098-ef87-4940-8766-35422a1f54d0', 'Guardian of Nature', 'A nature spirit answers your call and transforms you into a powerful guardian. The transformation lasts until the spell ends. You choose one of the following forms to assume: Primal Beast or Great Tree.<br> <b>Primal Beast</b>: Bestial fur covers your body, your facial features become feral, and you gain the following benefits:<br>  Your walking speed increases by 10 feet.<br>  You gain darkvision with a range of 120 feet.<br>  You make Strength-based attack rolls with advantage.<br>  Your melee weapon attacks deal an extra 1d6 force damage on a hit.<br> <b>Great Tree</b>: Your skin appears barky, leaves sprout from your hair, and you gain the following benefits:<br>  You gain 10 temporary hit points.<br>  You make Constitution saving throws with advantage.<br>  You make Dexterity- and Wisdom-based attack rolls with advantage.<br>  While you are on the ground, the ground within 15 feet of you is difficult terrain for your enemies.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1654d2e3-503d-4af0-b4b9-d0878e63f4ce', 'Weird', 'Drawing on the deepest fears of a group of creatures, you create illusory creatures in their minds, visible only to them. Each creature in a 30-foot-radius sphere centered on a point of your choice within range must make a Wisdom saving throw. On a failed save, a creature becomes frightened for the duration. The illusion calls on the creature''s deepest fears, manifesting its worst nightmares as an implacable threat. At the end of each of the frightened creature''s turns, it must succeed on a Wisdom saving throw or take 4d10 psychic damage. On a successful save, the spell ends for that creature.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d0e402cc-d5f6-4c20-a269-baed6e9d2147', 'Finger of Death', 'You send negative energy coursing through a creature that you can see within range, causing it searing pain. The target must make a Constitution saving throw. It takes 7d8+30 necrotic damage on a failed save, or half as much damage on a successful one.<br> A humanoid killed by this spell rises at the start of your next turn as a zombie that is permanently under your command, following your verbal orders to the best of its ability.', '', '60 feet', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('90d8018f-7eb3-483f-a838-83b2512868c2', 'Maze', 'You banish a creature that you can see within range into a labyrinthine demiplane. The target remains there for the duration or until it escapes the maze.<br> The target can use its action to attempt to escape. When it does so, it makes a DC 20 Intelligence check. If it succeeds, it escapes, and the spell ends (a minotaur or goristro demon automatically succeeds).<br> When the spell ends, the target reappears in the space it left or, if that space is occupied, in the nearest unoccupied space.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b3fa33fe-3204-4b9b-b45f-08b8882bf08a', 'Symbol', 'When you cast this spell, you inscribe a harmful glyph either on a surface (such as a section of floor, a wall, or a table) or within an object that can be closed to conceal the glyph (such as a book, a scroll, or a treasure chest). If you choose a surface, the glyph can cover an area of the surface no larger than 10 feet in diameter. If you choose an object, that object must remain in its place, if the object is moved more than 10 feet from where you cast this spell, the glyph is broken, and the spell ends without being triggered.<br> The glyph is nearly invisible, requiring an Intelligence (Investigation) check against your spell save DC to find it.<br> You decide what triggers the glyph when you cast the spell. For glyphs inscribed on a surface, the most typical triggers include touching or stepping on the glyph, removing another object covering it, approaching within a certain distance of it, or manipulating the object that holds it. For glyphs inscribed within an object, the most common triggers are opening the object, approaching within a certain distance of it, or seeing or reading the glyph.<br> You can further refine the trigger so the spell is activated only under certain circumstances or according to a creature''s physical characteristics (such as height or weight), or physical kind (for example, the ward could be set to affect hags or shapechangers). You can also specify creatures that don''t trigger the glyph, such as those who say a certain password.<br> When you inscribe the glyph, choose one of the options below for its effect. Once triggered, the glyph glows, filling a 60-foot-radius sphere with dim light for 10 minutes, after which time the spell ends. Each creature in the sphere when the glyph activates is targeted by its effect, as is a creature that enters the sphere for the first time on a turn or ends its turn there.<br> <b>Death</b>: Each target must make a Constitution saving throw, taking 10d10 necrotic damage on a failed save, or half as much damage on a successful save.<br> <b>Discord</b>: Each target must make a Constitution saving throw. On a failed save, a target bickers and argues with other creatures for 1 minute. During this time, it is incapable of meaningful communication and has disadvantage on attack rolls and ability checks.<br> <b>Fear</b>: Each target must make a Wisdom saving throw and becomes frightened for 1 minute on a failed save. While frightened, the target drops whatever it is holding and must move at least 30 feet away from the glyph on each of its turns, if able.<br> <b>Hopelessness</b>: Each target must make a Charisma saving throw. On a failed save, the target is overwhelmed with despair for 1 minute. During this time, it can''t attack or target any creature with harmful abilities, spells, or other magical effects.<br> <b>Insanity</b>: Each target must make an Intelligence saving throw. On a failed save, the target is driven insane for 1 minute. An insane creature can''t take actions, can''t understand what other creatures say, can''t read, and speaks only in gibberish. The DM controls its movement, which is erratic.<br> <b>Pain</b>: Each target must make a Constitution saving throw and becomes incapacitated with excruciating pain for 1 minute on a failed save.<br> <b>Sleep</b>: Each target must make a Wisdom saving throw and falls unconscious for 10 minutes on a failed save. A creature awakens if it takes damage or if someone uses an action to shake or slap it awake.<br> <b>Stunning</b>: Each target must make a Wisdom saving throw and becomes stunned for 1 minute on a failed save.', 'mercury, phosphorus, and powdered diamond and opal with a total value of at least 1,000 gp, which the spell consumes', 'Touch', 'VSMgp', NULL, '1 minute', false, 'Until dispelled or triggered', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bd83c747-da54-4b84-a944-97b1bd3ea8a9', 'Glibness', 'Until the spell ends, when you make a Charisma check, you can replace the number you roll with a 15. Additionally, no matter what you say, magic that would determine if you are telling the truth indicates that you are being truthful.', '', 'Self', 'V', NULL, '1 action', false, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cc097d9c-0e9f-49e8-a2e5-ea492ef320be', 'Dancing Lights', 'You create up to 4 torch-sized lights within range, making them appear as torches, lanterns, or glowing orbs that hover in the air for the duration. You can also combine the 4 lights into 1 glowing vaguely humanoid form of Medium size. Whichever form you choose, each light sheds dim light in a 10-foot radius.<br> As a bonus action on your turn, you can move the lights up to 60 feet to a new spot within range. A light must be within 20 feet of another light created by this spell, and a light winks out if it exceeds the spell''s range.', 'Phosphorus, wychwood or glowworm', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('029130b4-9ea8-4b10-ae1b-d053d2b65f33', 'Summon Elemental', 'You call forth an elemental spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Elemental Spirit stat block. When you cast the spell, choose an element: Air, Earth, Fire, or Water. The creature resembles a bipedal form wreathed in the chosen element, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, use the higher level wherever the spell''s level appears in the stat block.', 'air, a pebble, ash, and water inside a crystal vial worth at least 400 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d3c3f337-42e2-4616-b916-ea86e9a65d7d', 'Cure Wounds', 'A creature you touch regains a number of hit points equal to 1d8 + your spellcasting ability modifier. This spell has no effect on undead or constructs.<br><br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 2nd level or higher, the healing increases by 1d8 for each slot level above 1st.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 1);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('334896dd-a82c-4133-98e1-586147a07bb1', 'Chill Touch', 'You create a ghostly, skeletal hand in the space of a creature within range. Make a ranged spell attack against the creature to assail it with the chill of the grave. On a hit, the target takes 1d8 necrotic damage, and it can''t regain hit points until the start of your next turn. Until then, the hand clings to the target.<br> If you hit an undead target, it also has disadvantage on attack rolls against you until the end of your next turn.<br> This spell''s damage increases by 1d8 when you reach 5th level (2d8), 11th level (3d8), and 17th level (4d8).', '', '120 feet', 'VS', NULL, '1 action', false, '1 round', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 0);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('94405a25-5631-4184-b784-a531c5c9d9f1', 'Sickening Radiance', 'Dim, greenish light spreads within a 30-foot-radius sphere centered on a point you choose within range. The light spreads around corners, and it lasts until the spell ends.<br> When a creature moves into the spell''s area for the first time on a turn or starts its turn there, that creature must succeed on a Constitution saving throw or take 4d10 radiant damage, and it suffers one level of exhaustion and emits a dim, greenish light in a 5-foot radius. This light makes it impossible for the creature to benefit from being invisible. The light and any levels of exhaustion caused by this spell go away when the spell ends.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c42f5ff2-cdec-495e-8f03-29fdab01d071', 'Staggering Smite', 'The next time you hit a creature with a melee weapon attack during this spell''s duration, your weapon pierces both body and mind, and the attack deals an extra 4d6 psychic damage to the target. The target must make a Wisdom saving throw. On a failed save, it has disadvantage on attack rolls and ability checks, and can''t take reactions, until the end of its next turn.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('2cc98c53-bd32-4c08-98fe-90493b34cf96', 'Storm Sphere', 'A 20-foot-radius sphere of whirling air springs into existence centered on a point you choose within range. The sphere remains for the spell''s duration. Each creature in the sphere when it appears or that ends its turn there must succeed on a Strength saving throw or take 2d6 bludgeoning damage. The sphere''s space is difficult terrain.<br> Until the spell ends, you can use a bonus action on each ofyour turns to cause a bolt of lightning to leap from the center of the sphere toward one creature you choose within 60 feet of the center. Make a ranged spell attack. You have advantage on the attack roll if the target is in the sphere. On a hit, the target takes 4d6 lightning damage.<br> Creatures within 30 feet of the sphere have disadvantage on Wisdom (Perception) checks made to listen.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the damage for each of its effects increases by 1d6 for each slot level above 4th.', '', '150 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('93f08e02-5cc6-4302-ad37-5f6fc595ba46', 'Summon Aberration', 'You call forth an aberrant spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Aberrant Spirit stat block. When you cast the spell, choose Beholderkin, Slaad, or Star Spawn. The creature resembles an aberration of that kind, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it take the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, use the higher level wherever the spell''s level appears on the stat block.', 'a pickled tentacle and an eyeball in a platinum inlaid vial worth at least 400 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('eddbdf6c-9721-4c8c-a34f-c2274038ec4a', 'Summon Construct', 'You call forth the spirit of a construct. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Construct Spirit stat block. When you cast the spell, choose a material: Clay, Metal, or Stone. The creature resembles a golem or a modron (your choice) made of the chosen material, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 4th level or higher, use the higher level wherever the spell''s level appears in the stat block.', 'an ornate stone and metal lockbox worth at least 400 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('74a2ea76-f61f-45dd-9f5e-2eb97e1bcb74', 'Summon Greater Demon', 'You utter foul words, summoning one demon from the chaos of the Abyss. You choose the demon''s type, which must be one of challenge rating 5 or lower, such as a shadow demon or a barlgura. The demon appears in an unoccupied space you can see within range, and the demon disappears when it drops to 0 hit points or when the spell ends.<br> Roll initiative for the demon, which has its own turns. When you summon it and on each of your turns thereafter, you can issue a verbal command to it (requfring no action on your part), telling it what it must do on its next turn. If you issue no command, it spends its turn attacking any creature within reach that has attacked it.<br> At the end of each of the demon''s turns, it makes a Charisma saving throw. The demon has disadvantage on this saving throw if you say its true name. On a failed save, the demon continues to obey you. On a successful save, your control of the demon ends for the rest of the duration, and the demon spends its turns pursuing and attacking the nearest non-demons to the best of its ability. If you stop concentrating on the spell before it reaches its full duration, an uncontrolled demon doesn''t disappear for 1d6 rounds if it still has hit points.<br> As part of casting the spell, you can form a circle on the ground with the blood used as a material component. The circle is large enough to encompass your space. While the spell lasts, the summoned demon can''t cross the circle or harm it, and it can''t target anyone within it. Using the material component in this manner consumes it when the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 5th level or higher, the challenge rating increases by 1 for each slot level above 4th.', 'a vial of blood from a humanoid killed within the past 24 hours', '60 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4360f744-882a-4b0d-8b4c-b8091db90dd6', 'Vitriolic Sphere', 'You point at a location within range, and a glowing 1-foot ball of emerald acid streaks there and explodes in a 20-foot radius sphere. Each creature in that area must make a Dexterity saving throw. On a failed save, a creature takes 10d4 acid damage and 5d4 acid damage at the end of its next turn. On a successful save, a creature takes half the initial damage and no damage at the end of its next turn.<br> <b>At Higher Levels</b>. When you cast this spell using a spell slot of 5th level or higher, the initial damage increases by 2d4 for each slot level above 4th.', 'a drop of giant slug bile', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d2137d0c-b1c4-48dc-8a57-3e9130013ead', 'Power Word Pain', 'You speak a word of power that causes waves of intense pain to assail one creature you can see within range. If the target has 100 hit points or fewer, it is subject to crippling pain. Otherwise, the spell has no effect on it. A target is also unaffected if it is immune to being charmed.<br> While the target is affected by crippling pain, any speed it has can be no higher than 10 feet. The target also has disadvantage on attack rolls, ability checks, and saving throws, other than Constitution saving throws. Finally, if the target tries to cast a spell, it must first succeed on a Constitution saving throw, or the casting fails and the spell is wasted.<br> A target suffering this pain can make a Constitution saving throw at the end ofeach of its turns. On a successful save, the pain ends.', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('0a474012-5f18-4625-a61b-687f167be5fa', 'Watery Sphere', 'You conjure up a sphere of water with a 5-foot radius on a point you can see within range. The sphere can hover but no more than 10 feet off the ground. The sphere remains for the spell''s duration.<br> Any creature in the sphere''s space must make a Strength saving throw. On a successful save, a creature is ejected from that space to the nearest unoccupied space of the creature''s choice outside the sphere. A Huge or larger creature succeeds on the saving throw automatically, and a Large or smaller creature can choose to fail it. On a failed save, a creature is restrained by the sphere and is engulfed by the water. At the end of each of its turns, a restrained target can repeat the saving throw, ending the effect on itself on a success.<br> The sphere can restrain as many as four Medium or smaller creatures or one Large creature. If the sphere restrains a creature that causes it to exceed this capacity, a random creature that was already restrained by the sphere falls out of it and lands prone in a space within 5 feet of it.<br> As an action, you can move the sphere up to 30 feet in a straight line. If it moves over a pit, a cliff, or other drop-off, it safely descends until it is hovering 10 feel above the ground. Any creature restrained by the sphere moves with it. You can ram the sphere into creatures, forcing them to make the saving throw.<br> When the spell ends, the sphere falls to the ground and extinguishes all normal flames within 30 feet of it. Any creature restrained by the sphere is knocked prone in the space where it falls. The water then vanishes.', 'a droplet of water', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 4);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c6f00c08-0c20-4028-b1ed-657549aa25f2', 'Banishing Smite', 'The next time you hit a creature with a weapon attack before this spell ends, your weapon crackles with force, and the attack deals an extra 5d10 force damage to the target. Additionally, if this attack reduces the target to 50 hit points of fewer, you banish it. If the target is native to a different plane of existence than the one you''re on, the target disappears, returning to its home plane. If the target is native to the plane you''re on, the creature vanishes into a harmless demiplane. While there, the target is incapacitated. It remains there until the spell ends, at which point the target reappears in the space it left or in the nearest unoccupied space if that space is occupied.', '', 'Self', 'V', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5fd4088d-fe37-4fc2-bb36-f41bc5e16658', 'Circle of Power', 'Divine energy radiates from you, distorting and diffusing magical energy within 30 feet of you. Until the spell ends, the sphere moves with you, centered on you. For the duration, each friendly creature in the area (including you) has advantage on saving throws against spells and other magical effects. Additionally, when an affected creature succeeds on a saving throw made against a spell or magical effect that allows it to make a saving throw to take only half damage, it instead takes no damage if it succeeds on the saving throw.', '', 'Self (30-foot radius)', 'V', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('bd9ccbd4-fd3c-4812-9dc4-6247ba6755e1', 'Conjure Volley', 'You fire a piece of nonmagical ammunition from a ranged weapon or throw a nonmagical weapon into the air and choose a point within range. Hundreds of duplicates of the ammunition or weapon fall in a volley from above and then disappear. Each creature in a 40-foot-radius. 20-foot-high cylinder centered on that point must make a Dexterity saving throw. A creature takes 8d8 damage on a failed save, or half as much damage on a successful one. The damage type is the same as that of the ammunition or weapon.', 'one piece of ammunition or one thrown weapon', '150 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3451e9eb-12d3-41b8-8cac-8e4feff58c1e', 'Control Winds', 'You take control of the air in a 100-foot cube that you can see within range. Choose one of the following effects when you cast the spell. The effect lasts for the spell''s duration, unless you use your action on a later turn to switch to a different effect. You can also use your action to temporarily halt the effect or to restart one you''ve halted.<br> <b>Gusts</b>: A wind picks up within the cube, continually blowing in a horizontal direction you designate. You choose the intensity of the wind: calm, moderate, or strong. If the wind is moderate or strong, ranged weapon attacks that enter or leave the cube or pass through it have disadvantage on their attack rolls. If the wind is strong, any creature moving against the wind must spend 1 extra foot of movement for each foot moved.<br> <b>Downdraft</b>: You cause a sustained blast of strong wind to blow downward from the top of the cube. Ranged weapon attacks that pass through the cube or that are made against targets within it have disadvantage on their attack rolls . A creature must make a Strength saving throw if it flies into the cube for the first time on a turn or starts its turn there flying. On a failed save, the creature is knocked prone.<br> <b>Updraft</b>: You cause a sustained updraft within the cube, rising upward from the cube''s bottom side. Creatures that end a fall within the cube take only half damage from the fall. When a creature in the cube makes a vertical jump, the creature can jump up to 10 feet higher than normal.', '', '300 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('4a213b03-9037-42d0-81d0-e44327ceba5a', 'Danse Macabre', 'Threads of dark power leap from your fingers to pierce up to five Small or Medium corpses you can see within range. Each corpse immediately stands up and becomes undead. You decide whether it is a zombie or a skeleton (the statistics for zombies and skeletons are in the <i>Monster Manual</i>), and it gains a bonus to its attack and damage rolls equal to your spellcasting ability modifier.<br> You can use a bonus action to mentally command the creatures you make with this spell , issuing the same command to all of them. To receive the command, a creature must be within 60 feet of you. You decide what action the creatures will take and where they will move during their next turn, or you can issue a general command, such as to guard a chamber or passageway against your foes. If you issue no commands, the creatures do nothing except defend themselves against hostile creatures. Once given an order, the creatures continue to follow it until their task is complete.<br> The creatures are under your control until the spell ends, after which they become inanimate once more.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, you animate up to two additional corpses for each slot level above 5th.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 hour', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a8de8804-d133-48ba-a4a4-15bafc2b06f5', 'Dawn', 'The Light of dawn shines down on a location you specify within range. Until the spell ends, a 30-foot-radius, 40-foot-high cylinder of bright light glimmers there. This light is sunlight.<br> When the cylinder appears, each creature in it must make a Constitution saving throw, taking 4d10 radiant damage on a failed save, or half as much damage on a successful one. A creature must also make this saving throw whenever it ends its turn in the cylinder.<br> If you''re within 60 feet of the cylinder, you can move it up to 60 feet as a bonus action on your turn.', 'a sunburst pendant worth at least 100gp', '60 feet', 'VSMgp', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('05224b18-5363-412e-9b1c-bba260e113bd', 'Destructive Wave', 'You strike the ground, creating a burst of divine energy that ripples outward from you. Each creature you choose within 30 feet of you must succeed on a Constitution saving throw or take 5d6 thunder damage, as well as 5d6 radiant or necrotic damage (your choice), and be knocked prone. A creature that succeeds on its saving throw takes half as much damage and isn''t knocked prone.', '', 'Self (30-foot radius)', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('95dd909e-7c60-4451-90d3-942376bcfaf8', 'Enervation', 'A tendril of inky darkness reaches out from you, touching a creature you can see within range to drain life from it. The target must make a Dexterity saving throw. On a successful save, the target takes 2d8 necrotic damage, and the spell ends . On a failed save, the target takes 4d8 necrotic damage, and until the spell ends, you can use your action on each of your turns to automatically deal 4d8 necrotic damage to the target. The spell ends if you use your action to do anything else, if the target is ever outside the spell''s range, or if the target has total cover from you.<br> Whenever the spell deals damage to a target, you regain hit points equal to half the amount of necrotic damage the target takes.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage increases by 1d8 for each slot level above 5th.', '', '60 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6cdc831d-c8d8-49c2-b922-989275bbd709', 'Holy Weapon', 'You imbue a weapon you touch with holy power. Until the spell ends, the weapon emits bright light in a 30-foot radius and dim light for an additional 30 feet. In addition, weapon attacks made with it deal an extra 2d8 radiant damage on a hit. If the weapon isn''t already a magic weapon, it becomes one for the duration.<br> As a bonus action on your turn, you can dismiss this spell and cause the weapon to emit a burst of radiance. Each creature of your choice that you can see within 30 feet of you must make a Constitution saving throw. On a failed save, a creature takes 4d8 radiant damage, and it is blinded for 1 minute. On a successful save, a creature takes half as much damage and isn''t blinded. At the end of each of its turns, a blinded creature can make a Constitution saving throw, ending the effect on itself on a success.', '', 'Touch', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('61dfd331-72fe-41a0-8e3d-0bfca45d6ef3', 'Immolation', 'Flames wreathe one creature you can see within range. The target must make a Dexterity saving throw. It takes 8d6 fire damage on a failed save, or half as much damage on a successful one. On a failed save, the target also burns for the spell''s duration. The burning target sheds bright light in a 30-foot radius and dim light for an additional 30 feet. At the end of each of its turns, the target repeats the saving throw. It takes 4d6 fire damage on a failed save, and the spell ends on a successful one. These magical flames can''t be extinguished by nonmagical means.<br> If damage from this spell kills a target, the target is turned to ash.', '', '90 feet', 'V', NULL, '1 action', false, 'up to 1 hour', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e6b52c6a-88bd-4ba1-88d9-aaffcdc97938', 'Power Word Stun', 'You speak a word of power that can overwhelm the mind of one creature you can see within range, leaving it dumbfounded. If the target has 150 hit points or fewer, it is stunned. Otherwise, the spell has no effect.<br> The stunned target must make a Constitution saving throw at the end of each of its turns. On a successful save, this stunning effect ends.', '', '60 feet', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('755cb803-a23a-41b9-892b-af0aaf0d4082', 'Infernal Calling', 'Uttering a dark incantation, you summon a devil from the Nine Hells. You choose the devil''s type, which must be one of challenge rating 6 or lower, such as a barbed devil or a bearded devil. The devil appears in an unoccupied space that you can see within range. The devil disappears when it drops to 0 hit points or when the spell ends.<br> The devil is unfriendly toward you and your companions. Roll initiative for the devil, which has its own turns. It is under the Dungeon Master''s control and acts according to its nature on each of its turns, which might result in its attacking you if it thinks it can prevail, or trying to tempt you to undertake an evil act in exchange for limited service. The DM has the creature''s statistics.<br> On each of your turns, you can try to issue a verbal command to the devil (no action required by you). It obeys the command if the likely outcome is in accordance with its desires, especially if the result would draw you toward evil. Otherwise, you must make a Charisma (Deception, Intimidation, or Persuasion) check contested by its Wisdom (Insight) check. You make the check with advantage if you say the devil''s true name. Ifyour check fails, the devil becomes immune to your verbal commands for the duration of the spell , though it can still carry out your commands if it chooses. If your check succeeds, the devil carries out your command- such as attack my enemies, explore the room ahead, or bear this message to the queen- until it completes the activity, at which point it returns to you to report having done so.<br> If your concentration ends before the spell reaches its full duration, the devil doesn''t disappear if it has become immune to your verbal commands. Instead, it acts in whatever manner it chooses for 3d6 minutes, and then it disappears.<br> If you possess an individual devil''s talisman, you can summon that devil if it is of the appropriate challenge rating plus 1, and it obeys all your commands, with no Charisma checks required.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the challenge rating increases by 1 for each slot level above 5th.', 'a ruby worth at least 999 gp', '90 feet', 'VSMgp', NULL, '1 minute', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cb054123-1c94-42b7-a35a-2ed5dcb77b62', 'Steel Wind Strike', 'You flourish the weapon used in the casting and then vanish to strike like the wind. Choose up to five creatures you can see within range. Make a melee spell attack against each target. On a hit, a target takes 6d10 force damage.<br> You can then teleport to an unoccupied space you can see within 5 feet of one of the targets you hit or missed.', 'a melee weapon worth at least 1 sp', '30 feet', 'SMgp', NULL, '1 action', false, 'up to 1 hour', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6fea3a4f-5960-4743-8c6d-1ecaefdef96f', 'Summon Celestial', 'You call forth a celestial spirit. It manifests in an angelic form in an unoccupied space that you can see within range. This corporeal form uses the Celestial Spirit stat block. When you cast the spell, choose Avenger or Defender. Your choice determines the creature''s attack in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, use the higher level whenever the spell''s level appears in the stat block.', 'a golden reliquary worth at least 500 gp', '90 feet', 'VSM', NULL, '1 action', false, 'Instantaneous', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c9cfbbc3-9c0d-4105-873c-47b7e52c9be4', 'Swift Quiver', 'You transmute your quiver so it produces an endless supply of nonmagical ammunition, which seems to leap into your hand when you reach for it.<br> On each of your turns until the spell ends, you can use a bonus action to make two attacks with a weapon that uses ammunition from the quiver. Each time you make such a ranged attack, your quiver magically replaces the piece of ammunition you used with a similar piece of nonmagical ammunition. Any pieces of ammunition created by this spell disintegrate when the spell ends. If the quiver leaves your possession, the spell ends.', 'a quiver containing at least one piece of ammunition', 'Touch', 'VSM', NULL, '1 bonus action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ca172569-6bb0-4950-ad3f-08d7ba26e1f6', 'Synaptic Static', 'You choose a point within range and cause psychic energy to explode there. Each creature in a 20-foot-radius sphere centered on that point must make an Intelligence saving throw. A creature with an Intelligence score of 2 or lower can''t be affected by this spell. A target takes 8d6 psychic damage on a failed save, or half as much damage on a successful one.<br> After a failed save, a target has muddled thoughts for 1 minute. During that time, it rolls a d6 and subtracts the number rolled from all its attack rolls and ability checks, as well as its Constitution saving throws to maintain concentration. The target can make an Intelligence saving throw at the end of each of its turns, ending the effect on itself on a success.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', false, NULL, '9ef0055e-fbc4-4e93-bec9-c9dc594703f7', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('d58b48fc-9a41-4f13-bbe6-a1dd916e4d08', 'Wall of Light', 'A shimmering wall of bright light appears at a point you choose within range. The wall appears in any orientation you choose: horizontally, vertically, or diagonally. It can be free floating, or it can rest on a solid surface. The wall can be up to 60 feet long, 10 feet high, and 5 feet thick. The wall blocks line of sight, but creatures and objects can pass through it. It emits bright light out to 120 feet and dim light for an additional 120 feet.<br> When the wall appears, each creature in its area must make a Constitution saving throw. On a failed save, a creature takes 4d8 radiant damage, and it is blinded for 1 minute. On a successful save, it takes half as much damage and isn''t blinded. A blinded creature can make a Constitution saving throw at the end of each of its turns, ending the effect on itself on a success.<br> A creature that ends its turn in the wall''s area takes 4d8 radiant damage.<br> Until the spell ends, you can use an action to launch a beam of radiance from the wall at one creature you can see within 60 feet of it. Make a ranged spell attack. On a hit, the target takes 4d8 radiant damage. Whether you hit or miss, reduce the length of the wall by 10 feet. If the wall''s length drops to 0 feet, the spell ends.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 6th level or higher, the damage increases by 1d8 for each slot level above 5th.', 'a hand mirror', '120 feet', 'VSM', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('cea358b2-671f-44e6-bdb6-abc9a798f5f6', 'Whirlwind', 'A whirlwind howls down to a point that you can see on the ground within range. The whirlwind is a 10-foot-radius, 30-foot-high cylinder centered on that point. Until the spell ends, you can use your action to move the whirlwind up to 30 feet in any direction along the ground. The whirlwind sucks up any Medium or smaller objects that aren''t secured to anything and that aren''t worn or carried by anyone.<br> A creature must make a Dexterity saving throw the first time on a turn that it enters the whirlwind or that the whirlwind enters its space, including when the whirlwind first appears. A creature takes 10d6 bludgeoning damage on a failed save, or half as much damage on a successful one. In addition, a Large or smaller creature that fails the save must succeed on a Strength saving throw or become restrained in the whirlwind until the spell ends. When a creature starts its turn restrained by the whirlwind, the creature is pulled 5 feet higher inside it, unless the creature is at the top. A restrained creature moves with the whirlwind and falls when the spell ends, unless the creature has some means to stay aloft.<br> A restrained creature can use an action to make a Strength or Dexterity check against your spell save DC. If successful, the creature is no longer restrained by the whirlwind and is hurled 3d6 x 10 feet away from it in a random direction.', 'a piece of straw', '300 feet', 'VM', NULL, '1 action', false, 'up to 1 minute', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('6466aa02-bfc2-4788-b100-7d65e2904c08', 'Transmute Rock', 'You choose an area of stone or mud that you can see that fits within a 40-foot cube and that is within range, and choose one of the following effects.<br> <b>Transmute Rock to Mud</b>: Nonmagical rock of any sort in the area becomes an equal volume of thick, flowing mud that remains for the spell''s duration.<br> The ground in the spell''s area becomes muddy enough that creatures can sink into it. Each foot that a creature moves through the mud costs 4 feet of movement, and any creature on the ground when you cast the spell must make a Strength saving throw. A creature must also make the saving throw when it moves into the area for the first time on a turn or ends its turn there. On a failed save, a creature sinks into the mud and is restrained, though it can use an action to end the restrained condition on itself by pulling itself free of the mud.<br> If you cast the spell on a ceiling, the mud falls. Any creature under the mud when it falls must make a Dexterity saving throw. A creature takes 4d8 bludgeoning damage on a failed save, or half as much damage on a successful one.<br> <b>Transmute Mud to Rock</b>: Nonmagical mud or quicksand in the area no more than 10 feet deep transforms into soft stone for the spell''s duration. Any creature in the mud when it transforms must make a Dexterity saving throw. On a successful save, a creature is shunted safely to the surface in an unoccupied space. On a failed save, a creature becomes restrained by the rock. A restrained creature, or a nother creature within reach, can use an action to try to break the rock by succeeding on a DC 20 Strength check or by dealing damage to it. The rock has AC 15 and 25 hit points, and it is immune to poison and psychic damage.', 'clay and water', '120 feet', 'VSM', NULL, '1 action', false, '1 round', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3f5905e7-a152-4f08-9413-8a54cde5f268', 'Invulnerability', 'You are immune to all damage until the spell ends.', 'a small piece of adamantine worth at least 500 gp, which the spell consumes', 'Self', 'VSMgp', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('ce1caae7-9ce7-4851-ba55-7562e6a2ad59', 'Wrath of Nature', 'You call out to the spirits of nature to rouse them against your enemies. Choose a point you can see within range. The spirits cause trees, rocks, and grasses in a 60-foot cube centered on that point to become animated until the spell ends.<br> <b>Grasses and Undergrowth</b>: Any area of ground in the cube that is covered by grass or undergrowth is difficult terrain for your enemies.<br> <b>Trees</b>: At the start of each of your turns, each of your enemies within 10 feet of any tree in the cube must succeed on a Dexterity saving throw or take 4d6 slashing damage from whipping branches.<br> <b>Roots and Vines</b>: At the end of each of your turns, one creature of your choice that is on the ground in the cube must succeed on a Strength saving throw or become restrained until the spell ends. A restrained creature can use an action to make a Strength (Athletics) check against your spell save DC, ending the effect on itself on a success.<br> <b>Rocks</b>: As a bonus action on your turn, you can cause a loose rock in the cube to launch at a creature you can see in the cube. Make a ranged spell attack against the target. On a hit, the target takes 3d8 nonmagical bludgeoning damage, and it must succeed on a Strength saving throw or fall prone.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 5);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7abc0c2c-0d60-491a-956a-19cc61250141', 'Arcane Gate', 'You create linked teleportation portals that remain open for the duration. Choose two points on the ground that you can see, one point within 10 feet of you and one point within 500 feet of you. A circular portal, 10 feet in diameter, opens over each point. If the portal would open in the space occupied by a creature, the spell fails, and the casting is lost.<br> The portals are two-dimensional glowing rings filled with mist, hovering inches from the ground and perpendicular to it at the points you choose. A ring is visible only from one side (your choice), which is the side that functions as a portal.<br> Any creature or object entering the portal exits from the other portal as if the two were adjacent to each other, passing through a portal from the non-portal side has no effect. The mist that fills each portal is opaque and blocks vision through it. On your turn, you can rotate the rings as a bonus action so that the active side faces in a different direction.', '', '500 feet', 'VS', NULL, '1 action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f65cfbec-9b58-45eb-8210-aa645758f11b', 'Bones of the Earth', 'You cause up to six pillars of stone to burst from places on the ground that you can see within range. Each pillar is a cylinder that has a diameter of 5 feet and a height of up to 30 feet. The ground where a pillar appears must be wide enough for its diameter, and you can target ground under a creature if that creature is Medium or smaller. Each pillar has AC 5 and 30 hit points. When reduced to 0 hit points, a pillar crumbles into rubble, which creates an area of difficult terrain with a 10-foot radius that lasts until the rubble is cleared.  Each 5-foot-diameter portion of the area requires at least 1 minute to clear by hand.<br> If a pillar is created under a creature, that creature must succeed on a Dexterity saving throw or be lifted by the pillar. A creature can choose to fail the save.<br> If a pillar is prevented from reaching its full height because of a ceiling or other obstacle, a creature on the pillar takes 6d6 bludgeoning damage and is restrained, pinched between the pillar and the obstacle. The restrained creature can use an action to make a Strength or Dexterity check (the creature''s choice) against the spell''s save DC. On a success, the creature is no longer restrained and must either move off the pillar or fall off it.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, you can create two additional pillars for each slot level above 6th.', '', '120 feet', 'VS', NULL, '1 action', false, 'up to 10 minutes', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7969ca1e-3c40-487e-a067-8689adf6415f', 'Power Word Heal', 'A wave of healing energy washes over the creature you touch. The target regains all its hit points. If the creature is charmed, frightened, paralyzed, or stunned, the condition ends. If the creature is prone, it can use its reaction to stand up. This spell has no effect on undead or constructs.', '', 'Touch', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a451b5f3-9a6d-49b3-8392-73cc1ecc480c', 'Create Homunculus', 'While speaking an intricate incantation, you cut yourself with a jewel-encrusted dagger, taking 2d4 piercing damage that can''t be reduced in any way. You then drip your blood on the spell''s other components and touch them, transforming them into a special construct called a homunculus.<br> T he statistics of the homunculus are in the <i>Monster Manual</i>. It is your faithful companion, and it dies if you die. Whenever you finish a long rest, you can spend up to half your Hit Dice if the homunculus is on the same plane of existence as you. When you do so, roll each die and add your Constitution modifier to it. Your hit point maximum is reduced by the total, and the homunculus''s hit point maximum and current hit points are both increased by it. This process can reduce you to no lower than 1 hit point, and the change to your and the homunculus''s hit points ends when you finish your next long rest. The reduction to your hit point maximum can''t be removed by any means before then, except by the homuoculus''s death.<br> You can have only one homunculus at a time. If you cast this spell while your homunculus lives, the spell fails .', 'clay, ash, and mandrake root, all of which the spell consumes, and a jewel-encrusted dagger worth at least 1,000 gp', 'Touch', 'VSMgp', NULL, '1 hour', false, '10 days', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('77a4cc1f-ee5f-4ceb-a4cf-9b9bc05fd2ae', 'Druid Grove', 'You invoke the spirits of nature to protect an area outdoors or underground. The area can be as small as a 30-foot cube or as large as a 90-foot cube. Buildings and other structures are excluded from the affected area. If you cast this spell in the same area every day for a year, the spell lasts until dispelled.<br> The spell creates the following effects within the area. When you cast this spell, you can specify creatures as friends who are immune to the effects. You can also specify a password that, when spoken aloud, makes the speaker immune to these effects.<br> The entire warded area radiates magic. A <i>dispel magic</i> cast on the area, if successful, removes only one of the following effects, not the entire area. That spell''s caster chooses which effect to end. Only when all its effects are gone is this spell dispelled.<br> <b>Solid Fog</b>: You can fill any number of 5-foot squares on the ground with thick fog, making them heavily obscured. The fog reaches 10 feet high. In addition, every foot of movement through the fog costs 2 extra feet. To a creature immune to this effect, the fog obscures nothing and looks like soft mist, with motes of green light floating in the air.<br> <b>Grasping Undergrowth</b>: You can fill any number of 5-foot squares on the ground that aren''t filled with fog  with grasping weeds and vines, as if they were affected by an <i>entangle</i> spell. To a creature immune to this effect, the weeds and vines feel soft and reshape themselves to serve as temporary seats or beds.<br> <b>Grove Guardians</b>: You can animate up to four trees in the area, causing them to uproot themselves from the ground. These trees have the same statistics as an awakened tree, which appears in the <i>Monster Manual</i>, except they can''t speak, and their bark is covered with druidic symbols. If any creature not immune to this effect enters the warded area, the grove guardians fight until they have driven offor slain the intruders. The grove guardians also obey your spoken commands (no action required by you) that you issue while in the area. If you don''t give them commands and no intruders are present, the grove guardians do nothing. The grove guardians can''t leave the warded area. When the spell ends, the magic animating them disappears, and the trees take root again if possible.<br> <b>Additional Spell Effect</b>: You can place your choice of one of the following magical effects within the warded area:<br>  A constant <i>gust of wind</i> in two locations of your choice<br>  <i>Spike growth</i> in one location of your choice<br>  <i>Wind wall</i> in two locations of your choice.<br> To a creature immune to this effect, the winds are a fragrant, gentle breeze, and the area of <i>spike growth</i> is harmless.', 'mistletoe, which the spell consumes, that was harvested with a golden sickle under the light of a full moon', 'Touch', 'VSM', NULL, '10 minutes', false, 'Until dispelled', false, NULL, '9229f2b9-e42b-42e6-9b38-55aab1696d93', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('42c6e1f6-866f-4e3f-b4ff-21b504d0a384', 'Investiture of Flame', 'Flames race across your body, shedding bright light in a 30-foot radius and dim light for an additional 30 feet for the spell''s duration. The flames don''t harm you. Until the spell ends, you gain the following benefits:<br>  You are immune to fire damage and have resistance to cold damage.<br>  Any creature that moves within 5 feet of you for the first time on a turn or ends its turn there takes 1d10 fire damage.<br>  You can use your action to create a line of fire 15 feet long and 5 feet wide extending from you in a direction you choose. Each creature in the line must make a Dexterity saving throw. A creature takes 4d8 fire damage on a failed save, or half as much damage on a successful one.', '', 'Self', 'VS', NULL, '1 action', false, 'Instantaneous', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f364364a-a6de-4fb4-bf75-3fdf7027452a', 'Investiture of Ice', 'Until the spell ends, ice rimes your body, and you gain the following benefits:<br>  You are immune to cold damage and have resistance to fire damage.<br>  You can move across difficult terrain created by ice or snow without spending extra movement.<br>  The ground in a 10-foot radius around you is icy and is difficult terra in for creatures other than you. The radius moves with you.<br>  You can use your action to create a 15-foot cone of freezing wind extending from your outstretched hand in a direction you choose. Each creature in the cone must make a Constitution saving throw. A creature takes 4d6 cold damage on a failed save, or half as much da mage on a successful one. A creature that fails its save against this effect has its speed halved until the start of your next turn.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3a730099-76f9-45dd-aebd-0b321790a7cf', 'Investiture of Stone', 'Until the spell ends, bits of rock spread across your body, and you gain the following benefits:<br>  You have resistance to bludgeoning, piercing, and slashing damage from nonmagical attacks.<br>  You can use your action to create a small earthquake on the ground in a 15-foot radius centered on you. Other creatures on that ground must succeed on a Dexterity saving throw or be knocked prone.<br>  You can move across difficult terrain made of earth or stone without spending extra movement. You can move through solid earth or stone as if it was air and without destabilizing it, but you can''t end your movement there. If you do so, you are ejected to the nearest unoccupied space, this spell ends, and you are stunned until the end of your next turn.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('defb9cf9-5b5f-489c-a6e2-79e0a8f51c8a', 'Investiture of Wind', 'Until the spell ends, wind whirls around you, and you gain the following benefits:<br>   Ranged weapon attacks made against you have disadvantage on the attack roll.<br>  You gain a flying speed of 60 feet. If you are still flying when the spell ends, you fall, unless you can somehow prevent it.<br>  You can use your action to create a 15-foot cube of swirling wind centered on a point you can see within 60 feet ofyou. Each creature in that area must make a Constitution saving throw. A creature takes 2d10 bludgeoning damage on a failed save, or half as much damage on a successful one. If a Large or smaller creature fails the save, that creature is also pushed up to 10 feet away from the center of the cube.', '', 'Self', 'VS', NULL, '1 action', false, 'up to 10 minutes', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('a7a41cd3-a551-479d-bd25-8ec981f9384f', 'Time Stop', 'You briefly stop the flow of time for everyone but yourself. No time passes for other creatures, while you take 1d4+1 turns in a row, during which you can use actions and move as normal.<br> This spell ends if one of the actions you use during this period, or any effects that you create during this period, affects a creature other than you or an object being worn or carried by someone other than you. In addition, the spell ends if you move to a place more than 1,000 feet from the location where you cast it.', '', 'Self', 'V', NULL, '1 action', false, 'Instantaneous', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('883932a1-586a-4a7f-8546-9afa57e16ec0', 'Soul Cage', 'This spell snatches the soul of a humanoid as it dies and traps it inside the tiny cage you use for the material component. A stolen soul remains inside the cage until the spell ends or until you destroy the cage, which ends the spell. While you have a soul inside the cage, you can exploit it in any of the ways described below. You can use a trapped soul up to six times. Once you exploit a soul for the sixth time, it is released, and the spell ends. While a soul is trapped, the dead humanoid it came from can''t be revived.<br> <b>Steal Life</b>: You can use a bonus action to drain vigor from the soul and regain 2d8 hit points.<br> <b> Query Soul</b>: You ask the soul a question (no action required) and receive a brief telepathic answer, which you can understand regardless of the language used. The soul knows only what it knew in life, but it must answer you truthfully and to the best of its ability. The answer is no more than a sentence or two and might be cryptic.<br> <b> Borrow Experience</b>: You can use a bonus action to bolster yourself with the soul''s life experience, making your next attack roll, ability check, or saving throw with advantage. If you don''t use this benefit before the start of your next turn, it is lost.<br> <b> Eyes of the Dead</b>: You can use an action to name a place the humanoid saw in life, which creates an invisible sensor somewhere in that place if it is on the plane of existence you''re currently on. The sensor remains for as long as you concentrate, up to 10 minutes (as if you were concentrating on a spell). You receive visual and auditory information from the sensor as if you were in its space using your senses.<br> A creature that can see the sensor (such as one using <i>see invisibility</i> or truesight) sees a translucent image of the tormented humanoid whose soul you caged.', 'a tiny silver cage worth 100 gp', '60 feet', 'VSMgp', NULL, '1 reaction', false, '8 hours', false, NULL, 'f6804f17-5187-4a57-97a3-c97234c8a9b7', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('9413941a-79c1-4851-ab85-94e91b28ef96', 'Summon Fiend', 'You call forth a fiendish spirit. It manifests in an unoccupied space that you can see within range. This corporeal form uses the Fiendish Spirit stat block. When you cast the spell, choose Demon, Devil, or Yugoloth. The creature resembles a fiend of the chosen type, which determines certain traits in its stat block. The creature disappears when it drops to 0 hit points or when the spell ends.<br>The creature is an ally to you and your companions. In combat, the creature shares your initiative count, but it takes its turn immediately after yours. It obeys your verbal commands (no action required by you). If you don''t issue any, it takes the Dodge action and uses its move to avoid danger.<br><b>At Higher Levels</b>: When you cast this spell using a spell slot of 7th level or higher, use the higher level wherever the spell''s level appears in the stat block.', 'humanoid blood inside a ruby vial worth at least 600 gp', '90 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('f8180652-e8ab-4d57-b328-64858ef87841', 'True Seeing', 'This spell gives the willing creature you touch the ability to see things as they actually are. For the duration, the creature has truesight, notices secret doors hidden by magic, and can see into the Ethereal Plane, all out to a range of 120 feet.', 'an ointment for the eyes that costs 25 gp, is made from mushroom powder, saffron, and fat, and is consumed by the spell', 'Touch', 'VSMgp', NULL, '1 action', false, '1 hour', false, NULL, 'cae6ff69-54dc-4c7f-9c7e-34e958d5c3df', 6);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('c6398c9e-651b-4db4-a416-0a5a521f7eb8', 'Crown of Stars', 'Seven star-like motes of light appear and orbit your head until the spell ends. You can use a bonus action to send one of the motes streaking toward one creature or object within 120 feet of you. When you do so, make a ranged spell attack. On a hit, the target takes 4d12 radiant damage. Whether you hit or miss, the mote is expended. The spell ends early if you expend the last mote.<br> If you have four or more motes remaining, they shed bright light in a 30-foot radius and dim light for an additional 30 feet. If you have one to three motes remaining, they shed dim light in a 30-foot radius.<br> <b>At Higher Levels</b>: When you cast this spell using a spell slot of 8th level or higher, the number of motes created increases by two for each slot level above 7th.', '', 'Self', 'VS', NULL, '1 action', false, '1 hour', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('7b971e6c-60ba-4948-9019-5dd8d149052b', 'Dream of the Blue Veil', 'You and up to eight willing creatures within range fall unconscious for the spells'' duration and experience visions of another world on the Material Plane, such as Oerth, Toril, Krynn, or Eberron. If the spell reaches its full duration, the visions conclude with each of you encountering and pulling back a mysterious blue curtain. The spell then ends with you mentally and physically transported to the world that was in the visions.<br>To cast this spell, you must have a magic item that originated on the world you wish to reach, and you must be aware of the world''s existence, even if you don''t know the world''s name. Your destination in the other world is a safe location within 1 mile of where the magic item was created. Alternatively, you can cast the spell if one of the affected creatures was born on the other world, which causes your destination to be a safe location within 1 mile of where that creature was born.<br>The spell ends early on a creature if that creature takes any damage, and the creature isn''t transported. If you take any damage, the spell ends for you and all other creatures, with none of you being transported.', 'a magic item or a willing creature from the destination world', '20 feet', 'VSM', NULL, '10 minutes', false, '6 hours', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('e9393c56-43b9-4b18-a5ac-383cf172e59c', 'Regenerate', 'You touch a creature and stimulate its natural healing ability. The target regains 4d8 + 15 hit points. For the duration of the spell, the target regains 1 hit point at the start of each of its turns (10 hit points each minute).<br> The target''s severed body members (fingers, legs, tails, and so on), if any, are restored after 2 minutes. If you have the severed part and hold it to the stump, the spell instantaneously causes the limb to knit to the stump.', 'A prayer wheel and holy water', 'Touch', 'VSM', NULL, '1 minute', false, '1 hour', false, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3953aa09-d510-48b7-a64a-52a9c713ee8e', 'Temple of the Gods', 'You cause a temple to shimmer into existence on ground you can see within range. The temple must fit within an unoccupied cube of space, up to 120 feet on each side. The temple remains until the spell ends. It is dedicated to whatever god, pantheon, or philosophy is represented by the holy symbol used in the casting.<br> You make all decisions about the temple''s appearance. The interior is enclosed by a floor, walls, and a roof, with one door granting access to the interior and as many windows as you wish. Only you and any creatures you designate when you cast the spell can open or close the door.<br> The temple''s interior is an open space with an idol or altar at one end. You decide whether the temple is illuminated and whether that illumination is bright light or dim light. The smell of burning incense fills the air within, and the temperature is mild.<br> The temple opposes types of creatures you choose when you cast this spell. Choose one or more of the following: celestials, elementals, fey, fiends, or undead. If a creature of the chosen type attempts to enter the temple, that creature must make a Charisma saving throw. On a failed save, it can''t enter the temple for 24 hours. Even if the creature can enter the temple, the magic there hinders it. Whenever it makes an attack roll, an ability check, or a saving throw inside the temple, it must roll a d4 and subtract the number rolled from the d20 roll.<br> In addition, the sensors created by divination spells can''t appear inside the temple, and creatures within can''t be targeted by divination spells.<br> Finally, whenever any creature in the temple regains hit points from a spell of 1st level or higher, the creature regains additional hit points equal to your Wisdom modifier (minimum 1 hit point).<br> The temple is made from opaque magical force that extends into the Ethereal Plane, thus blocking ethereal travel into the temple''s interior. Nothing can physically pass through the temple''s exterior. It can''t be dispelled by <i>dispel magic</i>, and <i>antimagic field</i> has no effect on it. A <i>disintegrate</i> spell destroys the temple instantly.<br> Casting this spell on the same spot every day for a year makes this effect permanent.', '', '120 feet', 'VSMgp', NULL, '1 hour', false, '24 hours', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 7);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b3446e96-cd26-4935-b56f-f6e72805ab59', 'Illusory Dragon', 'By gathering threads of shadow material from the Shadowfell, you create a Huge shadowy dragon in an unoccupied space that you can see within range. The illusion lasts for the spell''s duration and occupies its space, as if it were a creature.<br> When the illusion appears, any of your enemies that can see it must succeed on a Wisdom saving throw or become frightened of it for 1 minute. If a frightened creature ends its turn in a location where it doesn''t have line of sight to the illusion, it can repeat the saving throw, ending the effect on itself on a success.<br> As a bonus action on your turn, you can move the illusion up to 60 feet. At any point during its movement, you can cause it to exhale a blast of energy in a 60-foot cone originating from its space. When you create the dragon, choose a damage type: acid, cold, fire, lightning, necrotic, or poison. Each creature in the cone must make an Intelligence saving throw, taking 7d6 damage of the chosen damage type on a failed save, or half as much damage on a successful one.<br> The illusion is tangible because of the shadow stuff used to create it, but attacks miss it automatically, it succeeds on all saving throws, and it is immune to all damage and conditions. A creature that uses an action to examine the dragon can determine that it is an illusion by succeeding on an Intelligence (Investigation) check against your spell save DC. If a creature discerns the illusion for what it is, the creature can see through it and has advantage on saving throws against its breath.', '', '120 feet', 'S', NULL, '1 action', false, 'up to 1 minute', true, NULL, 'df89f39d-d882-4125-9b30-a2132847f4ea', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('b23a5598-7980-40e1-a610-209671976b79', 'Mighty Fortress', 'A fortress of stone erupts from a square area of ground of your choice that you can see within range. The area is 120 feet on each side, and it must not have any buildings or other structures on it. Any creatures in the area are harmlessly lifted up as the fortress rises.<br> The fortress has four turrets with square bases, each one 20 feet on a side and 30 feet tall, with one turret on each corner. The turrets are connected to each other by stone walls that are each 80 feet long, creating an enclosed area. Each wall is 1 foot thick and is composed of panels that are 10 feet wide and 20 feet tall. Each panel is contiguous with two other panels or one other panel and a turret. You can place up to four stone doors in the fortress''s outer wall.<br> A small keep stands inside the enclosed area. The keep has a square base that is 50 feet on each side, and it has three floors with 10-foot-high ceilings. Each of the floors can be divided into as many rooms as you like, provided each room is at least 5 feet on each side. The floors of the keep are connected by stone staircases, its walls are 6 inches thick, and interior rooms can have stone doors or open archways as you choose. The keep is furnished and decorated however you like, and it contains sufficient food to serve a nine-course banquet for up to 100 people each day. Furnishings, food, and other objects created by this spell crumble to dust if removed from the fortress.<br> A staff of one hundred invisible servants obeys any command given to them by creatures you designate when you cast the spell. Each servant functions as if created by the <i>unseen servant</i> spell.<br> The walls, turrets, and keep are all made of stone that can be damaged. Each 10-foot-by-10-foot section of stone has AC 15 and 30 hit points per inch of thickness. It is immune to poison and psychic damage. Reducing a section of stone to 0 hit points destroys it and might cause connected sections to buckle and collapse at the DM''s discretion.<br> After 7 days or when you cast this spell somewhere else, the fortress harmlessly crumbles and sinks back into the ground, leaving any creatures that were inside it safely on the ground.<br> Casting this spell on the same spot once every 7 days for a year makes the fortress permanent.', 'a diamond worth at least 500 gp, which the spell consumes', '1 mile', 'VSMgp', NULL, '1 minute', false, 'Instantaneous', false, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('5cc8f0be-eda4-44e9-8d76-7863fd4f27ce', 'Telepathy', 'You create a telepathic link between yourself and a willing creature with which you are familiar. The creature can be anywhere on the same plane of existence as you. The spell ends if you or the target are no longer on the same plane.<br> Until the spell ends, you and the target can instantaneously share words, images, sounds, and other sensory messages with one another through the link, and the target recognizes you as the creature it is communicating with. The spell enables a creature with an Intelligence score of at least 1 to understand the meaning of your words and take in the scope of any sensory messages you send to it.', 'a pair of linked silver rings', 'Unlimited', 'VSM', NULL, '1 action', false, '24 hours', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('3dbfd35e-3edc-4598-8d5a-c561a4c5b5bd', 'Tsunami', 'A wall of water springs into existence at a point you choose within range. You can make the wall up to 300 feet long, 300 feet high, and 50 feet thick. The wall lasts for the duration.<br> When the wall appears, each creature within its area must make a Strength saving throw. On a failed save, a creature takes 6d10 bludgeoning damage, or half as much damage on a successful save.<br> At the start of each of your turns after the wall appears, the wall, along with any creatures in it, moves 50 feet away from you. Any Huge or smaller creature inside the wall or whose space the wall enters when it moves must succeed on a Strength saving throw or take 5d10 bludgeoning damage. A creature can take this damage only once per round. At the end of the turn, the wall''s height is reduced by 50 feet, and the damage creatures take from the spell on subsequent rounds is reduced by 1d10. When the wall reaches 0 feet in height, the spell ends.<br> A creature caught in the wall can move by swimming. Because of the force of the wave, though, the creature must make a successful Strength (Athletics) check against your spell save DC in order to move at all. If it fails the check, it can''t move. A creature that moves out of the area falls to the ground.', '', 'Sight', 'VS', NULL, '1 minute', false, 'up to 6 rounds', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 8);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('1dc2679c-3b51-478f-a5ec-d3e6d563d3b8', 'Meteor Swarm', 'Blazing orbs of fire plummet to the ground at four different points you can see within range. Each creature in a 40-foot-radius sphere centered on each point you choose must make a Dexterity saving throw. The sphere spreads around corners. A creature takes 20d6 fire damage and 20d6 bludgeoning damage on a failed save, or half as much damage on a successful one. A creature in the area of more than one fiery burst is affected only once.<br> The spell damages objects in the area and ignites flammable objects that aren''t being worn or carried.', '', '1 mile', 'VS', NULL, '1 action', false, 'Instantaneous', false, NULL, '1218dfbf-8a82-4d62-99b9-b830491c0e9e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('af18e37d-20c9-4b7c-a2ba-ef0474f4630f', 'Blade of Disaster', 'You create a blade-shaped planar rift about 3 feet long in an unoccupied space you can see within range. The blade lasts for the duration. When you cast this spell, you can make up to two melee spell attacks with the blade, each one against a creature, loose object, or structure within 5 feet of the blade. On a hit, the target takes 4d12 force damage. This attack scores a critical hit if the number on the d20 is 18 or higher. On a critical hit, the blade deals an extra 8d12 force damage (for a total of 12d12 force damage).<br>As a bonus action on your turn, you can move the blade up to 30 feet to an unoccupied space you can see and then make up to two melee spell attacks with it again.<br>The blade can harmlessly pass through any barrier, including a Wall of Force.', '', '60 feet', 'VS', NULL, '1 bonus action', false, 'up to 1 minute', true, NULL, '3dcebbe9-0f6f-417d-b917-d4b6614f0c3e', 9);
INSERT INTO public.spells (id, name, description, ingredients, range, components, material, action, ritual, duration, concentration, "castingTime", "schoolId", level) VALUES ('87312695-838c-430c-89f8-87146f045c86', 'Mass Polymorph', 'You transform up to ten creatures of your choice that you can see within range. An unwilling target must succeed on a Wisdom saving throw to resist the transformation. An unwilling shapechanger automatically succeeds on the save.<br> Each target assumes a beast form of your choice, and you can choose the same form or different ones for each target. The new form can be any beast you have seen whose challenge rating is equal to or less than the target''s (or half the target''s level, if the target doesn''t have a challenge rating). The target''s game statistics, including mental ability scores, are replaced by the statistics of the chosen beast, but the target retains its hit points, alignment, and personality.<br> Each target gains a number of temporary hit points equal to the hit points of its new form. These temporary hit points can''t be replaced by temporary hit points from another source. A target reverts to its normal form when it bas no more temporary hit points or it dies. If the spell ends before then, the creature loses all its temporary hit points and reverts to its normal form.<br> The creature is limited in the actions it can perform by the nature of its new form. It can''t speak, cast spells, or do anything else that requires hands or speech.<br> The target''s gear melds into the new form. The target can''t activate, use, wield, or otherwise benefit from any of its equipment.', 'a caterpillar cocoon', '120 feet', 'VSM', NULL, '1 action', false, 'up to 1 hour', true, NULL, '91b0663b-e85a-4085-a4fb-a0e62939d2f6', 9);
INSERT INTO public.users (id, username, password, "firstName", "lastName", email, "updatedAt", "createdAt", "refreshToken", role) VALUES ('23b02578-eb21-4753-aaff-fe1694aaaa8a', 'root', '$argon2id$v=19$m=65536,t=3,p=4$6tVlv0DnOIgkpRc+UQwbxA$vyIkw+SRHgFcatRj0FDQoFhJHYj+iCzwx3pMOGTSJOA', 'root', 'root', 'root@root.com', '2025-12-16', '2025-12-16', '$argon2id$v=19$m=65536,t=3,p=4$p/8Cjp63AAesnKRnDxBnbg$qHR5ttqyIzV6UBG6cgsGMnGxF9HkAlQiWDOmCGVAN2c', NULL);
